use `yummy`;

DROP TABLE IF EXISTS `reports`;
DROP TABLE IF EXISTS `comments`;
DROP TABLE IF EXISTS `reactions`;
DROP TABLE IF EXISTS `posts`;
DROP TABLE IF EXISTS `tipNutri`;
DROP TABLE IF EXISTS `users`;

CREATE TABLE `users` (
  `id` int PRIMARY KEY AUTO_INCREMENT,
  `fullname` varchar(255) NOT NULL,
  `email` varchar(255),
  `password` varchar(255) NOT NULL,
  `phone` varchar(255) UNIQUE NOT NULL,
  `gender` int DEFAULT 1,
  `role` int DEFAULT 1,
  `birthday` varchar(255),
  `country` varchar(255),
  `bio` text,
  `image` varchar(255),
  `block_date` varchar(255),
  `status` int DEFAULT 1,
  `confirm_code` varchar(100),
  `created_at` timestamp 
) ENGINE=InnoDB;


CREATE TABLE `posts` (
  `id` int PRIMARY KEY AUTO_INCREMENT,
  `user_id` int NOT NULL,
  `video_url` varchar(255) NOT NULL,
  `title` varchar(255) NOT NULL,
  `overview` text NOT NULL,
  `making` text NOT NULL,
  `material_detail` text NOT NULL,
  `time` int NOT NULL,
  `ration` int NOT NULL,
  `kind` varchar(255) NOT NULL,
  `main_material` varchar(255) NOT NULL,
  `price` int NOT NULL,
  `nation` varchar(255) NOT NULL,
  `holiday` varchar(255) NOT NULL,
  `category` varchar(255) NOT NULL,
  `suitable` varchar(255) NOT NULL,
  `view_conter` bigint DEFAULT 0,
  `created_at` timestamp ,
  `deleted_at` varchar(255),
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB;


CREATE TABLE `comments` (
  `id` int PRIMARY KEY AUTO_INCREMENT,
  `post_id` int NOT NULL,
  `user_id` int NOT NULL,
  `content` varchar(255) NOT NULL,
  `status` int DEFAULT 0,
  `image_url` varchar(255),
  `created_at` varchar(255),
  FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`) ON DELETE CASCADE,
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)  ON DELETE CASCADE
) ENGINE=InnoDB;


CREATE TABLE `reactions` (
  `id` int PRIMARY KEY AUTO_INCREMENT,
  `post_id` int NOT NULL,
  `user_id` int NOT NULL,
  `created_at` timestamp ,
  FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`) ON DELETE CASCADE,
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB;


CREATE TABLE `reports` (
  `id` int PRIMARY KEY AUTO_INCREMENT,
  `cmt_id` int,
  `user_id` int NOT NULL,
  `type` int,
  `report_author` int,
  `created_at` varchar(255),
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB;

CREATE TABLE `tipNutri` (
  `id` int PRIMARY KEY AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `content` text NOT NULL,
  `type` varchar(50) NOT NULL,
  `author` varchar(255) NOT NULL,
  `kind` varchar(255) NOT NULL,
  `image` varchar(255) NOT NULL,
  `user_id` int NOT NULL,
  `created_at` varchar(255),
  FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE

) ENGINE=InnoDB;


INSERT INTO `users` VALUES (1,'Admin','ledieuly0504@gmail.com','0e7517141fb53f21ee439b355b5a1d0a0e7517141fb53f21ee439b355b5a1d0a0e7517141fb53f21ee439b355b5a1d0a','0345355888',1,10,'','Đại học FPT','Quản lí trang web Yummy','null_img.png',NULL,1,NULL,'2020-08-14 05:34:38'),(3,'Đỗ Anh Hoàng','','7acd48792024103b14ce5989283e3e327acd48792024103b14ce5989283e3e327acd48792024103b14ce5989283e3e32','0976283733',1,1,'','','','3.jpg','null',1,NULL,'2020-08-14 06:31:20'),(4,'Hung Vu','hungvu@123','73230b5b21b01ca7c8bf35b328e2b89c73230b5b21b01ca7c8bf35b328e2b89c73230b5b21b01ca7c8bf35b328e2b89c','0327272745',1,1,'','','','7.jpg',NULL,1,NULL,'2020-08-14 06:31:20'),(5,'Lê Diệu Ly','','0daba3692203c92976790e5847d62a600daba3692203c92976790e5847d62a600daba3692203c92976790e5847d62a60','0384838666',0,1,'','','','18.jpg','null',1,NULL,'2020-08-14 14:47:14'),(6,'phuong thao',NULL,'b0d488b2940946a68404d73d69e3de6db0d488b2940946a68404d73d69e3de6db0d488b2940946a68404d73d69e3de6d','0961393111',1,1,NULL,NULL,NULL,'null_img.png',NULL,1,NULL,'2020-08-14 14:47:14'),(7,'Nguyen Hien','','5e2fb65985b6266c80cb30860d30f7e05e2fb65985b6266c80cb30860d30f7e05e2fb65985b6266c80cb30860d30f7e0','0976341569',0,1,'','','','14.jpg',NULL,1,NULL,'2020-08-14 14:47:14'),(8,'Dai Vuong',NULL,'8e6cb8d90aa189fb0bcef09f24e573348e6cb8d90aa189fb0bcef09f24e573348e6cb8d90aa189fb0bcef09f24e57334','0986897897',1,1,NULL,NULL,NULL,'null_img.png','null',1,NULL,'2020-08-14 14:47:14');

INSERT INTO `posts` VALUES (1002,1,'https://www.youtube.com/v/YEL1XU9uxig','Cá lóc kho tộ đưa cơm','\"Cá lóc kho tộ (cá quả kho tộ) là một trong những món cá kho ngon cho bữa cơm gia đình. Cách làm cá lóc kho tộ ngon nên cắt đều các khứa cá, tẩm ướp đầy đủ gia vị, kho cá với nồi đất thay cho các nồi inox thông thường. Bí quyết kho cá ngon từ Cooky là đem kho cá lửa nhỏ liu riu cho ngấm vị, nhiều tiêu ớt, hành lá để giúp cá kho không bị tanh nhé.\r\n\r\n\"','\"Chuẩn bị:\r\nB1: Chuẩn bị làm cá lóc kho: Làm sạch cá lóc, cắt khúc nhỏ dày khoảng 1.5-2 cm. Để tiết kiệm thời gian kho cá và đối với những bạn không biết làm cá lóc, bạn nên nhờ người bán làm sạch sẽ cá lóc luôn, khi mua về chỉ việc rửa lại.\r\nB2: Pha loãng nước màu kho cá với 1.5 muỗng canh nước ấm. Xếp cá vào nồi đất (Cách làm cá lóc kho tiêu ngon là dùng nồi đất thay vì nồi kim loại). Thêm vào 2 muỗng cà phê hạt nêm, 1/2 muỗng cà phê muối, 1/2 muỗng cà phê bột ngọt, 2 muỗng cà phê đường, 1/2 muỗng cà phê tiêu, 3 muỗng canh nước mắm, nước màu đã pha và 2 trái ớt cắt nhỏ ướp cá trong nồi đất khoảng 10 phút.\r\nThực hiện:\r\nB1: Đặt nồi cá kho tộ lên bếp, đậy nắp đun sôi rồi hạ nhỏ lửa. Cắt lát mỏng hành tím, phi sơ với 1 muỗng canh dầu ăn. Khi nồi cá kho tộ sôi thì cho hành tím cùng dầu phi vào nồi cá lóc kho tộ, trộn đều.\r\nB2: Thêm 200ml nước lọc vào rồi đậy nắp đun trong 30 phút với lửa vừa. Khi nồi cá kho tộ sệt lại thì thêm hành lá, ớt cắt lát, đun tiếp ít phút rồi tắt bếp. Vậy là hoàn thành món cá lóc kho tộ ngon lành cành đào rồi nhé.\r\nB3: Dọn cả nồi cá lóc kho tộ ra mâm cơm. Vì nồi cá kho tộ rất nóng nên đặt lên một chiếc đĩa để dễ di chuyển nhé. Bày trí đẹp mắt và thưởng thức cá lóc kho tộ với cơm thôi nào. Những ngày mưa rả rít, cả nhà quây quần bên mâm cơm gia đình với món cá lóc kho tộ bùi béo, mặn ngọt, cay thơm đậm đà thì ấm lòng hết biết nhé.\"','\"Cá lóc 1 con\r\n(Khoảng 600 gram)\r\n Hạt nêm 2 muỗng cà phê\r\n Bột ngọt 1/2 muỗng cà phê\r\n Đường trắng 2 muỗng cà phê\r\n Tiêu 1/2 muỗng cà phê\r\n Nước mắm 3 muỗng canh\r\n Nước màu 1 muỗng canh\r\n Ớt 2 trái(Ớt tươi)\r\n Hành tím 4 củ\r\n Hành lá 2 nhánh\r\n Dầu ăn 1 muỗng canh\"',60,1,'Kho-Om','Hải sản',80,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',2,'2020-07-07 00:18:05','NULL'),(1005,1,'https://www.youtube.com/v/4CGA8RWQmZ0','Cá chép om  dưa chua','\"Cá Chép Om Dưa Chua - món ăn ngon chuẩn vị miền Bắc, thích hợp cho các bữa cơm gia đình ngày Tết. Cách nấu canh cá chép om dưa chua đậm đà, có thịt cá chép mềm, quyện đều gia vị và phụ liệu thơm ngon, không bị tanh, rất ngon miệng. Học ngay cách làm cá chép om dưa với thịt cá thơm ngon, bổ dưỡng, đơn giản ngay sau đây nhé.\r\n','\"B1: Chuẩn bị nấu cá chép om dưa chua: Làm sạch vẩy cá chép, cắt vây, bỏ ruột và rửa sạch nhớt. \r\nƯớp cá chép với 1 muỗng canh hạt nêm, 1 muỗng canh tiêu xay và 1 muỗng canh nước mắm trong \r\n10 phút để hương vị cá được đậm đà.\r\nB2: Làm nóng 2 muỗng canh dầu ăn, cho 15gr tỏi băm vào phi vàng. Sau đó thêm 100gr thịt ba \r\nchỉ đã được cắt miếng nhỏ vào xào đều tay đến khi phần mỡ săn lại.\r\nB3: Tiếp theo cho 2 quả cà chua cắt múi vào, xào đến khi cà chua chín và tiết ra màu đỏ đẹp. Thêm 150gr \r\ndưa cải chua, xào sơ trên chảo rồi nêm nếm với 20gr đường trắng, 20ml nước mắm, 15gr hạt nêm, 5gr muối,\r\nxào tiếp 5 phút cho cải chua và thịt ba chỉ thấm gia vị.\r\nB4: Cách nấu cá chép om dưa chua: Lúc này thêm 500ml nước dùng gà, đun đến khi nước dùng sôi\r\nthì nêm nếm gia vị lại cho vừa ăn rồi cho cá chép vào om. Nấu chín một mặt rồi trở mặt tiếp theo để \r\ncá chép chín đều. Cuối cùng là 2 thành phần tạo nên đặc trưng cho món ăn cá chép om dưa chua đó\r\nlà thì là và hành lá, cho vào rồi nhắc xuống dùng ngay khi cá còn nóng để hương vị món ăn được thơm \r\nngon nhất nhé! Khi ăn đến đâu thì cho thì là và hành lá đến đó để vừa tái là ăn được.\r\nB5: Cá chép om dưa chua có vị chua ngọt và hương thơm quyến rũ của thì là, gia vị đặc trưng của món \r\ncanh miền Bắc. Cá om dưa mềm thịt nhưng không tanh, có thể ăn với cơm hoặc bún tươi đều được. \r\nMón cá chép om dưa này nên ăn khi còn nóng sẽ ngon hơn rất nhiều. Cuối tuần bạn hãy thử cách nấu \r\ncá chép om dưa ngon để cùng gia đình và bạn bè thưởng thức nhé.\"','\"Cá chép 1 con\r\n(1kg)\r\n Dưa cải chua 150 gr\r\n Cà chua 2 trái\r\n Thịt ba chỉ 100 gr\r\n Hành lá 50 gr\r\n Thì là 50 gr\r\n Hạt nêm 25 gr\r\n Tiêu 1 muỗng canh\r\n Nước mắm 25 ml\r\n Đường trắng 20 gr\r\n Muối 5 gr\r\n Dầu ăn 2 muỗng canh\r\n Tỏi băm 15 gr\r\n Nước dùng gà 500 ml\"',45,2,'Kho-Om','Hải sản',100,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1006,1,'https://www.youtube.com/v/NHUm9ThPAMg','Cá hồi chiên xù sốt thì là','Cá hồi là một loại thực phẩm bổ dưỡng và ngon miệng, rất tốt cho sức khỏe đặc biệt là các bạn nhỏ. Khi được chiên xù lên, cá hồi trở thành một món ăn thơm ngon, giòn vỏ ngoài và mềm bên trong, chấm vào nước sốt béo ngậy cực kì hấp dẫn.','B1: Cá hồi rửa sạch, rút hết xương, lóc bỏ phần da rồi cắt thành những khối vuông nhỏ bằng viên xúc \r\nxắc.\r\nB2: Ướp cá hồi với 1/2 muỗng cà phê muối và 1/2 muỗng cà phê tiêu xay 10 phút cho thấm. Chuẩn bị \r\n3 chén đựng trứng gà đánh tan, bột mì và bột chiên xù. Lần lượt nhúng từng viên cá hồi vào bột mì, \r\ntrứng gà và bột chiên xù để từng viên cá áo đều một lớp bột xù giòn tan.\r\nB3: Đem cá đi chiên giòn ngập dầu rồi vớt ra dĩa có lót giấy thấm dầu.\r\nB4: Làm sốt chấm cá: cho vào cối xay 20gr thì là, 10gr ngò rí, 10gr cần tàu, 10gr tỏi tép cùng với 2 \r\nmuỗng canh nước mắm Maggi, 2 muỗng canh đường trắng và 1 muỗng canh nước lọc. Xay nhuyễn \r\ncác nguyên liệu rồi lấy hỗn hợp trộn với 100gr sốt mayonnaise, khuấy đều ta có nước sốt chấm béo \r\nngậy cho món cá.\r\nB5: Cá hồi chiên xù với lớp vỏ giòn tan chấm với nước sốt béo ngậy cực kì ngon. Các bé nhà mình \r\nđảm bảo sẽ rất ghiền món này cho mà xem.','Cá hồi 300 gr\r\n Trứng gà 1 quả\r\n Bột mì 30 gr\r\n Bột chiên xù 50 gr\r\n Muối 1/2 muỗng cà phê\r\n Tiêu 1/2 muỗng cà phê\r\n Dầu ăn 100 ml\r\nNước sốt ngò thì là\r\n Sốt Mayonnaise 100 gr\r\n Thì là 20 gr\r\n Ngò rí 10 gr\r\n Cần tàu 10 gr\r\n Tỏi 10 gr\r\n Nước mắm Maggi 2 muỗng \r\n canh\r\n Đường trắng 2 muỗng canh',45,3,'Kho-Om','Hải sản',250,'Nhật Bản','Ngày lễ khác','Khai vị','Người gầy',2,'2020-07-07 00:18:05','NULL'),(1009,1,'https://www.youtube.com/v/xtIkeG64Fpo','Cá ba sa áp  chảo sốt chanh dây','Cá basa áp chảo sốt chanh dây là món ngon bổ dưỡng vì con cá basa có giá trị dinh dưỡng cao không kém các loại cá nước biển sâu, trong mỡ cá chứa nhiều axit béo không no, không có cholesterol nên rất tốt cho sức khỏe. Hôm nay Cooky giới thiệu đến các bạn món Cá sốt chanh dây chua ngọt cực kì dễ làm và nguyên liệu lại rất đơn giản. Đặc biệt là sốt chanh dây đảm bảo sẽ rất hút cơm, giúp các mẹ đổi món cho bữa cơm gia đình mình thêm phong phú.\r\n','B1: Cắt đôi chanh dây, nạo phần ruột rồi cho vào rây lọc lấy nước cốt. Đổ thêm 80-100ml nước ấm vào \r\nphần xác chanh dây rồi tiếp tục rây. Lọc lấy 150ml nước cốt, cho thêm vào 1 muỗng cà phê hạt để giúp \r\nsốt trông đẹp mắt hơn.\r\nB2: Cách làm sốt chanh dây: Pha 1 muỗng cà phê bột bắp với 20ml nước. Đổ 150ml nước cốt chanh \r\ndây, 70gr đường trắng, 1/4 muỗng cà phê muối vào chảo, khuấy đều cho đường tan. Đun lửa vừa đến \r\nkhi nước sốt sôi lên thì bạn cho bột bắp loãng vào, vừa đổ vừa vừa khuấy đều, thêm bơ lạt vào để \r\nkhoảng 30 giây rồi nhắc xuống. Bột bắp sẽ tạo độ sánh cho nước sốt.\r\nB3: Cá basa mua dạng phi lê sẵn, rửa sạch rồi thấm khô. Cắt cá ra thành những miếng vừa ăn rồi ướp \r\nvới 1/2 muỗng cà phê muối, 1/2 muỗng cà phê tiêu cho đều 2 mặt.\r\nB4: Lăn cá qua bột mì cho cá phủ một lớp bột mỏng. Cho dầu ăn vào chảo nóng, tráng đều dầu quanh \r\nmặt chảo, để lửa vừa rồi cho cá vào áp chảo. Nhớ để mặt có da xuống trước vì phần da cá basa hơi \r\ndày sẽ lâu chín hơn. Dùng sạn đè lên miếng cá để cá không bị cong khi chiên. Áp chảo đến khi phần \r\nda vàng giòn thì trở mặt, áp chảo nửa còn lại cho chín.\r\nB5: Khi ăn rưới nước sốt lên cá và thưởng thức thôi. Nước sốt chanh dây chua ngọt rưới lên từng miếng \r\nphi lê cá vàng giòn đảm bảo sẽ bắt cơm lắm đấy. Cá sốt chanh dây có thể ăn kèm với cơm trắng, hoặc \r\năn với các loại rau củ xào như một kiểu ăn Lowcarb đều được.','Cá ba sa 180 gr\r\n(Phi lê)\r\n Muối 1/2 muỗng cà phê\r\n Tiêu 1/2 muỗng cà phê\r\n Bột mì 50 gr\r\n Dầu ăn 20 ml\r\nPhần sốt chanh dây\r\n Chanh dây 4 trái\r\n Đường trắng 70 gr\r\n Muối 1/4 muỗng cà phê\r\n Bơ lạt 5 gr\r\n Bột bắp 1 muỗng cà phê',30,1,'Kho-Om','Hải sản',30,'Việt Nam','Ngày lễ khác','Khai vị','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1015,1,'https://www.youtube.com/v/NE_3LA8GS0s','Mực nướng đá  hấp bia','Mực nướng đá hấp bia được hấp chín trên đá cuội giúp giữ được lượng nước và không làm mất đi nhiều chất dinh dưỡng trong từng miếng mực, và còn hấp thu thêm khoáng chất từ bia và đá cuội làm cho món ăn trở nên ngon, đặc sắc hơn. ','B1: Mực ống 1kg làm sạch ruột, rồi để ráo. Trong khi đó bạn chuẩn bị các gia vị ướp: 10gr tỏi băm, \r\n10gr hành tím băm, 5gr ớt hiểm, 10gr ớt sa tế. Cho mực vào tô trộn đều cùng các gia vị tỏi băm, hành \r\ntím băm, ớt hiểm, ớt sa tế và ướp 15 phút.\r\nB2: Sau khi mực thấm gia vị, gắp đá nóng gắp bỏ vào nồi. Đổ mực đã ướp vào ngay sau khi gắp đá bỏ \r\nvào nồi để giữ được nhiệt độ cao nhất. Đổ 1 lon bia vào để nước đậm vị cho mực hơn. Đậy nắp lại và \r\nđợi, sau 10 phút mở nắp rồi gắp mực nóng hổi ra dĩa.\r\nB3: Mực nướng đá hấp bia là món ăn có sức hấp dẫn, bởi mùi hương của bia hòa quyện cùng mực, \r\nkèm theo màu sắc bắt mắt. Mực được nướng trên đá sẽ giữ được nước ngọt bên trong nhưng vẫn có \r\nđồ giòn tươi. Đây sẽ là món ngon để thực hiện vào những dịp cuối tuần hoặc chiêu đãi gia đình, bạn bè. \r\nBạn nhớ lưu công thức lại để cùng Cooky thực hiện món mực nướng đá hấp bia nhé!','Mực ống 1 kg\r\n Đá 10 viên(Đá cuội)\r\n Bia 1 lon\r\n Sa tế tôm 100 gr\r\n Tỏi băm 20 gr\r\n Hành tím 20 gr\r\n Hành lá 20 gr\r\n Ớt 20 gr',30,4,'Nướng','Hải sản',300,'Việt Nam','Mâm cỗ','Khai vị','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1036,1,'https://www.youtube.com/v/j_bD2CQqI1g','Mì cay kim chi hải sản','Mì cay kim chi hải sản là món ăn yêu thích của nhiều người, có kim chi chua chua, hải sản tươi ngon và rau nấm giòn ngọt, hương vị lôi cuốn hấp dẫn đến từng sợi mì cuối cùng. Công thức thực hiện món này rất đơn giản, chỉ cần bỏ ra khoảng 20 phút là có tô mì ngon lành rồi. ','B1: Chuẩn bị 150gr kim chi Bibigo, có thể vắt bớt nước trước khi dùng để món ăn ngon hơn. 100gr \r\nMực tươi làm sạch, cắt thành những khoanh tròn.\r\nB2: Bắc nồi lên bếp, cho vào 1 muỗng canh dầu ăn rồi làm nóng, tiếp theo cho 5gr tỏi băm và 15gr \r\nhành boa rô thái lát phi lên. Khi tỏi và hành boa rô thơm thì cho kim chi vào xào lên cho dậy mùi rồi chế \r\nvào nồi 500ml nước dùng gà, đun sôi. Nêm nước lèo với 1 gói gia vị mì Hàn Quốc và 5gr ớt sừng thái \r\nlát rồi khuấy đều\r\nB3: Tiếp theo cho hải sản vào nồi nấu 3 phút cho hải sản vừa chín tới rồi thêm bông cải xanh và nấm \r\nkim châm trắng vào. Đun cho nước sôi lên thì cho gói mì Hàn Quốc vào nồi, nấu khoảng 3 phút nữa cho \r\nmì chín là được.\r\nB4: Tô mì cay hải sản chất lượng đã hoàn tất rồi, nhìn thật hấp dẫn đúng không nào? Sợi mì Hàn Quốc \r\ndai dai, chìm trong nước lèo chua cay đậm đà, kim chi chua giòn bắt vị, thêm các loại hải sản và rau củ \r\ntươi ngon nữa, tự nấu mì tại nhà quá ngon và chất lượng luôn. Nhớ thưởng thức mì ngay khi còn nóng \r\nđể cảm nhận trọn vẹn hương vị bạn nhé!','Kimchi bibigo 150 gr\r\n Mì cay Hàn Quốc 1 gói\r\n Tôm tươi 150 gr\r\n Mực 100 gr\r\n Cá viên 50 gr\r\n Nước dùng gà 500 ml\r\n Nấm kim châm 50 gr\r\n Bông cải xanh 30 gr\r\n Ớt sừng 5 gr\r\n Hành boa rô 15 gr\r\n Tỏi 5 gr\r\n Dầu ăn 1 muỗng canh',15,2,'Canh-Súp','Bún-Phở-Miến',40,'Các nước khác','Ngày lễ khác','Ăn sáng','Người gầy',2,'2020-07-07 00:18:05','NULL'),(1038,1,'https://www.youtube.com/v/SsilfvtVwtY','Bún thái Tomyum','Bún thái - món ăn ngon cho bữa sáng ngày lạnh. Món bún Thái chua cay có nguồn gốc từ xứ sở Chùa Vàng. Với cách nấu bún Thái chi tiết sau đây , từ nay bạn hoàn toàn có thể tự nấu bún Thái tôm mực ngay tại nhà mỗi khi thèm mà không cần phải ra hàng quán tốn kém nữa rồi nhé! Bắt tay vào bếp và làm bún Thái hải sản ngay hôm nay đi nào!','\"B1: Nấu nước dùng bún Thái: Tách phần đầu tôm và thịt tôm để riêng, rửa sạch. Cho 200gr đầu tôm vào nồi lớn, thêm 10gr gừng và 5gr muối. Thêm 1 lít nước vào và hầm ở lửa nhỏ khoảng 30 phút. Thường xuyên hớt bọt để nước dùng được trong.\r\nB2: Để nấu bún thái có màu đỏ đẹp mắt: Đun nóng 20ml dầu ăn, phi thơm vàng 15gr hành tím băm, 10gr riềng cắt sợi, 30gr sả băm. Sau đó cho 150gr cà chua cắt múi cau vào xào. Thêm 50gr sa tế, 800ml nước dùng bún Thái, 5 lá chanh vào và đậy nắp nấu ở lửa vừa.\r\nB3: Khi nồi nước dùng bún Thái sôi, nêm vào nồi 50ml nước cốt me, 15gr muối, 10gr hạt nêm, 40gr đường và 20ml nước mắm. Đun đến khi nước sôi thêm lần nữa thì tắt bếp.\r\nB4: Đun sôi nồi nước, cho hải sản vào dĩa gồm thịt tôm, mực, nghêu và 10gr gừng vào hấp khoảng 10 phút, đến khi hải sản vừa chín tới thì tắt bếp.\r\nB5: Khi ăn bún thái thì xếp bún vào tô, trụng chín thịt bò và nấm kim châm, thêm các loại hải sản hấp chín như nghêu, mực và tôm vào tô. Món bún Thái hải sản ăn kèm rau cải cúc, hoa chuối và chấm thêm muối ớt xanh rất ngon.\"','\"Tôm tươi 400 gr\r\n Mực 100 gr\r\n Nghêu 200 gr\r\n Thịt bò 100 gr\r\n Gừng 10 gr\r\n Muối 20 gr\r\n Nước 1 lít\r\n Dầu ăn 20 ml\r\n Hành tím băm 15 gr\r\n Sả băm 30 gr\r\n Cà chua 150 gr\r\n Sa tế 50 gr\r\n Nước cốt me 50 ml\r\n Hạt nêm 10 gr\r\n Đường trắng 40 gr\r\n Nước mắm 20 ml\r\n Lá chanh 5 lá\r\n Bún tươi 500 gr\r\n Nấm kim châm 50 gr\r\n Riềng 10 gr\"',45,2,'Canh-Súp','Bún-Phở-Miến',50,'Thái Lan','Ngày lễ khác','Ăn sáng','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1039,1,'https://www.youtube.com/v/_fEYMkY19qU&feature=youtu.be','Lẩu thái hải sản','Lẩu Thái hải sản là sự lựa chọn hoàn hảo cho những bữa tiệc sum họp gia đình, đặc biệt là những ngày thời tiết se lạnh, cả gia đình quây quần bên nồi lẩu nghi ngút khói thì quá tuyệt vời. Để có một nồi lẩu Thái chua cay đúng vị không phải ai cũng nắm được bí quyết đúng không, vậy thì theo dõi công thức dưới đây để cùng xem bí quyết đó là gì nhé!\r\n','B1: Để có được một nồi lẩu Thái hoàn chỉnh bước đầu bạn cần chuẩn bị các loại hải sản tôm, mực, nghêu,...; các loại rau nấm nhúng lẩu như rau muống, rau nhút, nấm kim châm, nấm rơm,... và bún hoặc mì để ăn lẩu. Tiếp theo đến phần quan trọng nhất là nước lẩu, bắc lên bếp một chiếc nồi lớn, cho vào nồi 1.8 lít nước, sau đó thêm vào 1 gói Lẩu Thái - Nước dùng hoàn chỉnh Barona, khuấy đều và đun sôi.\r\nB2: Sau khi đun sôi nước lẩu, cho thêm 6 lát riềng đập dập, 3 cây sả cắt khúc đập dập, 5 lá chanh tước bỏ gân và 1 quả cà chua cắt múi cau vào nồi. Đun sôi lần nữa là hoàn tất.\r\nB3: Vậy là nồi lẩu Thái thơm nồng chua cay đã sẵn sàng phục vụ các thực khách dù là khó tính nhất. Khi ăn đun nước lẩu sôi bùng, lần lượt nhúng các loại hải sản và rau nấm vào rồi thưởng thức thôi.','Nước dùng lẩu Thái Barona 1 gói\r\n(Nước dùng hoàn chỉnh Barona)\r\n Riềng 6 lát\r\n Sả 3 cây\r\n Lá chanh 5 lá\r\n Cà chua 1 trái\r\n Nước 1 3/4 lít\r\nHải sản ăn lẩu\r\n Tôm tươi 300 gr\r\n Mực 300 gr\r\n Nghêu 500 gr\r\nRau ăn lẩu\r\n Nấm rơm 100 gr\r\n Nấm linh chi nâu 100 gr\r\n Nấm kim châm 100 gr\r\n Rau muống 150 gr\r\n Rau nhút 150 gr',30,4,'Canh-Súp','Hải sản',350,'Thái Lan','Ngày lễ khác','Lẩu','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1041,1,'https://www.youtube.com/v/6XJvoIuETfY&feature=youtu.be','Cơm chiên mực trứng muối','Cơm chiên hay còn gọi là cơm rang là món ăn cứu đói hoàn hảo cho những bữa ăn bận rộn và không muốn tốn quá nhiều thời gian trong bếp. Hôm nay nhà Cooky gợi ý thêm cho bạn một công thức cơm chiên mới ngon lành dễ làm cực kì, chỉ cần 4 nguyên liệu chính là cơm nguội, mực tươi, trứng muối và trứng gà là bạn có thể làm ra một dĩa cơm cực kì ngon miệng và đẹp mắt rồi đấy. Theo dõi công thức và thực hiện ngay bạn nhé!\r\n','B1: 6 lòng đỏ trứng muối cho ra dĩa, phết lên một lớp dầu mè mỏng rồi cho vào lò nướng chín khoảng 7 phút ở 170 độ C. Sau khi nướng để trứng muối hơi nguội bớt thì lấy 4 lòng đỏ đem cắt thành những miếng nhỏ, 2 lòng đỏ còn lại đem tán nhuyễn.\r\nB2: 2 con mực tươi khoảng 400gr mua về làm sạch ruột, lột bỏ lớp da màu tím bên ngoài rồi cắt thành những khoanh tròn.\r\nB3: Đến công đoạn chiên cơm: chuẩn bị sẵn 500gr cơm nguội, cho trứng muối đã tán nhuyễn, 2 quả trứng gà vào bóp đều. Bắc một chiếc chảo nóng, cho vào 2 muỗng canh dầu ăn sau đó cho 15gr tỏi thái lát vào phi thơm. Tiếp theo cho cơm nguội đã bóp với trứng vào rang đều tay cho đến khi cơm khô tơi ra.\r\nB4: Dàn phần cơm rang qua 1/2 chảo, phần chảo còn lại thêm vào 1 muỗng canh dầu ăn, cho mực tươi vào chảo, thêm 20gr ớt sa tế khô, 15ml nước tương, 30gr dầu hào vào rồi xào cho đến khi mực săn lại và thấm gia vị. Tiếp theo trộn đều phần cơm rang và mực xào lại với nhau, đảo xóc đều tay cho đến khi cơm khô ráo, hạt cơm giòn tơi là được. Cuối cùng thêm vào phần trứng muối cắt nhỏ cùng 30gr hành lá cắt nhỏ, đảo lên lần nữa rồi tắt bếp.\r\nB5: Những miếng trứng muối bùi bùi mặn nhẹ hòa với mực tươi giòn sật mang vị ngọt tự nhiên, cơm chiên nóng hổi, giòn tơi hương vị vừa miệng không cần phải thêm nước tương như cơm chiên thông thường nữa. Để làm nổi bật hương vị hơn bạn có thể cho thêm một chút tương ớt cay the là đúng kiểu một bữa cơm chiên cứu đói hoàn hảo mà không kém phần \"\"sang chảnh\"\" rồi. Lưu ngay công thức và thực hiện những lúc đói bụng nha!','Cơm nguội 500 gr\r\n Mực 400 gr\r\n Lòng đỏ trứng muối 6 cái\r\n Trứng gà 2 quả\r\n Tỏi 15 gr\r\n Hành lá 30 gr\r\n Ớt sa tế 20 gr\r\n Nước tương 15 ml\r\n Dầu hào 30 gr\r\n Dầu ăn 3 muỗng canh',15,2,'Chiên-Rán','Cơm-Xôi-Cháo',130,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',3,'2020-07-07 00:18:05','NULL'),(1056,1,'https://www.youtube.com/v/LPvS-FruYBg&feature=youtu.be','Mì xào giòn hải sản','Mì xào giòn hay mì xào giòn hải sản là món ăn không còn quá xa lạ với người Việt mình vì rất dễ bắt gặp nó ở các biển hiệu của các quán ăn kiểu Trung Hoa trên khắp các con đường trong những thành phố lớn. Món mì xào giòn với những sợi mì được chiên giòn rụm ăn kèm với các loại rau củ và hải sản xào, vừa thơm ngon lạ miệng lại còn mang lại đầy đủ các chất.','\"B1: Cho dầu ăn vào chảo, chờ cho dầu thật nóng thì cho mì vào chiên phồng lên. Vớt ra cho vào dĩa có lót giấy thấm bớt dầu.\r\nB2: Pha nước sốt cho mì xào giòn bằng cách khuấy đều 2 muỗng canh nước tương, 1 muỗng canh dầu mè và 2 muỗng canh dầu hào lại với nhau.\r\nB3: Cho vào chảo 5 muỗng canh dầu ăn, đợi cho dầu thật nóng thì cho tỏi băm và hành tím băm vào. Phi đến khi 2 nguyên liệu chuyển vàng thơm\r\nB4: Thêm tim heo và gan heo thái lát vào, xào sơ cho săn lại. Sau đó cho tôm, mực vào, tiếp tục xào đều tay.\r\nB5: Khi các nguyên liệu vừa chín tới bạn cho cà rốt, hành tây cắt muối cau vào, thêm 3 muỗng canh nước lọc rồi tiếp tục xào 2 phút. Đến khi hành vừa chuyển hơi trong thì bạn thêm nấm rơm, xào tiếp tục 2 phút nữa.\r\nB6: Lúc này khi các nguyên liệu lâu chín đã được cho vào hết, bạn mới cho cải ngọt cắt khúc vào. Khi cải vừa chín giòn thì chế hỗn hợp nước sốt đã chuẩn bị vào chảo, xào đều cho các nguyên liệu của mì xào giòn thấm gia vị.\r\nB7: Tiếp tục thêm cà chua xắt múi cau (1 quả xắt làm 8), 1 muỗng canh bột năng pha loãng với 2 muỗng canh nước vào để tạo độ sêt. Cuối cùng thêm cần tàu xắt khúc vào, đảo đều rồi nhắc xuống là hoàn thành mì xào giòn.\r\nB8: Khi ăn bày mì ra dĩa, gắp đồ xào lên phía trên, rưới thêm nước sốt để làm cho mì xào giòn ngon mềm dễ ăn hơn. Món mì xào giòn này dùng nóng là ngon nhất, nên khi ăn bạn mới rưới sốt lên nhé, rưới sớm quá sợi mì sẽ bị nhũn, ăn mì xào giòn không còn giòn ngon nữa. Vắt mì được chiên giòn rụm ăn kèm với các loại rau củ và hải sản xào, vừa ngon ngon lạ miệng lại còn mang lại đủ các chất. Chắc chắn các thành viên trong gia đình bạn sẽ vô cùng thích thú với mì xào giòn cho mà xem.\"','Mì vắt 2 vắt\r\n(Mì dùng để chiên giòn)\r\n Tôm tươi 100 gr\r\n(Đã bỏ vỏ)\r\n Mực ống 50 gr\r\n Tim heo 50 gr\r\n Gan heo 50 gr\r\n Cải ngọt 150 gr\r\n Cà rốt 1/2 củ\r\n Nấm rơm 100 gr\r\n Hành tây 1/2 củ\r\n Cần tàu 30 gr\r\n Dầu hào 2 muỗng canh\r\n Nước tương 2 muỗng canh\r\n Dầu mè 1 muỗng canh\r\n Bột năng 1 muỗng canh\r\n Dầu ăn 300 ml\r\n Tỏi băm 20 gr\r\n Hành tím băm 20 gr',30,1,'Xào-Rang-Rim','Hải sản',130,'Trung Quốc','Ngày lễ khác','Món chính','Người gầy',1,'2020-07-07 00:18:05','NULL'),(1057,1,'https://www.youtube.com/v/wKonfahzjeI&feature=youtu.be','Canh đậu tương hải  sản Hàn Quốc','Canh đậu tương hay còn được gọi là doenjang-jjigae, đây là một món ăn truyền thống của người Hàn Quốc mà các bạn có thể dễ dàng bắt gặp ở bất cứ quán ăn đường phố hay đơn giản là trong những bộ phim của đất nước này. Những ngày thời tiết se lạnh, mát mát bạn lại muốn ăn gì đó cay cay, mặn mặn. Hãy nghĩ đến món canh đậu tương Hàn Quốc đầy ắp hải sản. Món này ăn với cơm cuộn, cơm nóng, hoặc mì thì không thể chê vào đâu được. Cùng học cách nấu canh đậu tương hải sản Hàn Quốc đơn giản tại nhà ngay nào!\r\n','B1: Cho 100gr tôm, 100gr mực và 100gr nghêu lần lượt vào nồi với 1,5 lít nước luộc sơ qua. Sau đó vớt hải sản ra rồi dùng rây lọc phần nước luộc.\r\nB2: Cho cá cơm khô vào một cái túi vải thật sạch, buộc kín rồi cho vào nồi đun với nước dùng luộc hải sản để nước canh được ngọt hơn. Đun sôi khoảng 15 phút thì vớt cá ra, đồng thời cho vào 100gr khoai tây (cắt hạt lựu), 100g bí ngòi (cắt hạt lựu) và 50gr hành tây (cắt hạt lựu). Khuấy đều rồi cho vào 2 muỗng canh tương đậu Hàn Quốc. Vì tương đậu có vị mặn sẵn nên không cần thêm các gia vị khác.\r\nB3: Khi rau củ chín cho vào 1 muỗng canh ớt bột và vài lát ớt tươi. Thêm hải sản và đậu phụ vào nồi, nấu thêm 2 phút nữa thì tắt bếp.\r\nB4: Canh đậu tương lên men của người Hàn đã từ lâu trở thành một thương hiệu ẩm thực nổi tiếng khắp thế giới. Vào một ngày trời lạnh hay khi bạn đang cảm thấy mệt mỏi, thì một bát canh đậu tương cùng cơm trắng sẽ khiến bạn thấy dễ chịu ngay lập tức. Các chị em hãy dành tặng cả nhà một chuyến du lịch Hàn Quốc ngay tại phòng ăn bằng món canh đậu tương lên men thơm ngon này nhé!','Mực 100 gr\r\n Tôm tươi 100 gr\r\n Nghêu 100 gr\r\n Cá cơm khô 50 gr\r\n Khoai tây 100 gr\r\n Bí ngòi 100 gr\r\n Hành tây 50 gr\r\n Đậu tương Hàn Quốc 2 muỗng canh\r\n Đậu hũ non 50 gr\r\n Ớt bột 10 gr\r\n Ớt sừng 5 gr\r\n(Xanh)',45,4,'Canh-Súp','Hải sản',40,'Các nước khác','Ngày lễ khác','Khai vị','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1062,1,'https://www.youtube.com/v/9zQlc67w5Ss&feature=youtu.be','Gỏi hải sản chanh  muối','Chanh muối được biết đến nhiều với công dụng làm nước uống giải khát, nhưng bạn có biết chanh muối còn có thể dùng làm sốt trộn gỏi. Nếu chưa thử qua món này thì hãy thử ngay công thức Gỏi hải sản chanh muối. Không dầu mỡ, nhiều rau nhưng vẫn giàu dinh dưỡng, món gỏi hải sản chanh muối với vị chua ngọt đặc trưng hấp dẫn giúp các bạn gái thỏa cơn ghiền ăn vặt mà không sợ lên cân! \r\n','B1: Cho vào nồi 100gr mực và 100gr tôm cùng vài lát gừng rồi luộc chín, sau đó vớt ra để ráo.\r\nB2: Cho vào chảo 3 muỗng canh đường trắng, 2 muỗng canh nước chanh muối, 1 muỗng canh nước mắm và 3 lát chanh muối, khuấy đều đến khi sôi rồi để nguội. Sau đó cho vào 1 muỗng canh tỏi băm, 1 muỗng canh ớt băm, 1 muỗng canh nước cốt tắc vào nước chanh muối đã nấu. Trộn đều cho sốt được hòa quyện với nhau.\r\nB3: Ngó sen mua về rửa sạch, cắt khúc rồi cho vào ngâm cùng với nước muối pha giấm để ngó sen được trắng. Cho vào tô lớn 150gr ngó sen đã vắt ráo nước, 50gr cà rốt cắt sợi, 10gr rau răm băm nhuyễn và hải sản đã luộc vào trộn đều cùng nước sốt chanh muối. Vậy là ta đã có một món gỏi hải sản chanh muối chua ngọt hấp dẫn rồi.\r\nB4: Lùa một đũa gỏi hải sản đầy cho vào miệng, đầu tiên sẽ cảm nhận được cái giòn giòn của ngó sen, vị ngọt của tôm mực, mùa thơm của rau, vị béo của đậu phộng và đặc biệt là vị chua ngọt rất kích thích từ nước gỏi chanh muối. Chỉ cần tưởng tượng thôi cũng khiến bạn không kìm được lòng. Còn chờ gì nữa mà không thử ngay thôi bạn ơi!','Mực 100 gr\r\n Tôm tươi 100 gr\r\n Ngó sen 150 gr\r\n Cà rốt 50 gr\r\n Rau răm 10 gr\r\n Đường trắng 3 muỗng canh\r\n Nước chanh muối 2 muỗng canh\r\n Nước mắm 1 muỗng canh\r\n Chanh muối 1 trái\r\n(Cắt lát)\r\n Tỏi băm 1 muỗng canh\r\n Ớt băm 1 muỗng canh\r\n Nước cốt tắc 1 muỗng canh',30,2,'Luộc-Hầm-Hấp','Hải sản',40,'Việt Nam','Mâm cỗ','Salad','Người tập GYM',1,'2020-07-07 00:18:05','NULL'),(1063,1,'https://www.youtube.com/v/hHHbHLABY10&feature=youtu.be','Bún mắm miền Tây','Bún mắm là món ăn ngon có nguồn gốc từ Campuchia, được nấu từ mắm bò hốc nhưng khi du nhập qua Việt Nam thì người dân xứ ta biến tấu thành mắm cá linh và mắm cá sặc, dần dà món ăn này trở thành đặc sản của vùng đồng bằng sông Cửu Long. Cách nấu bún mắm miền tây ngon với mùi hương đặc trưng, nước lèo đậm đà, ngọt thanh, kết hợp với nhiều loại thịt cá và rau sống miền sông nước, tạo nên một sự hài hoà, mang sức hấp dẫn khó thể chối từ.','B1: Đặt nồi lên bếp, cho 150gr mắm cá linh và 150gr mắm cá sặc vào nồi, thêm 1 lít nước lọc. Bật lửa lớn nấu đến khi nước sôi thấy nổi bọt thì hớt bỏ phần bọt bẩn này để nước lèo bún mắm được trong. Nấu đến khi thấy mắm rã hết chỉ còn xương thì nhắc xuống đem lọc nước luộc qua rây để loại bỏ phần xương.\r\nB2: Tiếp theo ta tiến hành làm chả ớt. Cho vào tô lớn 100gr cá thác lác, dùng chày giã cho dai cá, đến khi thấy cá không dính vào chày nữa thì thêm vào tô 100gr thịt heo xay, tiếp tục giã đến khi thịt không còn dính vào chày. Lúc này ta nêm gia vị cho chả trong bún mắm gồm 10gr hành lá cắt nhỏ, 10gr hành tím băm, 3gr ớt băm, 20gr đường trắng, 10gr hạt nêm, 10ml nước mắm, 3gr tiêu xay, 10ml dầu ăn, dùng chày quết đều gia vị là xong phần nhân. Ớt sừng đỏ để nguyên cuống, xẻ dọc quả ớt móc bỏ hạt rồi nhồi nhân vào. Đem chả ớt đi hấp 5 phút cho chín sau đó đem rán vàng.\r\nB3: Các loại hải sản ăn kèm bún mắm gồm: tôm, mực và cá lóc. Bóc bỏ đầu và vỏ tôm, chừa phần đuôi. Làm sạch mực, cắt khoanh tròn và khứa cho đẹp mắt. Lọc bỏ xương và da cá lóc, cắt thành những miếng vừa ăn. Đun sôi 500ml nước, cho các loại hải sản vào luộc chín sau đó vớt ra thả vào thau nước lạnh để giữ được màu sắc đẹp và độ giòn cho hải sản. Giữ lại nước luộc.\r\nB4: Đặt một chiếc nồi sạch lên bếp, cho vào 2 muỗng canh dầu ăn, chờ dầu nóng thì thêm vào 30gr sả băm, 10gr tỏi băm, 10gr hành tím băm và 5gr ớt băm. Xào cho các nguyên liệu vàng thơm thì chế nước luộc hải sản và nước luộc mắm vào nồi, thêm vào 3 cây sả cắt khúc đập dập rồi đun sôi.\r\nB5: Khi nước lèo bún mắm sôi ta nêm nếm lại với 50gr đường trắng và 50ml nước mắm sao cho vừa khẩu vị. Cuối cùng cho 150gr cà tím cắt khúc vào nấu đến khi nước lèo sôi lại, cà tím chín thì tắt bếp\r\nB6: Vậy bạn đã nấu bún mắm miền Tây chuẩn vị ngon rồi đấy. Muốn ăn bún mắm đúng chuẩn thì ngoài các loại hải sản và chả ớt đã chuẩn bị, heo quay là một thành phần khó có thể nào thiếu được. Bên cạnh đó, các loại rau đặc sản của vùng sông nước như điên điển, so đũa, kèo nèo, bông súng, rau đắng hoặc các loại rau sống quen thuộc hơn như rau muống, rau nhút, bắp chuối, giá đỗ là những loại rau sống tạo nên nét đặc biệt của món bún mắm ngon này. Trọn vẹn hơn nữa là chén nước me chua chua ngọt ngọt cộng thêm chút the the của ớt băm, ăn bún mắm mà thiếu nước me chấm cùng thì cứ như ăn bún đậu mà thiếu đi mắm tôm vậy đó.\r\nB7: Bún mắm với mùi hương đặc trưng, nước lèo đậm đà ngọt thanh kết hợp với nhiều loại thịt cá và rau sống tạo nên một sự hài hoà, mang sức hấp dẫn khó mà có thể chối từ, ai đã ăn qua một lần là nhớ mãi không quên. Tranh thủ dịp rảnh rỗi cuối tuần hãy vào bếp và triển ngay món bún mắm miền tây ngon đậm đà cho bữa tiệc gia đình nhé.','Bún tươi 1 gr\r\nNước lèo\r\n Mắm cá linh 150 gr\r\n Mắm cá sặc 150 gr\r\n Cà tím 150 gr\r\n Sả băm 30 gr\r\n Tỏi băm 10 gr\r\n Hành tím băm 10 gr\r\n Sả 3 cây\r\n Đường trắng 50 gr\r\n Nước mắm 50 ml\r\n Dầu ăn 2 muỗng canh\r\nChả ớt\r\n Cá thác lác 100 gr\r\n Thịt heo bằm 100 gr\r\n Hành lá 10 gr\r\n(Phần lá xanh)\r\n Ớt sừng 10 trái\r\n Hành tím băm 10 gr\r\n Ớt băm 3 gr\r\n Đường trắng 20 gr\r\n Hạt nêm 10 gr\r\n Nước mắm 10 ml\r\n Dầu ăn 10 ml\r\n Tiêu 3 gr\r\n Dầu ăn 50 ml\r\nHải sản - Heo quay\r\n Cá lóc 1 con\r\n Mực 300 gr\r\n Tôm tươi 300 gr\r\n Thịt heo quay 300 gr\r\nRau sống - Bún\r\n Rau đắng 100 gr\r\n Rau muống 50 gr\r\n(Rau muống bào)\r\n Bắp chuối 50 gr\r\n(Bắp chuối bào)\r\n Kèo nèo 100 gr\r\n Bông súng 100 gr\r\n(Thân súng)\r\n Bông so đũa 80 gr\r\n Bông điên điển 80 gr\r\n Giá đỗ 80 gr',60,4,'Luộc-Hầm-Hấp','Bún-Phở-Miến',500,'Việt Nam','Ngày lễ khác','Ăn sáng','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1064,1,'https://www.youtube.com/v/FGe1kd8A-PU&feature=youtu.be','Súp tôm bông cải xanh','Tôm và bông cải xanh là hai loại thực phẩm rất thích hợp để chế biến các món ăn dặm vì sự tươi ngon và giàu chất dinh dưỡng. Hôm nay Cooky chia sẻ với các bạn món Súp tôm bông cải xanh siêu đơn giản nhưng thơm ngon vô cùng, đặc biệt công thức sử dụng nước mắm Hải Nhi dành riêng cho trẻ nhỏ nên cực kì an toàn.\r\n','B1: Sơ chế nguyên liệu: Khoai tây gọt vỏ, rửa sạch, cắt từng miếng vuông nhỏ. Bông cải xanh rửa sạch, cắt hạt lựu nhỏ. Tôm bóc vỏ bỏ đầu và chỉ đen ở lưng, cắt tôm từng lát mỏng và bằm nhuyễn.\r\nB2: Cho 50gr khoai tây vào nấu cùng 200ml nước dùng tôm. Khi khoai tây chín mềm nhừ thì dùng muỗng nghiền nát hoặc máy xay, xay mịn khoai tây.\r\nB3: Cho 30gr thịt tôm bằm nhuyễn vào nồi khoai tây xay mịn. Dùng muỗng khuấy đều để tôm không bị vón cục. Khi tôm vừa chín tới thì cho tiếp vào nồi 30gr bông cải xanh vào nấu thêm 2 phút.\r\nB4: Khi súp đã chín, các bạn nêm vào 1/4 muỗng cà phê nước mắm Hải Nhi. Dùng muỗng khuấy đều súp và tắt bếp. Nước mắm Hải Nhi là loại đặc biệt dành riêng cho trẻ nhỏ nên cực kì an toàn và phù hợp với các bé ăn dặm.\r\nB5: Các mẹ hãy thêm ngay món súp tôm bông cải xanh thơm ngon này vào thực đơn ăn dặm cho bé thay vì các món cháo thường ngày nhé. Chắc chắn các bé sẽ rất thích thú và ăn ngon miệng đó. Món súp sánh mịn, ngọt thơm từ tôm và rau củ, vừa hấp dẫn vừa giàu dinh dưỡng cho bé.',' Khoai tây 50 gr\r\n Bông cải xanh 30 gr\r\n Tôm tươi 30 gr\r\n Dầu olive 1 muỗng cà phê\r\n Nước mắm Hải Nhi 1/4 muỗng cà phê',30,2,'Canh-Súp','Hải sản',20,'Châu Âu','Mâm cỗ','Khai vị','Người tập GYM',1,'2020-07-07 00:18:05','NULL'),(1069,1,'https://www.youtube.com/v/IsGPfxhqINA&feature=youtu.be','Cà ri nghêu cực ngon cho tín đồ nghiện sò','Mùi cà ri thơm thơm, cay cay, nước cà ri béo béo, ngọt thanh với vị nghêu đặc trưng đảm bảo bạn sẽ không ngớt xuýt xoa khi được nếm thử. Vậy còn chờ gì nữa mà chưa trổ tài nào!','B1: Tỏi, gừng băm cho ra chén để riêng. Trên một cái nồi (hoặc chảo sâu lòng) cho bơ vào đun chảy. Cho tỏi, gừng băm và đầu hành lá vào phi thơm. Sau đó, thêm hành tây cắt mỏng, ớt sừng cắt lát và 1/2 bột cà ri vào, đảo đều đến khi hành tây vừa trong thì cho nghêu vào xào.\r\nB2: Khi nghêu vừa mở hé nắp thì đổ 100ml nước sôi. Nêm gia vị 1 muỗng cà phê nước mắm, 2 muỗng cà phê đường trắng, 1/2 bột cà ri còn lại và nước cốt dừa vào, đảo đều cho nghêu hé hết nắp, tắt bếp ngay.\r\nB3: Thành phẩm: Dùng chung với bánh mì, cơm nóng hoặc bún đều rất ngon. Trước khi ăn rắc thêm hành lá, ngò rí cắt nhỏ để món ăn thêm đẹp mắt','Nghêu 500 gr\r\n Bột cà ri 1 muỗng cà phê\r\n Nước cốt dừa 80 ml\r\n Ớt sừng 2 trái\r\n Hành tây 1 củ\r\n Tỏi băm 1 muỗng cà phê\r\n Gừng băm 1 muỗng cà phê\r\n Bơ lạt 15 gr\r\n Nước mắm 1 muỗng cà phê\r\n Đường trắng 2 muỗng cà phê\r\n Ngò rí 50 gr\r\n Hành lá 50 gr',30,2,'Canh-Súp','Hải sản',40,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1070,1,'https://www.youtube.com/v/gLQQfzeWKM4&feature=youtu.be','Miến hoa đậu biếc trộn kiểu Thái','Hoa đậu biếc là nguyên liệu tạo màu rất được ưa chuộng ở Thái, từ các loại thức uống, các loại bánh ngọt cho đến các món mặn, hoa đậu biếc phủ sóng không chừa một món ăn nào. Học hỏi trên sự biến tấu đó, hôm nay trang web giới thiệu cho bạn món Miến trộn hoa đậu biếc kiểu Thái cực kì lạ mắt và màu nhiệm','B1: Đầu tiên bạn chuẩn bị một nồi có 1 lít nước lọc, bắc nồi lên bếp đun đến khi nước hơi nóng thì thả 30 hoa đậu biếc khô (khoảng 2gr) vào nồi rồi đun sôi. Chờ màu hoa đậu biếc hòa tan vào nước thành màu xanh đậm thì vớt xác hoa ra ngoài. Cho 100gr miến khô vào nồi nước, luộc miến 3 phút rồi tắt bếp, ngâm thêm 2 phút nữa đến khi sợi miến ngấm màu xanh đẹp của hoa đậu biếc.\r\nB2: Chuẩn bị một tô nước đá lạnh, sau khi ngâm miến đủ 3 phút thì bạn vớt ra tô, ngâm trong nước lạnh đến khi miến nguội hẳn thì vớt ra để ráo. Trên một chiếc chảo sạch khác, đun nóng 50ml dầu ăn rồi thả 50gr tỏi băm vào phi vàng thơm thì tắt bếp. Đổ dầu tỏi vào tô miến rồi trộn đều.\r\nB3: Chuẩn bị nước sốt trộn miến gồm: 50ml nước mắm, 50gr đường thốt nốt, 15gr ớt đỏ, 10gr tép tỏi, 1 trái chanh, 30gr cà chua bi cắt đôi và 15gr lá húng lũi. Đầu tiên bạn cho đường thốt nốt, tỏi và ớt vào cối rồi giã nhuyễn. Tiếp theo thêm nước mắm vào rồi khuấy đều cho hòa quyện. Sau đó vắt nước cốt chanh vào cối, rồi cho vỏ chanh vào luôn. Cuối cùng cho cà chua bi, và húng lủi vào rồi giã nhẹ là hoàn tất.\r\nB4: Chuẩn bị các phần chất đạm ăn kèm tùy theo sở thích và khẩu vị của gia đình. Ở đây mình có 100gr đậu hũ trắng cắt miếng vuông nhỏ chiên vàng giòn, các loại hải sản như 200gr tôm tươi đã bóc vỏ, 200gr mực tươi cắt khoanh và 300gr chem chép, tất cả đem luộc chín.\r\nB5: Chuẩn bị thêm một ít thịt băm cho món miến hấp dẫn và dễ ăn hơn. Cho vào chảo 2 muỗng canh dầu ăn, thả 15gr hành tím băm vào phi thơm. Sau đó cho 100gr thịt băm vào xào sơ, nêm nếm vào đây 1 muỗng canh nước mắm, 1/2 muỗng canh đường, 1 muỗng cà phê hạt nêm và 1 ít tiêu xay rồi xào lên cho thịt băm thấm gia vị rồi tắt bếp.\r\nB6: Xếp miến vào giữa rồi xếp thêm tôm, mực, chem chép, đậu hũ chiên và thịt bằm lên xung quanh, thêm cả trứng luộc nếu thích. Bổ sung thêm các loại rau củ cho món miến thêm phần dinh dưỡng và ăn đỡ ngán hơn như giá đỗ, rau xà lách, cà rốt, dưa leo thái sợi. Ngoài cách trình bày thông thường bạn cũng có thể biến tấu thành món miến bay để làm tăng sự hấp dẫn và mới lạ.\r\nB7: Khi ăn rưới sốt lên miến và trộn đều rồi thưởng thức. Nhưng khoan đã, còn một điều kì diệu nữa, vắt vào miến một ít chanh bạn sẽ thấy những sợi miến đổi màu từ từ, đang là màu xanh sẽ chuyển dần sang màu tím rất đẹp mắt.','Miến hoa đậu biếc\r\n Miến 100 gr\r\n(Miến khô)\r\n Hoa đậu biếc 2 gr\r\n(Khoảng 30 hoa khô)\r\n Dầu ăn 50 ml\r\n Tỏi băm 50 gr\r\nNước sốt trộn miến\r\n Nước mắm 50 ml\r\n Đường thốt nốt 50 gr\r\n Ớt 15 gr\r\n Tỏi 10 gr\r\n Chanh 1 trái\r\n Cà chua bi 30 gr\r\nCác nguyên liệu khác\r\n Húng lũi 15 gr\r\n Tôm tươi 200 gr\r\n Mực ống 200 gr\r\n Chem chép 300 gr\r\n Thịt băm 100 gr\r\n Đậu hũ trắng 100 gr\r\n Xà lách 100 gr\r\n Cà rốt 1 củ\r\n Dưa leo 1 trái\r\n Giá đỗ 100 gr\r\n Hành phi 30 gr\r\n Đậu phộng 30 gr\r\n Hành tím băm 15 gr\r\n Nước mắm 1 muỗng canh\r\n Đường trắng 1/2 muỗng canh\r\n Hạt nêm 1 muỗng cà phê\r\n Dầu ăn 100 ml',30,4,'Luộc-Hầm-Hấp','Bún-Phở-Miến',175,'Thái Lan','Ngày lễ khác','Salad','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1073,1,'https://www.youtube.com/v/xnePg3PDWls&feature=youtu.be','Nui xào kiểu Thái','Nui xào kiểu Thái là một biến tấu hoàn hảo của món Pad Thái quen thuộc, bằng việc thay bánh phở thành nui bạn đã có cho mình một công thức mới lạ mà công kém phần hấp dẫn cho bữa ăn sáng bận rộn. Nui xào kiểu Thái vẫn giữ được những hương vị truyền thống của Pad Thái như chua ngọt cũng như những thành phần quen thuộc là đậu hũ, tôm và trứng. Tín đồ món nui đừng bỏ lỡ món ăn này nha vì nó thật sự rất ngon miệng đó.','B1: Đun một nồi nước sôi, cho 200gr nui cùng 1/2 muỗng cà phê muối vào luộc khoảng 10 - 12 phút cho nui chín. Vớt nui ra xả qua nước lạnh, sau đó chắc ráo nước. Trộn nui với 1 muỗng cà phê dầu ăn để những sợi nui không bị dính vào nhau.\r\nB2: Pha nước sốt kiểu Thái: cho vào chảo 80gr đường thốt nốt, 40ml nước mắm, 40ml nước cốt me, 60gr tương ớt , khuấy đều và đun cho hỗn hợp sôi lăn tăn là được.\r\nB3: Làm nóng chảo và 2 muỗng canh dầu ăn, sau đó cho 100gr đậu hủ trắng đã cắt vuông vào chiên vàng đều. Tiếp theo cho 15gr xá bấu băm nhuyễn, 15gr hành tím băm nhuyễn vào phi cho thơm rồi mới cho 300gr tôm tươi bóc vỏ chừa đuôi và 30gr tôm khô ngâm nở vào xào.\r\nB4: Khi tôm vừa chín bạn trút nui đã luộc cùng với 50gr giá, 30gr hẹ vào xào đều tay cho các nguyên liệu hòa trộn. Cuối cùng bạn rưới số nước sốt đã pha vào nui xào, đảo lên cho nước sốt thấm vào các nguyên liệu.\r\nB5: Đến đây bạn dùng sạn đẩy nui qua một phía, chừa một khoảng trống trên chảo, đổ thêm 1 muỗng canh dầu ăn rồi đập vào đó 2 quả trứng gà. Đợi trứng hơi nóng chuẩn bị chín thì bạn dùng sạn xào đều cho trứng tơi ra. Trộn đều trứng và các nguyên liệu rồi tắt bếp.\r\nB6: Vậy là bạn đã có cho mình một đĩa nui xào kiểu Thái ngon lành, làm ăn sáng cho một ngày hoạt động nhiều hoặc chiêu đãi gia đình vào bữa cuối tuần cũng rất hợp lý. Mua nhiều nui quá mà ăn với nước lèo hoài cũng chán thì đây chính là một gợi ý quá xuất sắc luôn nha!','Nui 200 gr\r\n Muối 1/2 muỗng cà phê\r\n Dầu ăn 3 1/2 muỗng canh\r\n Đậu hũ trắng 100 gr\r\n Hành tím 15 gr\r\n Củ cải muối 15 gr\r\n(Xá bấu)\r\n Tôm tươi 300 gr\r\n Tôm khô 30 gr\r\n(Ngâm mềm)\r\n Giá đỗ 50 gr\r\n Hẹ 30 gr\r\n Trứng gà 2 quả\r\nNước sốt Thái\r\n Đường thốt nốt 80 gr\r\n Nước mắm 40 ml\r\n Nước cốt me 40 ml\r\n Tương ớt 60 g',30,2,'Xào-Rang-Rim','Bún-Phở-Miến',60,'Thái Lan','Ngày lễ khác','Ăn sáng','Người gầy',1,'2020-07-07 00:18:05','NULL'),(1078,1,'https://www.youtube.com/v/eZAo7kh_6tU&feature=youtu.be','Lẩu ghẹ kim chi','Còn gì thích hơn khi cả gia đình quây quần bên nhau thưởng thức nồi lẩu, đặc biệt là trong những thời tiết giá lạnh. Nếu như bạn đã quá quen thuộc với món lẩu từ gia cầm như vịt, gà…hãy cùng đổi vị với cách nấu lẩu ghẹ kim chi cực ngon tại nhà dưới đây nhé. Món lẩu lạ miệng với với thịt ghẹ tươi ngon, ngọt và thơm nồng hoà cùng hương vị chua chua, cay cay từ kim chi Hàn Quốc sẽ là món ăn hấp dẫn cho những bữa tiệc gia đình bạn. Cùng học ngay cách làm món ăn hấp dẫn này thôi!','B1: Nấu nước dùng lẩu: Cho vào nồi 2 muỗng canh dầu ăn, chờ dầu nóng phi thơm 15gr tỏi, 15gr hành tím, 15gr sả và 1 muỗng canh màu dầu điều. Cho vào 100gr cà chua (bổ múi cau), xào sơ sau đó cho vào nồi lẩu 2 lít nước, thả vào 50gr củ hành tây để nước dùng thêm ngọt. Khi nước sôi nêm gia vị với 1 muỗng cà phê muối, 2 muỗng canh đường, 2 muỗng canh hạt nêm, 1 muỗng canh ớt bột. Cuối cùng cho vào nồi 500gr kim chi cải thảo và 50gr nấm đùi gà, dùng vá khuấy đều nhẹ nhàng để nước dùng được hoà quyện.\r\nB2: Nước dùng lẩu đã sẵn sàng, giờ chỉ cần cho lên bếp cồn đun nóng, chờ nước sôi là có thể nhúng ghẹ vào nấu chín và thưởng thức. Ghẹ sẽ làm nước ngọt và đậm vị hơn nữa. Bạn nấu đến khi ghẹ bắt đầu chuyển màu cam đỏ đẹp mắt thì trở mặt để ghẹ được chín đều nhé. Khi ghẹ chín vớt ra đĩa, đồng thời cho các loại rau ăn kèm như tần ô, rau muống bào, cải thảo vào nồi để trụng ăn kèm lẩu.\r\nB3: Nồi lẩu ghẹ kim chi đạt chuẩn phải hài hòa hương vị, chua chua, cay cay, thịt ghẹ phải tươi ngon, ngọt và thơm nồng. Món này chan cùng bún và ăn kèm với các loại rau tuỳ thích, đặc biệt ngon nhất khi chấm cùng muối ớt xanh hoặc muối tiêu chanh. Với cách nấu lẩu ghẹ kim chi chua cay ngon miệng, hấp dẫn trên đây, hy vọng các bạn sẽ trổ tài để chiêu đãi bạn bè, gia đình vào dịp cuối tuần nhé!','Ghẹ 1 kg\r\n Kim chi cải thảo 500 gr\r\n Cà chua 100 gr\r\n Nấm đùi gà 50 gr\r\n Tỏi 15 gr\r\n Hành tím 15 gr\r\n Sả 15 gr\r\n Hành tây 50 gr\r\n Muối 1 muỗng cà phê\r\n Đường trắng 2 muỗng canh\r\n Hạt nêm 2 muỗng canh\r\n Ớt bột 1 muỗng canh\r\n Dầu điều 1 muỗng canh\r\n Cải thảo 100 gr\r\n Rau tần ô (cải cúc) 100 gr\r\n Bún tươi 500 gr',60,2,'Luộc-Hầm-Hấp','Hải sản',350,'Các nước khác','Ngày lễ khác','Lẩu','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1084,1,'https://www.youtube.com/v/2rmxmTTDWvc&feature=youtu.be','Bánh canh ghẹ chuẩn vị nhà hàng','Bánh canh ghẹ là món ăn đậm đà hương vị biển cả hấp dẫn, vừa mát vừa giàu dinh dưỡng nên được rất nhiều người yêu thích, chọn làm món “khoái khẩu”.','B1: Bắc nồi lên bếp, cho vào nồi 300gr xương ống (đã trụng) cùng 1.2 lít nước, đun sôi hớt bọt sau đó hạ nhỏ lửa, hầm 30 phút lấy nước dùng.\r\nB2: 2 con ghẹ rửa sạch, tách mai ghẹ ra khỏi thân, gỡ bỏ mang sau đó dùng muỗng múc gạch ra chén để riêng. Bắc nồi lên bếp, cho 2 muỗng canh dầu màu điều vào đun nóng, sau đó cho 15gr hành tím băm vào phi thơm. Tiếp theo cho vào nồi 1 lít nước dùng xương heo đã nấu lúc nãy rồi đun sôi.\r\nB3: Cho thêm ghẹ, 150gr chả cua, 100gr nấm rơm, 30gr sả cắt khúc chẻ nhỏ vào nồi rồi khuấy đều. Đun cho thịt ghẹ chín và ra nước ngọt\r\nB4: Sau khi thịt ghẹ chín thì gắp ra khỏi nồi, nêm nếm nước bánh canh với 1 muỗng canh đường, 1 muỗng canh hạt nêm, 1 muỗng cà phê muối và 50ml nước cốt dừa. Khi chuẩn bị ăn, lấy một chiếc vá lổ rồi cho trực tiếp bánh canh bột gạo vào nồi trụng đến khi sợi bánh canh chín mềm. Bột áo sợi bánh canh sẽ làm cho nước lèo có độ sánh nhẹ.\r\nB5: Múc bánh canh ra tô, đặt ghẹ lên trên, rắc thêm tí hành ngò cắt nhỏ là hoàn tất. Chuẩn bị thêm muối ớt xanh hoặc muối tiêu chanh để chấm thịt ghẹ, và không thể thiếu bánh quẩy để món bánh canh ghẹ tròn vị hơn. Nước bánh canh ngọt thanh thơm mùi ghẹ, béo nhẹ hương nước cốt dừa. Thịt ghe tươi dai chấm vào muối ớt xanh là hết xảy. Từ giờ đừng tốn kém ra ngoài hàng ăn bánh canh ghẹ nữa nhé, tự nấu ở nhà vừa đơn giản mà còn chất lượng, tại sao lại không thử.','Ghẹ 2 con\r\n Bánh canh 300 gr\r\n(Bánh canh bột gạo)\r\n Xương ống 300 gr\r\n Chả cua 150 gr\r\n Nấm rơm 100 gr\r\n Sả 30 gr\r\n Hành tím 15 gr\r\n Dầu điều 2 muỗng canh\r\n Nước cốt dừa 50 ml\r\n Đường trắng 1 muỗng canh\r\n Hạt nêm 1 muỗng canh\r\n Muối 1 muỗng cà phê',60,1,'Luộc-Hầm-Hấp','Bún-Phở-Miến',150,'Việt Nam','Mâm cỗ','Ăn sáng','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1086,1,'https://www.youtube.com/v/atUolzsWZuY','Rau cải bó xôi xào bò','Món xào cho ngày mưa dầm dề','B1.Ướp thịt bò với chít xì dầu, tiêu, tỏi trong 10 phút.                                                                      B2.Làm nóng chảo và cho chút dầu oliu và tỏi băm vào.                                                                                  B3.Sau đó cho bò ướt xào tái chín (2 phút) rồi cho thêm rau bó xôi đã cắt vào xào chung. Nêm nếm cho vừa ăn rồi tắt bếp là xong nè.','75 gr thịt bò                                                                                                                                                            200 gr rau bó xôi\r\nTỏi, tiêu, muối',15,4,'Xào-Rang-Rim','Rau đậu',20,'Châu Âu','Ngày lễ khác','Salad','Đối tượng khác',1,'2020-07-07 00:18:05','NULL'),(1087,1,'https://www.youtube.com/v/Fvzz4bZTjRc&t=36s','Rau muống đồng xào tỏi','Món rau ăn nhậu lai rai','B1.Rau muống đem về cắt bớt lá ngắt khúc vừa ăn rửa sạch nhiều lần cho ra mủ.nấu nồi nước luộc cho chút dầu và muối nước sôi cho rau vào luộc.                                                                                                                                                                         B2.Nước sôi bùng vài dạo thì vớt rau ra để ráo.bắc chảo cho dầu ăn vào đập dập nửa củ tỏi chiên vàng lấy tỏi ra cho rau muống vào cho dầu hào vào xào nhanh rau thấm dầu hào cho 1 phần tỏi vào.                                                                                                    B3.Trút ra dĩa cho tiêu lên và phần tỏi còn lại và thưởng thức','\"200gr Rau muống\r\n1 củ tỏi\r\ngia vị\"',15,2,'Xào-Rang-Rim','Rau đậu',10,'Việt Nam','Ngày lễ khác','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1088,1,'https://www.youtube.com/v/p5PVA4-tcKY','Rau củ xào tôm thơm ngon','Món ngon hàng ngày','\"B1.Sơ chế đồ làm món chính.\r\nB2.Cho lần lượt các nguyên liệu vào : Dầu ăn phi thơm tỏi ~> nấm hương + muối 30s ~> súp lơ + 1 chút nước ~> súp lơ chín cho tất cả các nguyên liệu còn lại vào đảo đều khoảng 1-2\', nêm nếm gia vị ok là xong ạ.                                                                                                                                                                                                                                                   B3. Bắc ra đĩa và thưởng thức                                                                                                                                                               \"','\"Dầu ăn Pam, muối\r\n10 g Tỏi\r\n5 g Nấm hương khô\r\n200 g Súp lơ xanh\r\n200 g Súp lơ trắng\r\n130 g tôm nõn đã hấp\r\n40 g Hành tây\r\n40 g Cần tây\r\n25 g ớt chuông\r\n10 g Cà rốt\"',15,2,'Xào-Rang-Rim','Rau đậu',40,'Việt Nam','Mâm cỗ','Món chính','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1089,1,'https://www.youtube.com/v/_CMsUGdFp6o','Rau cải thảo xào','Món ăn hàng ngày và món nhậu','\"B1.Cải thảo cắt xéo nhỏ, rửa sạch với ít muối, để ráo. Cho ít dầu vào chảo phi thơm đầu hành lá cắt nhỏ, cho cải vào xào nêm 1/2 mcf hạt nêm, xíu đường, đảo đều (cải thảo rất mau mềm), cải chín, tắt bếp\r\nB2.Cải còn nóng cho ra dĩa thêm hành lá cắt khúc và ít tiêu. Xong, nhanh gọn lẹ có ngay món cải xào, ăn kèm theo bữa cơm gia đình\r\nB3.Bắc ra đĩa và thưởng thức\"','\"1/2 kg cải thảo\r\nHành lá\r\nHạt nêm, đường, tiêu\"',15,4,'Xào-Rang-Rim','Thịt gia cầm',20,'Việt Nam','Ngày lễ khác','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1091,1,'https://www.youtube.com/v/9XoZXVQbTLI','Gà hấp rau mướp ăn kèm bún','Món ăn lạ cho bữa an gia đình','\"B1.Đem rau móp xắt hạt lựu, băm ức gà. Khi xào ức gà cho 2-3 lát gừng vào, khi ức gà chín lấy gừng bỏ đi. Cho tỏi ớt băm nhuyễn phi lên, rồi bỏ ức gà xào chín vào, sau đó bỏ rau móp vô. Nêm đường, nước tương, nước mắm sao cho vừa ăn.\r\nB2.Sau đó là xong rồi, rất dễ nấu, cũng rất nhanh gọn. Ăn kèm với cơm cũng rất ngon, giòn giòn, chua chua, cay cay, ăn rất đưa cơm. Có thể ăn kèm với mì tươi, bún, miến, hủ tiếu khô. (Do nhà có con nít nên mình không cho nhiều ớt, các bạn có thể cho thêm ớt chuông, băm nhuyễn cho vào nhìn màu rất đẹp, ăn cũng ngon hơn)\"','\"Rau móp 0,5 kg\r\nỨc gà 400 gr\r\nỚt, tỏi, gừng\r\nĐường, nước tương, nước mắm\"',30,2,'Xào-Rang-Rim','Thịt gia cầm',120,'Việt Nam','Tết nguyên đán','Món chính','Gia đình',3,'2020-07-07 00:18:05','NULL'),(1093,1,'https://www.youtube.com/v/l5h1ajhLuJE','Rau súp lơ xanh xào thịt bò','Món ăn ngon cho gia đình','\"B1.Chẻ lơ xanh ngâm muối rửa sạch,trần sơ qua nước sôi cho mềm.Sau đó bắt chảo lên cho dầu ăn vào để sôi xong cho tỏi băm tao vàng rồi xào thịt bò ướp sẵn(2m canh xì dầu +1 m đường +1/3 m cafe tiêu +1m canh nước) rồi cho ra dĩa để đó.\r\nB2.Tiếp tục bắt chảo lên cho ít dầu ăn vào để sôi sau đó cho hỗn hợp hành tây + cà chua + thơm đã cắt sẵn + lơ xanh vào đảo liên tục,sau đó cho thịt bò vào rồi tắt lửa\r\nB3.Bây giờ thì múc ra dĩa thưởng thức các bạn nhé\"','\"1 búp súp lơ xanh\r\n200 gr thịt bò +1 củ hành tây nhỏ\r\n2 trái cà chua + 1 miếng thơm\"',15,4,'Xào-Rang-Rim','Thịt bò',50,'Việt Nam','Ngày lễ khác','Món chính','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1095,1,'https://www.youtube.com/v/D1-sXunyJWY','Rau cải thìa xào cà chua','Món ăn tốt cho sức khỏe','\"B1.Phi thơm tỏi, cho cà chua vào xào vừa chín cho phần thân cứng cải thảo vào đảo\r\nB2.Cho gia vi vào\r\nB3.Khi thấy thân cải thảo tái, bỏ lá cải thảo vào đảo đều. Mình xài lửa trung bình và canh để cải thảo ra ít nước thôi rồi thêm hành ngò, rắc ít tiêu là thơm ngon.\"','\"Tỏi\r\nCà chua\r\nCải thảo\r\nDầu ăn, hạt nêm, muối, bột ngọt (nếu thích)\"',15,2,'Xào-Rang-Rim','Rau đậu',20,'Việt Nam','Ngày lễ khác','Món chay','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1097,1,'https://www.youtube.com/v/9b5PGpopI4U','Rau bắp cải xào tỏi','Món ăn tốt cho sức khỏe','\"B1.Thái sợi bắp cải, ngâm nước muối rồi để ráo\r\nB2.Đập dẹt tỏi\r\nB3.Phi thơm tỏi cùng 1 thìa dầu ăn, cho bắp cải vào xào cho luôn bột nêm vào xào chín\"','\"1/2 cái Bắp Cải\r\n5 củ tỏi\r\n10 gr Bột nêm\"',15,2,'Xào-Rang-Rim','Rau đậu',10,'Việt Nam','Ngày lễ khác','Món chay','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1103,1,'https://www.youtube.com/v/ctcByz0hcK4','Rau củ xào thơm','Món ăn lạ cho bữa ăngia đình','\"B1.Rửa sạch nguyên vật liệu, cắt khúc vừa ăn.\r\nB2.Xào thơm với nước cho ra chất ngọt, tiếp tục cho cà rốt và nấm vào xào. Nấm và cà rốt vừa chín tới thì cho ớt chuông và đậu bo vào xào thêm vài phút cho chín. Có thể bỏ thêm nước trong quá trình xào nếu thích.\"','\"1/2 trái thơm\r\n1 trái ớt chuông\r\n1 củ cà rốt\r\nĐậu bo\r\nNấm\"',15,4,'Xào-Rang-Rim','Rau đậu',20,'Việt Nam','Ngày lễ khác','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1105,1,'https://www.youtube.com/v/wpBocKZqnew','Rau bí xào thịt bò','Món ăn cho gia đình','\"B1.Ra vườn cắt rau bí, tước bỏ sạch sơ.\r\nB2.Vò nát lá rau sau đó rửa sạch với ba lượt nước. Để ráo nước.\r\nB3.Thịt bò thái miếng vừa ăn, đập khoảng 4 tép tỏi to. Nhóm bếp.\r\nB4.Cho chảo lên bếp, cho dầu vào cho nóng, cho tỏi và thịt bò xào tới khi chín tới, cho ra bát. Để lại phần mỡ tiếp tục cho rau bí vào xào.\r\nB5.Rau bí chín tới, cho thịt bò vào đảo đều. Nêm hai thìa hạt nêm, 1/2 thìa mỳ chính (thìa ăn cơm).\r\nB6.Đảo nhanh tay tới khi ngấm gia vị là được. Cho ra đĩa vag thưởng thức.\r\nLưu ý: rau bí ngon chủ yếu do công đoạn tước rau bí. Rau bí mềm, ngọt. Không còn sơ. Chúc các bạn tước rau bí thành công.\"','\"200 g thịt bò\r\n15 ngọn mập Rau bí\r\nHạt nêm, dầu mỡ, mỳ chính, tỏi\"',15,4,'Xào-Rang-Rim','Rau đậu',50,'Việt Nam','Mâm cỗ','Món chính','Người tập GYM',1,'2020-07-07 00:18:05','NULL'),(1109,1,'https://www.youtube.com/v/WHWRnPQJiI4','Rau bắp cải xào mì','Món ăn sáng','\"B1.Bạn để vắt mì vào tô.cho nước sôi vào thấy vừa là vớt ra.bắp cải xắt mỏng thôi.\r\nB2.Bắt chảo cho nóng cho dầu rồi cho bắp cải vào.bắp cải vừa chín thì cho mì vào.nêm nếm theo ý bé nhé bạn.khi mì chín thì tắt bếp.cho mì ra dĩa cho xúc xích lên trên.bé sẽ thích đấy\"','\"2 gói mì\r\nBắp cải\r\nThịt xay\r\nXúc xích\"',15,1,'Xào-Rang-Rim','Bún-Phở-Miến',15,'Châu Âu','Ngày lễ khác','Ăn sáng','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1110,1,'https://www.youtube.com/v/s-7Q7ejV2UY','Rau củ xào mì và thịt bò','Món ăn bình dân','\"B1.Thịt bò cắt lát mỏng ướp xíu nước mắm, bột ngọt, tiêu, hành tím, giá rửa sạch, hành tây cắt sợi\r\nB2.Ngâm mì với nước sôi cho mềm rồi đổ ra rổ cho ráo nước.\r\nB3.Bắc chảo với xíu dầu ăn cho hành tây, thịt bò lên xào tái rồi gắp ra. Cho giá lên xào với xíu hạt nêm, rồi cho mì lên trộn đều, nêm thêm xíu muối, bột ngọt, cho thịt bò lên trộn đều rồi tắt bếp.\"','\"10 vắt mì rau củ\r\n200 g thịt bò\r\n200 g giá\r\n1 củ hành tây nhỏ\"',15,2,'Xào-Rang-Rim','Thịt bò',30,'Việt Nam','Ngày lễ khác','Ăn sáng','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1115,1,'https://www.youtube.com/v/A8QKO995xCA','Rau thiên lý xào mề vịt','Món ăn ngon cho gia đình','\"B1.Tim gà mua về rửa qua với nước sạch\r\nB2.Ướp tim gà với hạt nêm, hành tím cắt nhỏ,hạt tiêu, bột gừng (Có thể dùng gừng tươi băm nhỏ) ướp từ 10-15\'\r\nB3.Đập tỏi, cho vào chảo nóng để lửa to, sau đó thêm phần tim gà đã ướp, xào đến khi phần tim săn lại\r\nB4.Cho hoa vào xào với một chút dầu hào và gia vị, đến khi hơi tái thì cho thêm tim gà, đảo nhanh tay là xong                                                                                                                B5.Chén thôi các cụ mợ ơi!!!!!\"','\"300 gr mề vịt\r\n400 gr hoa thiên lý\r\nHành tím, tỏi, bột gừng, hạt tiêu\r\nGia vị, hạt nêm, đâu hào\"',30,4,'Xào-Rang-Rim','Rau đậu',20,'Việt Nam','Mâm cỗ','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1116,1,'https://www.youtube.com/v/sAUzJbncEdo','Rau củ xào ức gà','Món ăn nhẹ giảm cân','B1.Thịt gà cắt khúc hoặc sợi, sau đó ướp 5 phút với (nước tương, mật ong, tiêu)\r\nB2.Trong thời gian đợi ướp gà. Bỏ bắp hạt và rau củ các loại trong tủ lạnh vào hấp tầm 5 phút\r\nB3.Cho gà vào xào trước rồi cho rau củ xào chung tầm 5 phút là ta có bước ăn trưa nhanh gọn lẹ rồi \r\n','100 gr Ức gà 165cal\r\n1/2 Trái bắp 90cal\r\n100 gr Đậu cô ve 100-130cal\r\nNước tương, mật ong, tiêu, ớt, dầu dừa',15,3,'Nướng','Rau đậu',30,'Châu Âu','Mâm cỗ','Món chính','Người béo phì',2,'2020-07-07 00:18:05','NULL'),(1117,1,'https://www.youtube.com/v/uA7I1VfOU2U','Đậu phụ viên chiên giòn','Món ăn lạ cho bữa ăn gia đình','B1.Tàu hũ ta không dùng đậu hũ non (đậu phụ) mua về bỏ tủ cho nó lạnh\r\nB2.Tất cả các nguyên liệu thái xong rồi trộn đều lại với nhau rồi chiên giòn lên tùy vào khẩu phần ăn của gia đình để ước lượng nguyên liệu','200 g Đậu hũ\r\n2 quả Trứng gà\r\nThịt xay\r\nNấm mèo\r\nGia vị các loại\r\nHành lá hoặc lá lốt\r\nCà rốt',30,2,'Chiên-Rán','Rau đậu',15,'Việt Nam','Ngày lễ khác','Ăn vặt','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1119,1,'https://www.youtube.com/v/jHDdOFyxcfo','Đậu phụ rán giòn ','Món ăn hàng ngày và món nhậu','B2.Mua 500g đậu phụ. Về rửa nhẹ với nước. Để ráo, cắt thành miếng vừa ăn. Cho dầu vào chảo đun nóng cho đậu vào rán vàng 2 mặt.\r\nB2.Nước chấm đậu: 2 thìa cà phê đường, 1/2 thìa cà phê mỳ chính. 4 thìa nước mắm (thìa ăn cơm), hai tép tỏi đập nát, cho 1 thìa tương ớt chinsu. Vắt 1 thìa nước cốt chanh trộn đều các hỗ hợp cho tan. Nếu các bạn thích ăn cay có thể cho thêm 1 quả ớt chỉ thiên băm nhỏ. Sau khi đậu rán vàng gắp ra đĩa, lấy 1/2 thìa mỡ còn lại trên chảo rán cho vào bát nước chấm ngấy đều. Chấm đậu với nước chấm ăn với cơm.','500 g Đậu phụ\r\nNước mắm, dầu thực vật, mỳ chính, đường, chanh, tương ớt chinsu, tỏi',15,2,'Chiên-Rán','Rau đậu',10,'Việt Nam','Ngày lễ khác','Món chay','Người béo phì',2,'2020-07-07 00:18:05','NULL'),(1121,1,'https://www.youtube.com/v/Cn88RlWipJU','Đậu phụ sốt cà chua','Món ăn hàng ngày cho cả gia đình','B1.Đậu phụ xắt miếng vừa ăn rồi rán vàng đều rồi cho ra đĩa. Cà chua rửa sạch, thái lát. Rau mùi rửa sạch để ráo nước. Hành khô băm nhỏ.\r\nB2.Cho dầu vào chảo đến nóng đều thì thêm hành khô băm sẵn phi thơm, vàng. Tiếp tục cho cà chua cắt lát vào đảo đều cho nhuyễn, thêm 1 chút bột canh sau đó thì cho đậu phụ đã rán và 1 xíu nước. Đun cho ngấm đều bỏ vào đĩa.\r\nB3.Cho 1 xíu tiêu xay tuỳ khẩu vị và trang trí bằng rau mùi đã chuẩn bị.','2 bìa đậu phụ\r\n2 quả cà chua, rau mùi\r\nHành khô, dầu, tiêu xay, bột canh',15,4,'Kho-Om','Rau đậu',15,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1123,1,'https://www.youtube.com/v/XX0V94t1bVQ','Đậu phụ sốt chua ngọt','Món ăn hàng ngày cho cả gia đình','B1.Chiên đậu phụ bằng dầu oliu (hoặc các loại dầu khác đều được). Có thể sử dụng nồi chiên không dầu (nếu có) để hạn chế dầu mỡ trong món ăn.\r\nB2.Cho tương ớt chua ngọt vào, đảo đều với đậu phụ. Hấp khoai lang, bắp, đậu hà lan, nấm đông cô.\r\nB3.Cuối cùng, cho lần lượt chả lụa chay (nếu là gà viên chay hay sườn non chay,... đóng gói thì ngâm nước, sơ chế theo hướng dẫn) và đậu, bắp,... vào đảo đều.','Đậu phụ mềm (chưa chiên) hoặc chiên sẵn\r\nChả lụa chay (có thể thay bằng gà viên chay, sườn non chay, bò chay, chả giò chay,...)\r\nĐậu hà lan, bắp, khoai lang,nấm đông cô (có thể thêm cà rốt, cà chua,nấm đùi gà, nấm mèo,...)\r\nTương ớt chua ngọt cholimex',15,4,'Kho-Om','Rau đậu',15,'Việt Nam','Ngày lễ khác','Món chay','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1125,1,'https://www.youtube.com/v/V62B-tJIL78','Đậu phụ kho nấm tiêu xanh','Món ăn dành cho gia đình','B1.Đậu phụ cắt miếng vuông khoảng 3cm\r\nB2.Nấm ngọc châm tách rời từng cây, nấm hương cắt đôi hoặc cắt làm tư.\r\nB3.Cho đậu, nấm, tiêu xanh vào nồi, nêm nước tương, muối, hạt nêm và 1 muỗng canh dầu ăn sau đó cho thêm 1 khoảng 20ml nước.\r\nB4.Kho nhỏ lửa khoảng 20’ cho nguyên liệu thấm rồi tắt bếp.','5 miếng (khoảng 200-250g) Đậu phụ nhỏ\r\n100 g Nấm hương tươi\r\n100 g Nấm ngọc châm (hoặc thuỷ tiên)\r\n2 muỗng canh Nước tương:\r\nTiêu xanh, muối, hạt nêm chay, dầu ăn',30,4,'Kho-Om','Rau đậu',30,'Trung Quốc','Tết nguyên đán','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1126,1,'https://www.youtube.com/v/Cj34qjH-etE','Đậu phụ xào giá hẹ','Món ăn dành cho gia đình','B1.Chiên đậu phụ bằng dầu oliu (hoặc các loại dầu khác đều được). Có thể sử dụng nồi chiên không dầu (nếu có) để hạn chế dầu mỡ trong món ăn.\r\nB2.Cho dầu, đảo đều giá hẹ trên chảo. Nêm hạt nêm, tiêu,... vừa ăn. Đảo thêm cho đến khi thấy giá mềm, cho đậu phụ vào, đảo thêm khoảng 3 phút.\r\nB3.Ăn kèm với nước mắm (chua hay mặn đều được).','Đậu phụ mềm vừa (chưa chiên) hoặc chiên sẵn\r\nGiá\r\nHẹ',30,4,'Xào-Rang-Rim','Rau đậu',20,'Việt Nam','Ngày lễ khác','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1128,1,'https://www.youtube.com/v/xGPXwkd5Xb8','Đậu phụ nấm om nước tương','Món ăn lạ miệng','B1.Đậu phụ cắt thành miếng vuông. Nấm rơm cắt đôi. Mộc nhĩ ngâm mềm cắt sợi.\r\nB2.Chiên vàng đậu phụ, băm hành tỏi vào chiên cùng cho thơm vàng hành tỏi. Cho nấm rơm, mộc nhĩ vào đảo sơ. Cho nước tương, xíu đường, tiêu, tương ớt vào đảo đều. Cho chút nước vào om khoảng 5p.','Đậu phụ\r\nNấm rơm, mộc nhĩ\r\nHành tỏi, nước tương, đường, tiêu, tương ớt',30,4,'Kho-Om','Rau đậu',20,'Trung Quốc','Mâm cỗ','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1134,1,'https://www.youtube.com/v/dVd3ruHx0Vs','Đậu phụ đảo bông với ngò rí','Món ăn nhắm','B1.Đậu phụ cho vào hộp, để lên ngăn đá 1 đêm. Sau đó mang ra rã đông, vắt ráo nước, bóp nhuyễn.\r\nB2.Boa rô bằm nhuyễn, phi thơm. Cho đậu phụ vào xào, nêm hạt nêm, nước mắm chay, hạt tiêu. Xào nhỏ lửa khoảng 10-15’, đậu khô tơi thì tắt bếp, rắc rau ngò rí vào.\r\nB3.Món này ăn với cơm hoặc cháo trắng hay dùng chiên cơm rất ngon.','200 g Đậu phụ tươi\r\n1 muỗng canh Nước mắm chay\r\nHạt nêm chay\r\nHạt tiêu\r\n1-2 muỗng canh Dầu ăn\r\nRau ngò rí (mùi ta)\r\nBoa rô',30,4,'Xào-Rang-Rim','Rau đậu',30,'Trung Quốc','Ngày lễ khác','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1136,1,'https://www.youtube.com/v/gRsf4H35TmM','Đậu phụ kho nấm','Món ăn ngon cho gia đình','Đậu phụ cắt miếng vuông 2,5 cm. Cho đậu vào chiên vàng. Lưu ý chiên vàng vừa phải không chiên quá kỹ đậu sẽ bị khô xác.\r\nNấm đông cô cắt đôi hoặc cắt tư, nấm thuỷ tiên tách rời từng cây.\r\nCho nước tương, hạt nêm chay vào nồi, cho đậu phụ chiên vào xóc đều cho thấm sau đó cho thêm một ít nước. Cho nấm vào rồi bật bếp kho nhỏ lửa. Kho khoảng 15’, đậu ngấm gia vị, nấm chín mềm là được, rắc thêm 1 chút hạt tiêu cho thơm.','Nguyên Liệu\r\n4 miếng Đậu phụ nhỏ\r\n100 g Nấm đông cô tươi\r\n100 g Nấm thuỷ tiên\r\nNước tương\r\nHạt nêm chay\r\nHạt tiêu\r\nDầu ăn',30,4,'Kho-Om','Rau đậu',30,'Trung Quốc','Ngày lễ khác','Món chay','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1138,1,'https://www.youtube.com/v/esC0WuaDeQc','Đậu phụ nhồi thịt sốt cà','Món ăn lạ và ngon','B1.Đậu mua về rửa qua với nước cho sạch rồi để ráo. Dùng thìa lấy bỏ phần ruột ở giữa miếng đậu để riêng.\r\nB2.Trộn đều phần ruột đậu với thịt băm, nấm mèo băm, thêm gia vị vừa ăn. Ướp khoảng 15p.\r\nB3.Nhồi hỗn hợp ở bước 2 vào phần giữa miếng đậu. Đem đi chiên vàng các mặt. Phần thịt dư vo viên lại rồi cũng chiên đến khi chín.\r\nB4.Chuẩn bị sốt: phi thơm hành rồi cho cà chua vào xào đến khi cà mềm thì cho chút nước vào tạo độ sệt. Cho đậu nhồi thịt và phần thịt viên vào nấu khoảng 5p cho thấm rồi tắt bếp.Thêm hành lá rau mùi tuỳ ý.','4 miếng đậu phụ nhỏ\r\nThịt heo xay\r\nNấm mèo băm nhỏ\r\n2 quả cà chua cắt hạt lựu\r\nGia vị',30,4,'Kho-Om','Rau đậu',50,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1139,1,'https://www.youtube.com/v/-t582qusyKc','Củ cải muối/Cải xá bấu','Món ăn kèm','B1.Củ cải nên chọn loại trắng, mượt, thon dài sẽ ngọt hơn. Có thể cắt bỏ lá hoặc để nguyên lá tùy thích. Rửa sạch củ cải và để thật ráo nước mới ướp.\r\nB2.Ngâm củ cái vào buổi tối và phơi vào ban ngày. Chuẩn bị 1 thâu, rải 1 lớp muối - 1 lớp cải – 1 lớp muối – 1 lớp cải – trên cùng là một lớp muối.\r\nB3.Sau ngày thứ nhất, nước đã ra ngập củ cải, vớt cải ra phơi. Lập lại quá trình tối ngâm, sáng phơi trong thời gian 5 ngày 5 đêm. Nếu củ cải nhỏ, sẽ giảm thời gian xuống 1 ngày.\r\nB4.Ngày thứ 5 có thể phơi thêm 1 ngày nắng nữa (nếu củ cải bạn mua là loại lớn), sao cho củ cải khô nhưng vẫn còn độ dẻo của nó.\r\nB5.Các bạn cho vào lọ dùng dần được 1 năm.\r\nB6.Để càng lâu, củ cải lên màu càng đẹp. Củ cải màu sậm là củ cải mình ngâm được 1 tháng, màu lên dễ thương lắm nè.\r\nB7.Mình thường dùng củ cải muối để nấu hủ tiếu. Nếu ăn với xôi, cháo thì sẽ thái sợi củ cải, sau đó ngâm củ cải với nước và thay nước vài lần để giảm bớt độ mặn rồi mới chế biến tùy thích.','1 kg củ cải\r\n400 g muối hột\r\n1 cái thau có nắp đậy',60,4,'Luộc-Hầm-Hấp','Rau đậu',20,'Việt Nam','Tết nguyên đán','Salad','Người béo phì',1,'2020-07-07 00:18:05','NULL'),(1142,1,'https://www.youtube.com/v/ukzWYP6Iy8Y','Củ cải muối chiên hột vịt','Món lạ ','B1.Củ cải rửa sơ, xắt lát mỏng rồi xắt thành sợi nhỏ. Ngâm rửa lại nhiều lần để bớt mặn\r\nB2.Trứng đập ra, đánh tan rồi nêm gia vị vào, thêm hành lá\r\nB3.Hành tím xắt lát mỏng, đem phi thơm rồi đổ vào tô trứng, sau đó là củ cải.\r\nB4.Cho dầu ăn lên chảo, múc một vá trứng cho vào chiên vàng, làm lần lượt đến khi hết trứng.\r\nB5.Ăn kèm nước mắm, cải chua hoặc dưa leo muối chua cũng rất ngon','2 cái trứng vịt\r\n1 củ cải muối\r\nHành tím, nước mắm, hạt nêm, bột ngọt',45,4,'Chiên-Rán','Trứng',20,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1145,1,'https://www.youtube.com/v/PyHFFSMcW4U','Củ sen hầm canh gà','Món tốt cho sức khỏe','B1.Thịt gà rửa sạch, chặt khúc vừa ăn. Nấm đông cô khô ngâm nước ấm cho nở, rửa sạch, cắt đôi. Cà rốt bào vỏ, rửa sạch, cắt khúc vừa ăn. Củ sen rửa sạch, cắt khúc vừa ăn. 2 tép tỏi băm nhỏ.\r\nB2.Bắt nồi lên bếp, nồi nóng cho dầu ăn và tỏi vào phi thơm. Cho tiếp thịt gà vào xào cho thịt săn lại. Đổ 1.5 lít (tuỳ bạn muốn nước nhiều hay ít) nước lọc vào nồi nấu sôi.\r\nB3.Nước sôi cho tiếp củ sen, cà rốt, nấm đông cô vào nồi. Nêm nếm gia vị vừa ăn. Nấu lửa vừa hầm cho gà và củ mềm.\r\nB4.Món ăn có thể làm canh để dùng cùng cơm trắng. Hoặc làm nước dùng để ăn mì, nui.','5 cái má đùi gà\r\n200 gram nấm đông cô\r\n300 gram củ sen làm sẵn\r\n1 củ cà rốt\r\nGia vị thường dùng',45,4,'Luộc-Hầm-Hấp','Thịt gia cầm',40,'Trung Quốc','Mâm cỗ','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1146,1,'https://www.youtube.com/v/aastGYnnYsc','Củ dền súp bào ngư','Món lạ và bổ dưỡng','B1.Bào ngư khô ngâm mềm, cắt nhỏ hạt lựu. Nình nhừ tầm 15 phút.\r\nB2.Tôm băm nhỏ hạt lựu, củ dền, ngô bao tử thái hạt lựu\r\nB3.Súp bào ngư vây cá củ dền bước làm 3 hình\r\nB4.Súp bào ngư vây cá củ dền bước làm 3 hình\r\nB5.Nấm hương, nấm tuyết ngâm xong thái nhỏ hạt lựu\r\nB6.Cho tôm, nấm hương, nấm hương, nấm tuyết, vây cá, củ dền vào ninh cùng với bào ngư đã ninh, nêm nếm gia vị cho vừa ăn. Tiếp tục cho bột năng và trứng đánh đều lên. Múc ra ăn cùng rau mùi thái nhỏ, hạt tiêu','6-7 con bào ngư khô\r\n5-6 con Tôm bóc vỏ\r\nThịt cua hoặc thịt ghẹ\r\nCủ dền\r\nNgô bao tử\r\nBột năng, trứng\r\nNấm hương, nấm tuyết\r\nGia vị, rau mùi\r\n1 nhúm Vây cá mập',60,4,'Canh-Súp','Hải sản',460,'Châu Âu','Ngày lễ khác','Khai vị','Người gầy',1,'2020-07-07 00:18:05','NULL'),(1147,1,'https://www.youtube.com/v/5y2F-7CT91s','Củ sen hầm xương','Món ăn bồi bổ','B1.Rửa sạch xương trụng nước sôi\r\nB2.Xào xương và củ sen với gia vị cho thơm, rồi đổ nước sôi vào hầm cho xương và củ sen mềm','2 cây củ sen\r\n1/2 kg xương\r\nGia vị',45,2,'Luộc-Hầm-Hấp','Thịt lợn',40,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1150,1,'https://www.youtube.com/v/ei1lbsOGWzw','Củ sen hầm canh sườn non','Món ăn ngon cho gia đình','B1.Sườn non chặt miếng nhỏ, trụng nước sôi, sả sạch. Cho vô 2 lít nước nấu lửa nhỏ\r\nB2.Củ sen gọt vỏ, dùng dao tách thành từng cục, luộc sơ vói nước pha dấm, sau đó cho vô nấu voi sườn 45 phút.\r\nB3.Cà rốt gọt vỏ, cắt lát dày.\r\nB4.Sau khi nâu sườn và củ sen được 45 phút thì cho cà rốt vô nấu 15 phút.\r\nB5.Nêm canh với 5 gr muối, 5gr hạt nêm 10 gr đường phèn và 10 gr dầu hào để tăng vị đậm đà.\r\nB6.Cho canh ra thố, rắc tiêu và hành ngò là ','300 gr sườn non\r\n500 gr củ sen\r\n150 gr cà rốt\r\nGia vị gồm muối, hạt nêm, đường phèn, dầu hào\r\nHành ngò thái nhỏ và tiêu',60,4,'Canh-Súp','Thịt lợn',120,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1151,1,'https://www.youtube.com/v/1Ntz59ROsuI','Củ sen hầm gà cho bữa cơm gia đình','Món ăn ngon cho gia đình','B1.Gà rửa sạch chặt vừa ăn, ướp muối bột nêm khoảng 15 phút, nấm rửa sạch ngâm cho mềm\r\nB2.Củ sen gọt vỏ thái lát mỏng, khi thái thả vào chậu nước có pha dấm và 1 chút muối. Cả nhà nhớ cho tẹo dấm thôi không sẽ chua nhé\r\nB3.Cà rốt tỉa hoa, hành mùi thái nhỏ\r\nB4.Gà xào săn rồi cho thêm nước đủ ăn, khi sôi vớt bọt rồi thêm củ sen vào đun nhỏ lửa khoảng 20 phút\r\nB5.Sau đó cho thêm ngô ngọt đã cắt nhỏ vào và đun thêm 10 phút. Các bạn nhớ đun lửa nhỏ tránh bị đục nước nhé\r\nB6.Gần đến thành công rồi giờ thêm cà rốt và nấm hương vào đun thêm 10 phút\r\nB7.Và thành quả khi bạn cho hành, mùi vào như thế này đây chúc cả nhà ngon miệng với công thức này nhé.','1 cái đùi gà (nhà mình chỉ thích ăn đùi)\r\n1 củ sen nhỏ\r\n1/2 củ cà rốt\r\n1 bắp ngô ngọt\r\nNấm hương, bột ngọt, muối, hành, mùi',60,4,'Canh-Súp','Thịt gia cầm',80,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1153,1,'https://www.youtube.com/v/qyaxSTeXBRs','Củ sen hầm thịt bò','Món ăn bổ dưỡng','B1.Thịt bò dùng thịt bò bắp là ngon nhất, nếu không có như mình thì dùng loại có dính tí mỡ để không bị quá khô là được. Thịt bò cắt miếng vừa ăn ướp với 1 tý nước mắm.\r\nB2.Củ sen chọn loại dài thuôn thì sẽ ngon hơn loại to nhé. Gọt vỏ củ sen, thái khoanh vừa ăn. Nấm hương ngâm vào nước nóng cho nở ra.\r\nB3.Tỏi băm nhuyễn, cà chua thái múi cau, hành cắt khúc vừa ăn\r\nB4.Cho 1 thìa cafe dầu ăn vào nồi, khi dầu sôi cho tỏi vào xào vàng thì cho thịt bò vào xào cho săn lại rồi cho nước sôi vào. Sau đó hớt qua hết bọt bẩn, rồi cho củ sen, cà chua, nấm hương, 1 cánh hoa hồi, nếu thích ăn cay thì cho thêm ớt, thêm bột gia vị rồi đậy vung hầm lửa liu riu cho đến khi mềm hẳn. Mình hầm bằng nồi áp suất thì mất khoảng 25 phút.\r\nB5.Trước khi tắt bếp nêm nếm lại cho vừa miệng, cho hánh lá, rau mùi vào rồi múc ra dùng nóng. Chúc các bạn thành công','300 g thịt bò\r\nVài cái củ sen\r\n2 quả cà chua\r\n10 cái nấm hương\r\nTỏi, nước mắm, hành, rau mùi, bột nêm, dầu ăn, hoa hồi (ớt)',60,4,'Luộc-Hầm-Hấp','Thịt bò',110,'Trung Quốc','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1158,1,'https://www.youtube.com/v/M4_ZSTrBiOw','Quả bơ và trứng chiên','Món ăn sáng','B1.Gọt vỏ quả bơ tách đôi quả và lấy hạt ra, trứng đập chia vào 2 nửa quả bơ\r\nB2.Cho dầu láng mặt chảo chiên ở lửa nhỏ vừa, chỉ chiên mặt có trứng và cho ra đĩa rắc chút gia vị và thưởng thức món ngon tuyệt :)\r\n*Lưu ý k nên làm với nồi chiên k dầu, trứng chưa kịp chín nhiệt đã làm quả bơ chín nhũn và bị ngấy','1 quả bơ chín tới\r\n1 quả trứng gà\r\nDầu ăn, gia vị',30,4,'Chiên-Rán','Trứng',40,'Việt Nam','Ngày lễ khác','Món chính','Đối tượng khác',1,'2020-07-07 00:18:05','NULL'),(1163,1,'https://www.youtube.com/v/BaddmdRycsM','Quả bơ salad cá hồi sốt dấm đen','Mùa bơ đến rồi, tranh thủ ăn bơ thôi!','B1.Cá hồi ướp với xíu muối và tiêu xay.\r\nB2.Cho 1 muỗng canh dầu vào chảo chống dính. Lửa lớn áp chảo mặt da trước 2ph. Lật cá lại áp chảo thêm 2.5 ph cho chín tới là được.\r\nB3.Rau củ xắt khối vuông 1 cm. Khoai tây, Củ dền luộc chín. Bày rau củ ở dưới, xếp cá lên trên, rắc chút xíu muối tiêu. Khi ăn dầm cá nhỏ trộn sốt mayonaise, dùng kèm bánh mì nướng giòn rất ngon.','1 Trái bơ dài 034\r\n2 miếng Cá hồi có da\r\n1/2 củ Củ dền\r\n1/2 củ khoai tây luộc chín\r\n2 trái Dưa leo baby\r\n2 muỗng canh Mayonaise\r\n1/2 trái Cà chua\r\nTiêu xay\r\nDầu hạt cải',30,2,'Luộc-Hầm-Hấp','Hải sản',40,'Châu Âu','Mâm cỗ','Salad','Người tập GYM',1,'2020-07-07 00:18:05','NULL'),(1164,1,'https://www.youtube.com/v/UGYSbp-zqgM','Quả sấu om chân giò','Món ăn bổ dưỡng','B1.Luộc sơ chân giò, ướp với nước mắm, đường\r\nB2.Đun đường đến khi đạt màu cánh gián thì trút chân giò vào, thêm 10 quả sấu, hầm 10 phút\r\nB3.Mở nắp nồi áp suất, đun thêm 1 chút cho cạn bớt nước. Phần nước chấm rau muống luộc rất ngon.','1 cái Chân giò\r\n10 quả sấu\r\nHành, tỏi',60,2,'Kho-Om','Thịt lợn',60,'Trung Quốc','Mâm cỗ','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1168,1,'https://www.youtube.com/v/bRBJYFCRN9M','Quả sấu om vịt và khoai sọ','Món ăn ngon cho gia đình','B1.Sơ chế vịt sạch sẽ\r\nB2.Ướp vịt với 2 cây sả, dầu hào, đường, nước mắm, mì chính\r\nB3.Cho vịt vào xào săn lên, cho sấu, khoai môn vào ninh đến khi nhừ vịt và nước hơi sánh\r\nB4.Nêm nếm gia vị vừa miệng. Vị sẽ hơi chua của sấu, ngọt của thịt vịt, thơm mùi sả và sánh ngậy bởi khoai môn\r\nChúc cả nhà ngon miệng nhé','1/2 con Vịt\r\n2 cây Sả\r\n5 quả sấu\r\n0,5 kg Khoai sọ\r\nDầu hào\r\nĐường\r\nMì chính\r\nNước mắm\r\n1 chút nước',60,2,'Kho-Om','Thịt gia cầm',70,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1169,1,'https://www.youtube.com/v/mZQ47c2CWJg','Quả sấu ngâm đường','Món giải khát','B1.Pha một thau nước muối loãng. Dùng dụng cụ gọt sấu cạo sạch vỏ sấu cạo xong quả nào thì cho vào thau nước muối loãng ngâm để sấu không bị thâm. Sau khi đã gọt hết thì rửa lại sấu.\r\nB2.Đun một nồi nước, nước vừa sôi cho sấu vào, để khoảng 3 phút đến khi sấu ngả sang màu vàng thì vớt sấu ra rổ và để ráo hoàn toàn (việc làm này giúp sấu bớt độ nhớt, khi ngâm sấu giòn và không bị nổi váng).\r\nB3.Ngâm sấu bằng 2 cách: nấu nước đường hoặc đổ đường trực tiếp vào sấu. Mình hay ngâm bằng cách nấu nước đường, cách này sấu không bị nổi váng. Với mình thì 1 kg sấu sau khi đã gọt vỏ ngâm với nửa kg đường, tùy vào khẩu vị mà ước tính lượng đường.\r\nB4.Sấu sau khi đã ráo nước thì mình xếp vào một lọ thủy tinh khô, đổ nước đường để nguội vào, bọc một túi nước sôi nguội và đè ở trên để sấu không bị nổi lên. Sấu ngâm đường để ít nhất khoảng 1 tháng hãy dùng.\r\nB5.Đập đá cho vào ly, múc nước sấu ngâm cho vào cùng vài quả sấu, thêm nước sôi nguội và thưởng thức.','Sấu tươi\r\nĐường\r\nMuối',15,4,'Có cách nấu khác','Nguyên liệu khác',20,'Việt Nam','Tết nguyên đán','Đồ uống','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1170,1,'https://www.youtube.com/v/xKYR5gCOtn0','Quả bơ Avocado custard - Custard ','Món ăn khác từ quả bơ ','B1.Bơ rửa sạch, cắt đôi, múc thịt xay nhuyễn lấy 30ml.\r\nB2.Đánh tan lòng đỏ trứng gà ra. Cho bột bắp vào trộn đều.\r\nB3.Sữa công thức pha ấm 40 độ. Đổ từ từ sữa vào hh trứng & bột bắp, khuấy đều.\r\nB4.Bắc lên bếp đun nóng hh. Khuấy đều tay để hh sệt lại.\r\nB5.Tắt bếp, cho bơ vào và khuấy đều tay. Đổ hh vào hũ tt.\r\nB6.Chờ hh nguội thì cho vào ngăn mát tủ lạnh, bảo quản được 5 ngày.','120 ml sữa công thức (pha gấp đôi số muỗng sữa)/ sữa mẹ\r\n15 gr bột bắp\r\n1 lòng đỏ trứng gà\r\n30 ml bơ',30,1,'Nướng','Rau đậu',30,'Châu Âu','Mâm cỗ','Bánh','Gia đình',3,'2020-07-07 00:18:05','NULL'),(1175,1,'https://www.youtube.com/v/xzdpo2LW06c','Quả bơ pudding hạt chia','Một món ăn dặm cho bé hay ăn nhẹ buổi chiều, thay vì xay sinh tố thì thêm chút gelatin sẽ được hỗn hợp hơi đông sánh, mềm mịn.\r\nTrái bơ có nhiều vitamin và chất béo tốt cho cơ thể, nhất là trẻ em. Mùa bơ tới rồi cả nhà cùng ăn bơ nhé.','B1.Bơ cắt đôi, bỏ hạt, xẻ miếng, múc ra tô\r\nB2.Xay mịn với đường, sữa, nước cốt chanh. Nước chanh giúp hỗn hợp bơ xay giữ màu xanh đẹp bắt, không bị thâm đen lại.\r\nB3.Ngâm gelatin với 12 ml nước. Quay 20s trong lò vi sóng cho tan. Sau đó trộn vào hỗn hợp bơ. Thêm hạt chia\r\nB4.Múc ra từng ly nhỏ. Để lạnh ít nhất 4h.','1 trái bơ 400gr\r\n1 mcf hạt chia\r\n1/2 mc nước cốt chanh\r\n1 mcf Đường cát (tùy thích)\r\n15 ml Sữa đặc có đường\r\n2.5 g gelatin bột',45,1,'Nướng','Trứng',40,'Châu Âu','Ngày lễ khác','Ăn vặt','Gia đình',18,'2020-07-07 00:18:05','NULL'),(1176,1,'https://www.youtube.com/v/JdWWMV6bkd4','Bánh bột mì chiên ','Món ăn vặt, món quà chiều cho mọi người','B1.Đập trứng gà bỏ vào hỗn hợp sữa và đường, đánh cho tan đường rồi bỏ bột mì khuấy đến khi hỗn hợp sánh mịn\r\nB2.Làm nóng chảo, đổ dầu hoặc bơ đều chảo, bắt đầu đổ hỗn hợp bột đã trộn\r\nB3.Chiên đến khi bánh chín vàng, vớt ra dĩa có lót giấy thấm bớt dầu và thưởng thức','1 quả trứng gà\r\n250 ml sữa tươi\r\n5 muỗng đường\r\n200-250 g bột mì',15,2,'Chiên-Rán','Trứng',30,'Việt Nam','Ngày lễ khác','Bánh','Gia đình',2,'2020-07-07 00:18:05','NULL'),(1185,1,'https://www.youtube.com/v/dGDPOr8BNQ8','Bánh bột mì thơm ngon ','Nên làm gì khi nhà chỉ có bột mì ? Hnay mình thấy công thức này hay ho, không ngờ làm ra nó y như bánh pancake và ăn rất ngon nhé.','B1.Cho trứng gà, 1 muỗng canh sữa đặc, 1 muỗng canh đường, 1/3 muỗng muối vào tô, đánh cho bông lên như làm bánh bông lan đồ í\r\nB2.Cho bột mì rây mịn vào tô (dùng bột mì đa dụng số 8/ số 11 đều được). Khuấy đều thành 1 hỗn hợp\r\nB3.Bắt chảo cho nóng lên rồi hạ lửa ở mức nhỏ nhất. Có thể dùng dầu ăn hoặc bơ lạt để chiên, không nên dùng bơ mặn vì nó ảnh hưởng đến vị của bánh. Dùng bơ lạt tráng trên mặt chảo, không nên cho quá nhiều. Sau đó đổ bánh thành từng cái tròn nhỏ. Đến khi thấy viền bánh khô lại, không dính chảo thì có thể lật bánh. Cho bánh ra đĩa thôi\r\nB4.Về phần trang trí, xếp bánh thành chồng, rắc vột cacao và đổ mật ong lên là ngon lắm luôn. Món này có thể dùng ăn sáng hoặc ăn nhẹ đều được.','1 quả trứng gà\r\n30 g bột mì (có thể nhắm chừng, không cần chuẩn xác)\r\n1 muỗng canh đường\r\n1 muỗng canh sữa đặc\r\n1/3 muỗng cà phê muối',15,1,'Chiên-Rán','Trứng',20,'Việt Nam','Ngày lễ khác','Bánh','Gia đình',1,'2020-07-07 00:18:05','NULL'),(1192,1,'https://www.youtube.com/v/g_cAAMd-DvU','Bánh canh bột mì','Món ăn ngon cho gia đình','B1.Bột mì cho vô thau nhựa với xíu muối cho khoảng 220ml nước nguội vô trộn đều rồi nhồi thành khối, cho xíu dầu ăn vô láng đều khối bột rồi để bột nghỉ 30 phút cho bột nở sẽ dễ cán mỏng.\r\n- Lấy bột ra nhồi sơ lại, chia làm 4 phần, cán mỏng từng phần, áo bột khô lên đều miếng bột rồi xếp chồng lên nhau dùng dao cắt sợi.\r\nB2.Cắt xong gở cho sợi bột tơi ra.Nấu nước sôi cho bột lên luộc 3 phút rồi đổ ra sả lại nước nguội cho hết dính.\r\nB3.Tôm rửa sạch, luộc chín với ít nước rồi lột vỏ tôm cho vỏ tôm vô lại nước luộc tôm nấu tiếp cho ra nước ngọt rồi lược bỏ vỏ đổ nước tôm chung với nước dùng gà. Nước dùng nấu sôi, nêm nếm nước mắm, muối, bột ngọt, đường, xíu mắm ruốc pha nước (nếu có), hành tím vài củ cho thơm.\r\n- Cho bột vào nước dùng, dùng của đảo nhẹ cho bột tơi ra,cho chả cá viên, tôm vô,nêm nếm cho vừa ăn, thêm dầu điều cho hấp dẫn.','300 g bột mì đa dụng\r\n2 lít nước dùng gà\r\n200 g tôm tươi\r\n100 g cá viên hoặc nấm rơm\r\nGia vị : nước mắm, muối, bột ngọt, đường, dầu điều hành tím..\r\nHành ngò',30,2,'Canh-Súp','Bún-Phở-Miến',40,'Việt Nam','Tết nguyên đán','Món chính','Gia đình',2,'2020-07-07 03:29:03','NULL'),(1193,1,'https://www.youtube.com/v/mMdoNbTpevA','Bánh khoai lang bột mì','Mình thích ăn bánh khoai lang chiên bột mì vào mùa mưa hoặc khi thời tiết se lạnh, hồi ở KTX, phòng 8 đứa mà chưa đầy 20 ngàn đã có món ngon lai rai chiều mưa.','B1.Khoai lang rửa sạch, cạo bỏ vỏ, cắt sợi.\r\nB2.Pha bột mì với nước, đường, dùng đũa khuấy mịn, sau đó cho phần khoai lang sợi và mè đen vào.\r\nB3.Bắt chảo lên bếp, cho dầu ăn vào, sau đó là bơ vàng, đợi bơ tan chảy và nóng lên thì lấy vá múc lượng khoai và bột mì vừa đủ cho vào, lưu ý bánh mỏng ăn sẽ giòn hơn. Lật đều bánh để không bị cháy và đợi bánh vàng thì gắp ra dĩa có lót giấy thấm dầu bên dưới.\r\nB4.Xếp bánh ra dĩa và thưởng thức.','100 gram bột mì\r\n2 củ khoai lang\r\n100 gram đường cát\r\n3 thìa mè đen\r\nBơ vàng',30,1,'Chiên-Rán','Trứng',45,'Việt Nam','Ngày lễ khác','Ăn vặt','Gia đình',17,'2020-07-07 03:29:03','NULL'),(1195,1,'https://www.youtube.com/v/oOn22mNcj9A','Bánh mì kẹp pho-mát giăm-bông (Sandwich jambon emmental)','Ngày trước nhìn thấy bánh mì là sợ, sợ nhai đến đau răng váng đầu vì bánh mì cứng và đặc hơn bánh mì ở nhà, thế mà bây giờ có khi ăn liền mấy cũng không sao,thậm chí thích mê mệt, chính là nhờ chiếc bánh mì kẹp pho-mát giăm-bông này!','B1.Bánh mì mới nướng xẻ đôi, lót nhân gồm rau xà lách, cà chua, tiếp đến một lớp giăm-bông, pho-mát, phủ nốt giăm-bông còn lại lên trên.                                                                    B2.Gói lại, bỏ vào cặp, kèm thêm chai nước hoa quả: bữa trưa ngày đi học của mình đã sẵn sàng!','1/2 cây bánh mì baguette mới nướng\r\n3-4 lát pho-mát emmental\r\n2 lát giăm-bông\r\n3 lát cà chua\r\nrau xà lách\r\n1 thìa cà phê (tsp) mù tạt (nếu thích)',15,1,'Chiên-Rán','Thịt lợn',15,'Châu Âu','Ngày lễ khác','Ăn vặt','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1196,1,'https://www.youtube.com/v/i-xxrqh5HpQ','Bánh mỳ phomai Hạ Long','Món ngon ăn sáng','B1.Đánh đều bơ lạt và đường bột\r\nB2.Sau đó cho trứng gà vào hỗn hợp bơ đường\r\nB3.Rây bột vào hỗn hợp, cho thêm tangzhong vào (hỗn hợp này sẽ làm bánh mềm và dai hơn)\r\nB4.Làm nóng lò 15ph trước khi nướng. Nướng bánh ở 175 độ trong 20-25 phút','200 gr bột mù đa dụng\r\n80 gr Tangzhong\r\n30 gr đường bột\r\n4 gr men instant\r\n50 ml nước\r\n35 gr bơ lạt\r\n1 quả trứng gà',45,1,'Nướng','Trứng',30,'Việt Nam','Ngày lễ khác','Bánh','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1197,1,'https://www.youtube.com/v/xX_iXdyhavc','Bánh mì phô mai bơ tỏi','Món ăn sáng, ăn vặt','B1.Tỏi giã mịn + 50 gram bơ lạt cho vào lò vi sóng\r\nB2.Bánh mì cắt lát, cho bơ tỏi ít muối, tiêu, lá thơm, sữa đặc khuấy đều phết lên hai mặt lát bánh mì\r\nB3.Cho vào lò chiên không dầu, 200 độ, 6 phút ta có bánh mì bơ tỏi tuyệt vời','Bơ lạt, phô mai, tỏi, muối, tiêu, lá thơm, ít sữa đặc\r\nBánh mì',30,4,'Nướng','Trứng',20,'Châu Âu','Ngày lễ khác','Bánh','Đối tượng khác',1,'2020-07-07 03:29:03','NULL'),(1199,1,'https://www.youtube.com/v/4nqXoVZbmkQ','Bánh Mì hấp vụn dừa+mật ong','Món ăn sáng','B1.Trộn tất cả nguyên liệu lại với nhau trừ mật ong+vụn dừa ra,nhồi tới khi bột kéo màng tầm 20p.Sau đó ủ tới khi bột nở gấp đôi”60p”\r\nB2.Khi bột nở lấy ra đấm xẹp bột chia thành 6 viên đều nhau 95gr/viên.Thoa dầu ăn hoặc bơ vào khuôn bên dưới lót giấy nến bỏ bột đã vo tròn.\r\nB3.Quét lòng đỏ trứng gà+sữa tươi kg đường lên mặt bánh xong lấy màng bọc thực phẩm bọc lại,ủ tới khi bánh nở gấp đôi dùng tăm đâm cho thủng lỗ chỗ”để khi hấp bánh thoát được hơi ra ngoài giúp bánh kg bị ứ nước”.Nước sôi bỏ bánh vô hấp tầm 5p sau đó vặn lửa về mức thấp nhất hấp 45-50p là bánh chín,đợi bánh nguội lấy ra quét 1 lớp mật ong rồi rắc vụn dừa lên là xong.','300 gr bột mì số 13 hoặc bột số 11\r\n4 trứng gà\r\n60gr đường”đối với bé lớn”bé nhỏ 30gr,\r\n30ml dầu ăn\r\n1 lòng đỏ trứng gà\r\nxíu sữa tươi kg đường\r\n1 muỗng cafe vanilla\r\n3gr men ngọt\r\nmật ong',15,4,'Nướng','Trứng',30,'Việt Nam','Ngày lễ khác','Bánh','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1204,1,'https://www.youtube.com/v/VRFpzvHpfcY','Bánh mì xúc xích','Món ăn sáng','B1.Bỏ bột+men vào trộn đều,tiép đó cho đường vào trộn đều,bỏ 2 trứng gà+80ml sữa tươi có đường+muối+vanilla vào trộn đều”c trộn 20p là kéo màng rồi”.Sau khi trộn xong lấy ra quét dầu ăn vào cuộn cục bột lại thoa đều dầu ăn lên cho bột kg bị khô mặt trong time ủ,ủ bột 1h\r\nB2.lấy bột ra tạo hình theo ý thích”có link kèm bên dưới”mở nóng lò trước 10p nhiệt 180 độ,bỏ bánh vào rãnh thứ 2 từ dưới lên,nướng nhiệt 180 độ time 30p là bánh chín','300 gr bột số 13\r\n3 quả trứng gà\r\nxíu vanilla\r\n80ml sữa tươi có đường\r\n4gr men ngọt\r\n2 muỗng ăn cơm đường\r\nxúc xích, mè trắng\r\nxíu muối, hành lá xắt nhỏ, tiêu, bột ngọt, dầu ăn\r\ntương ớt hoặc tương cà',15,4,'Nướng','Trứng',40,'Châu Âu','Ngày lễ khác','Bánh','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1206,1,'https://www.youtube.com/v/75UGocd6gQU','Vỏ bánh mì hamburger','Món ăn kèm','B1.Đường quậy trong nước cho men vào quậy đều rồi cho vô bột.cho thêm vô bột muối sữa tươi nhồi đều nhồi mịn cho bơ vô nhồi mịn lần nữa ủ nở gaps đôi.                                             B2.Lấy ra tạo hình ủ lại trong lò nướng nở gấp đôi.                                                                                                                                                                                                              B3.Quậy trứng sữa quét lên rắc mè rồi đem nướng.bánh để nguội dùng làm hamburger kẹp thịt rau.','350 g bột mì ngang\r\n100 g nước\r\n6 g men nở\r\n10 g đường\r\n2 g muối\r\n150 g sữa tươi\r\n30 g bơ\r\n1 lòng đỏ trứng\r\n1 muỗng canh sữa tươi',60,4,'Nướng','Trứng',40,'Châu Âu','Ngày lễ khác','Bánh','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1208,1,'https://www.youtube.com/v/fYX1BcgWMqw','Cách Làm Cà Phê Bọt Biển Đang Hot Hit','Cà phê bọt biển Dalgona là một món “hot trend” được bao người mê mẩn mặc dù chỉ mới xuất hiện một thời gian ngắn.','Bước 1: Cho cà phê vào một tô to sau đó cho 2 thìa canh đường cùng 1 thìa canh nước sôi rồi khuấy tan cà phê và đường.\r\n\r\nBước 2: Dùng máy đánh trứng đánh đều hỗn hợp.\r\n\r\nBước 3: Sau khi đánh được một lúc bạn cho thêm 1 thìa canh nước sôi và tiếp tục đánh cho đến khi hỗn hợp cà phê bông lên và tạo thành chóp cứng.\r\n\r\nBước 4: Cho sữa tươi không đường vào ly rồi cho phần kem cà phê lên trên.','2 muỗng đường\r\n- 2 muỗng cà phê đen hòa tan (loại ko đường ko sữa)\r\n- 2 muỗng nước sôi\r\n- Sữa tươi uống kèm',15,1,'Có cách nấu khác','Trứng',20,'Châu Âu','Ngày lễ khác','Đồ uống','Đối tượng khác',1,'2020-07-07 03:29:03','NULL'),(1210,1,'https://www.youtube.com/v/nD2k199_HBM','Cách Làm Trân Châu Đường Đen Thui và Trà Sữa cực dễ','Không bỗng nhiên mà trào lưu trà sữa trân châu và sữa tươi trân châu đường đen lại nổi lên “rần rần” trong giới trẻ suốt thời gian qua. Món uống hấp dẫn này có “ma lực” gây “nghiện” bởi hương vị lôi cuốn và những hạt trân châu dẻo thơm đến khó cưỡng. Đặc biệt trong thời tiết nóng bức, đây vẫn là những món uống được giới trẻ vô cùng ưa chuộng. Cùng học ngay cách làm sữa tươi trân châu đường đen đơn giản tại nhà để “giải nóng” và tìm hiểu thêm các thông tin thú vị về món uống hấp dẫn này nhé!','Bước 1: Nấu nước đường đen\r\nCho khoảng 40g đường đen và bột cacao vào một lượng nước vừa đủ rồ đun sôi để tạo cho trân châu có màu hấp dẫn, hương vị thơm ngon hơn.\r\n\r\nBước 2: Làm trân châu\r\nCho bột năng và bột gạo trộn lẫn với nhau. Sau đó từ từ rót nước đường đen vào hỗn hợp bột và khuấy đều cho bột đủ sệt và dẻo để nặn trân châu.\r\nBạn rửa sạch tay rồi nhồi bột trực tiếp để bột không bị vón cục. Nên nhồi bột mạnh và đều tay cho đến khi dẻo, mịn là được. Sau đó phủ một lớp bột năng bên ngoài bột để tránh bột bị dính. Ngắt bột thành những cục nhỏ rồi vo viên có đường kính khoảng 0,5 cm.\r\nBắc một nồi nước, đun sôi, sau đó cho trân châu vào luộc, lúc luộc chú ý khuấy nhẹ tay để trân châu không bị dính vào đáy nồi. Khi thấy các viên trân châu nổi lên mặt nước thì vớt ra cho vào tô nước lạnh ngay để trân châu không bị dính. Tiếp đó vớt ra để ráo.\r\nTiếp theo, cho phần đường đen còn lại vào 150ml nước, đun trên lửa vừa cho đến khi sôi, để sôi khoảng 5 – 7 phút thì tắt bếp rồi cho trân châu vào ngâm khoảng 30 phút đến 1 tiếng cho thấm vị đường.\r\n\r\nBước 3: Trình bày\r\nMúc trân châu ra ly thủy tinh và rót sữa tươi vào, thêm đá viên vào là xong.','Sữa tươi không đường: 200ml\r\nBột gạo: 100g\r\nBột năng: 100g\r\nBột cacao: 5g\r\nĐường đen Hàn Quốc: 120g\r\nDụng cụ: nồi, rây lọc, ly thủy tinh, thau inox.',15,1,'Có cách nấu khác','Nguyên liệu khác',15,'Trung Quốc','Ngày lễ khác','Đồ uống','Đối tượng khác',3,'2020-07-07 03:29:03','NULL'),(1216,1,'https://www.youtube.com/v/eqD2yRvTpmw','Nha Đam Đường Phèn Lá dứa hạt giòn thơm hoàn toàn không bị đắng','Vốn là loại thực phẩm tốt cho sức khỏe và sắc đẹp nên nha đam (hay còn được gọi là cây lô hội) được sử dụng chế biến thành nhiều món ăn như sinh tố, sữa chua, chè… Trong đó, nha đam nấu đường phèn lá dứa chiếm được cảm tình nhiều người hơn cả bởi vị ngọt thanh của đường phèn cùng hương thơm dịu nhẹ lá dứa.','Bước 1: Sơ chế nha đam\r\n– Rửa sạch nha đam dưới vòi nước, gọt bỏ lớp gai ở 2 bên rồi cắt thành khúc ngắn.\r\n\r\n– Dùng dao gọt bỏ lớp vỏ bên ngoài, lưu ý cách nấu nha đam đường phèn không bị đắng là nên gọt sạch lớp vỏ xanh.\r\n\r\n– Cho nha đam vừa gọt vỏ vào ngâm trong âu nước muối pha loãng khoảng 5 phút.\r\n– Rửa nha đam lại vài lần với nước để sạch chất nhớt, vớt ra đợi ráo nước.\r\nBước 2: Sơ chế lá dứa\r\n– Rửa sạch lá dứa dưới vòi nước, cắt thành khúc ngắn.\r\n– Cuộn lá dứa và buộc chặt thành bó.\r\n\r\nBước 3: Cắt nhỏ nha đam\r\n– Dùng dao cắt nha đam thành hạt lựu nhỏ và đều nhau.\r\n– Cho nha đam vào trong bát, thêm 15ml nước cốt chanh vào cùng, ngâm khoảng 5 phút rồi vớt ra rửa sạch, để ráo\r\n\r\nBước 4: Chần nha đam với nước nóng\r\n– Bắc nồi nước lên bếp, khi nước sôi, cho nha đam vào chần sơ qua khoảng 30 giây, vớt ra.\r\n– Cho nha đam vào ngâm trong âu nước đá lạnh khoảng 5-10 phút. Đây là mẹo đơn giản trong cách nấu nước nha đam đường phèn giúp nha đam trắng và giòn hơn.\r\n\r\nBước 5: Nấu nha đam đường phèn\r\n– Bắc nồi với 2.5 lít nước lên bếp, cho đường phèn, lá dứa vào cùng, nấu với lửa lớn. Lưu ý: trong quá trình nấu, bạn nên dùng muỗng khuấy đều để đường tan hoàn toàn.\r\n– Khi đường phèn tan, cho toàn bộ nha đam vào nấu thêm vài phút, khuấy đều tay rồi tắt bếp. Bí quyết nấu nha đam đường phèn ngon là không nên nấu quá lâu sẽ làm cho nha đam mất độ giòn.\r\n\r\nBước 6: Hoàn thành và trình bày\r\n– Vớt lá dứa ra ngoài, đợi nồi nước nguội bớt.\r\n– Cho nha đam nấu đường phèn ra ly rồi thưởng thức.\r\n\r\n','2,5kg đến 3kg nha đam\r\n250g đường phèn là tương đương gần 1 chén đường phèn \r\n2 cây lá dứa \r\n1,5 thìa cafe muối\r\n2 lít nước tương đương 8 chén nước',30,2,'Có cách nấu khác','Nguyên liệu khác',10,'Việt Nam','Ngày lễ khác','Đồ uống','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1218,1,'https://www.youtube.com/v/-bAFzDvPis8','Cách nấu Chè Hạt Sen Nha Đam đường phèn cho ngày hè hết nóng','Nấu chè hạt sen nha đam có khó không? Không hề khó chút nào đâu nhé! Chỉ với một vài nguyên liệu dễ tìm cùng 30 phút vào bếp, việc nấu chè tưởng như khó khăn lại trở nên đơn giản hơn bao giờ hết với công thức dễ ghi nhớ và dễ làm dưới đây.','Bước 1:\r\n\r\nNha đam tươi mua về bỏ vỏ và chỉ giữ lại phần thịt trong. Phần này sẽ khá nhớt và rất đắng. Vì thế cắt đến đâu, bạn ngâm muối đến đó. Rửa thật sạch nha đam với muối trắng cho đến khi cầm lên không còn trơn là được. Khi đó, bạn nhẹ nhàng tháinha đam nhỏ với kích thước hạt lựu hoặc tương đương một đốt tay banh nhé.\r\n\r\nBước 2:\r\n\r\nHạt sen trước hết phải xoi tin sen. Tim sen là phần giống như mầm cây, màu xanh nằm ở giữa hạt. muốn lấy phần này ra rất đơn giản, bạn chỉ cần dùng cây tăm nhọn xuyên nhẹ qua lỗ ở giữa hạt sen sẽ thấy phần tim này trồi ra.\r\n\r\nRửa hạt sen qua nước muối và hầm mềm. Thông thường, bạn nên để lửa vừa đến khi nước sôi sẽ chỉnh thành lửa nhỏ cho đến khi hạt mềm.\r\n\r\nBước 3:\r\n\r\nTiếp theo đó, bạn đổ nha đam vào nồi cùng hạt sen đang hầm. Đảo qua vài lần, bạn cho thêm đường phèn vào, khoảng 3 phút nữa thì tắt bếp.\r\n\r\nNếu không có đường phèn, bạn hoàn toàn có thể dùng đường kính. Tuy nhiên, vì chè cần dùng nhiều đường nên đường phèn sẽ giảm độ gắt khi ăn, mang lại cảm giác dễ chịu và ăn không bị quá ngấy.','100gram hạt sen tươi;\r\n500gram nha đam;\r\n100gram đường phèn;\r\nMột chút muối trắng.',30,2,'Luộc-Hầm-Hấp','Rau đậu',15,'Việt Nam','Ngày lễ khác','Ăn vặt','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1219,1,'https://www.youtube.com/v/N7b7FVrwttA','Cách nấu TRÀ TẮC','Yummy sẽ bật mí cách pha trà tắc ngon tại nhà nhờ mẹo sau. Bạn sẽ thấy khác biệt so với những cốc trà tắc thông thường đấy! Cùng khám phá ngay cách pha trà tắc cùng với mật ong ngon tại nhà nào!','Bước 1: Ủ trà\r\nBạn cho 2 túi trà Lipton vào ấm trà, sau đó rót một ít nước sôi vào bình để rửa trà trước và bỏ nước đó đi. Tiếp đến, bạn cho nước sôi vào ủ trà, khoảng 100ml. Ủ trong khoảng 5 phút là được.\r\n\r\nBước 2: Làm siro tắc\r\nTắc: vắt lấy nước cốt, bỏ hạt.\r\nSau đó, bạn thêm vào nước cốt tắc là đường cát, mật ong, muối và khuấy đều hỗn hợp.\r\nĐịnh lượng trên dành cho 1 – 2 ly trà tắc. Nếu bạn làm số lượng nhiều thì tăng định lượng cho phù hợp nhé.\r\n\r\nBước 3: Pha chế trà tắc ngon\r\nTiếp đến, bạn dùng shaker để pha chế trà tắc. Bạn rót khoảng 60ml trà tắc vào shaker, thêm đá viên, hỗn hợp siro tắc ở bước 2 vào. Lắc đều hỗn hợp để trà được tạo bọt và nguyên liệu hoà quyện vào nhau. Cuối cùng, bạn rót trà tắc ra ly và cho thêm tắc cắt mỏng thả vào ly trà để kích vị tự nhiên.','Trà lipton túi lọc: 1 – 2 túi\r\nTắc: 3 quảMật ong: 10ml\r\nĐường cát: 30gr\r\nMuối, đá viên, nước sôi\r\nDụng cụ: shaker, muỗng, ly, ống hút…',15,2,'Có cách nấu khác','Nguyên liệu khác',15,'Việt Nam','Tết nguyên đán','Đồ uống','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1220,1,'https://www.youtube.com/v/e6XTUD9SKx4','Siro Chanh Dây (Chanh Leo) Giải Nhiệt Mùa Hè','Chanh dây (hay còn gọi là chanh leo) là loại quả thơm ngon và tốt cho sức khỏe, có công dụng làm đẹp da. Hiện tại đang là mùa chanh dây, chanh rất tươi ngon và giá cả hợp lý, cùng Yummyhọc cách làm siro chanh dây đơn giản và chuẩn vị để thưởng thức trong ngày hè nóng bức này nhé!\r\n','Bước 1: Chanh dây sau khi mua về rửa sạch, cắt làm đôi. Dùng thìa nạo sạch phần ruột của chanh dây rồi cho vào rây, tán mạnh vào lòng rây để nước cốt chanh chảy xuống tô.\r\n\r\nBước 2: Tiếp tục cho phần hạt trên rây vào tô khác, thêm 200ml nước lọc, khuấy đều để phần cơm còn lại của chanh dây tan đều, rồi tiếp tục tán, lọc qua rây để có nước cốt lần 2. Bạn lặp lại bước 2 với 100ml nước lọc để thu nước cốt lần 3. Vậy là với 1kg chanh dây ta có thể thu được 500ml nước cốt.\r\n\r\nBước 3: Cho đường cát đã chuẩn bị vào hỗn hợp nước cốt chanh dây, khuấy đều cho tan đường.\r\n\r\nBước 4: Cho hỗn hợp trên vào xoong, bắc lên bếp đun nhỏ lửa. Khi hỗn hợp sôi, hớt bọt để nước được trong. Tiếp tục đun cho tới khi nước cốt sánh lại thì tắt bếp, để nguội, cho hỗn hợp vào chai thủy tinh bảo quản trong ngăn mát tủ lạnh.\r\n','Chanh dây: 1kgĐường cát trắng: 400grRây lọc (Đặt mua rây lọc tại đây)Tô (bát) to, thìa\r\n',15,1,'Có cách nấu khác','Nguyên liệu khác',15,'Việt Nam','Ngày lễ khác','Đồ uống','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1226,1,'https://www.youtube.com/v/35EuE_ClhIs','Cách làm sinh tố chanh tuyết ngon tuyệt tại nhà cực đơn giản','Sinh tố đá chanh hay còn gọi là sinh tố chanh tuyết là vị thức uống ngon mát lạnh thơm nhẹ, mùi hương của chanh, có vị chua.Với công dụng thơm ngon, có nhiều vitamin mà còn bổ dưỡng. Là món thức uống tuyệt vời và cách làm đon giản. ','Bước 1: Lấy 1 quả chanh gọt bỏ ½ vỏ và lấy ½ vỏ còn lại, chanh cắt thành miếng nhỏ (kích thước 2cm), cho vào cối xay của máy xay sinh tố nhưng không được vượt quá vạch cao nhất của cối (nên sử dụng cối nhỏ, cối dùng để xay sinh tố). Nhiều người nghĩ khi cho vỏ chanh vào sẽ làm sinh tố bị đắng, nhưng thực ra, ngay cả cách xay sinh tố chanh tươi cũng nên cho vỏ chanh giúp cho món sinh tố thơm hơn và có thêm màu xanh mát mắt, hơn nữa, khi bạn chọn được Chanh tươi vỏ sẽ không đắng. \r\nBước 2: Quả chanh còn lại vắt lấy nước, bỏ hạt, cho vào cối kèm theo chút xíu muối. Thêm vào chút đường, đá, sữa tươi và sữa đặc đã chuẩn bị và xay cùng nước cốt chanh.\r\nĐể sinh tố Chanh tuyết đá được ngon bạn nên cho nhiều đá như kiểu pha siro đá bào, như thế sẽ làm món sinh tố mát lạnh và còn có màu sắc đẹp mắt. Sau khi công đoạn xay hoàn tất, rót ra ly thưởng thức. ','Chanh: 3 quả\r\n\r\nSữa tươi không đường: 400ml\r\n\r\nSữa đặc: 50ml\r\n\r\nMuối, đường, đá bào hoặc đá viên.',15,1,'Có cách nấu khác','Nguyên liệu khác',10,'Châu Âu','Ngày lễ khác','Đồ uống','Gia đình',1,'2020-07-07 03:29:03','NULL'),(1228,1,'https://www.youtube.com/v/RORXt1ajZkk','Cách làm nước ép dứa cà rốt đẹp da giữ dáng','Nước ép dứa cà rốt là thức uống thơm ngon bổ dưỡng và được dùng hàng ngày có tác dụng chăm sóc sức khỏe hiệu quả, góp phần làm đẹp da, giữ dáng lại vừa giải nhiệt ngày hè. Cách pha chế nước ép dứa cà rốt khá đơn giản, không tốn nhiều thời gian. ','- Dứa gọt vỏ, bỏ mắt, thái miếng và bỏ lõi.\r\n\r\n- Cho dứa và cà rốt vào một máy xay sinh tố, xay nhuyễn\r\n\r\n- Lọc hỗn hợp dứa – cà rốt đã xay qua một rây lưới, lấy nước.\r\n\r\n- Gừng giã nhuyễn, lấy nước. Thêm nước gừng vào nước ép dứa – cà rốt một lượng sao cho phù hợp với khẩu vị của bạn.\r\n\r\n- Khi thưởng thức, bạn cho thêm vài viên đá để tăng thêm sự hấp dẫn và tươi mát của ly nước ép.','1 quả dứa\r\n- 50g gừng (tùy vào chất lượng của gừng mà bạn có thể cho nhiều hoặc ít hơn nhé)\r\n- 1 củ cà rốt vừa, xắt nhỏ.',15,1,'Có cách nấu khác','Nguyên liệu khác',10,'Châu Âu','Ngày lễ khác','Đồ uống','Người béo phì',1,'2020-07-07 03:29:03','NULL'),(1231,1,'https://www.youtube.com/v/5X-o1v-8iIc','Cách nấu sữa bắp đơn giản thơm ngon bổ dưỡng','Sữa ngô là một trong những thức uống được nhiều người yêu thích bởi vị béo ngậy thơm ngon. Hãy tham khảo cách làm sữa ngô ngon nhất dưới đây để thực hiện cho bé yêu và các thành viên trong gia đình thưởng thức.','Bước 1: Sơ chế nguyên liệu\r\n\r\n- Ngô mua về bóc tách hết phần lá và râu ngô ra khỏi bắp, lựa chọn phần lá non bên trong rửa sạch rồi bó lại.\r\n\r\n- Cho ngô và vỏ ngô đã sơ chế rửa sạch vào nồi đổ nước rồi bắc lên bếp để luộc trong 20-30 phút sao cho ngô chín và ra nước ngọt. Chú ý không nên luộc ngô quá lâu sẽ khiến ngô mềm khó tách hạt.\r\n\r\n- Vớt ngô ra rổ để nguội, phần nước luộc rất thơm có vị ngọt tự nhiên bạn chắt ra để riêng.\r\n\r\n- Bạn tách hạt ra khỏi lõi ngô theo 2 cách đó là dùng tay để tách từng hạt hoặc dùng dao bào lần lượt các lớp ngô đến khi chạm vào lõi.\r\n\r\nBước 2: Xay hạt ngô\r\n\r\n- Cho hạt đã bóc tách vào máy xay sinh tố với 1,5 lít nước luộc bắp hoặc nước luộc xay thật nhuyễn.\r\n\r\n- Đem hỗn hợp lọc qua rây để lấy nước và bỏ bã.\r\n\r\nBước 3: Nấu sữa ngô\r\n\r\n- Đổ hỗn hợp vừa lọc vào nồi cùng 200ml sữa tươi khuấy đều bắc lên bếp đun sôi. Trong quá trình đun vừa khuấy đều để sữa không bị cháy.\r\n\r\n- Khi sữa sôi bạn nêm đường cho vừa khẩu vị, khuấy thêm vài phút để đường tan hết sữa hơi sánh mịn thì tắt bếp để nguội.','Ngô ngọt: 2 bắp (nên chọn bắp to còn tươi)\r\n\r\n- Sữa tươi: 200ml\r\n\r\n- Đường cát trắng: 500g\r\n\r\n- Dụng cụ: máy xay sinh tố, rây lọc, nồi đun, tô lớn.',30,4,'Có cách nấu khác','Rau đậu',40,'Việt Nam','Ngày lễ khác','Đồ uống','Người gầy',1,'2020-07-07 03:29:03','NULL'),(1236,1,'https://www.youtube.com/v/KjUqRHkS3_M','Canh trứng cà chua','Món canh trứng cà chua cực kì dễ làm!!!','B1: trứng\r\nB2: đổ nước\r\nB3: ăn',' trứng\r\n đổ nước\r\n',15,1,'Canh/Súp','Trứng',15,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-06-28 21:19:52','NULL'),(1239,1,'https://www.youtube.com/v/IsZRZZOHNfo&feature=youtu.be','Gà om nấm hương','Gà om nấm hương- món ăn ngon dịp cuối tuần, bỏ một chút thời gian là đã có ngay món ăn hấp \ndẫn chiêu đãi gia đình, thịt gà thì dai nhưng mềm ngấm đều vị, nấm hương ngon, ngọt, thêm chút vị thơm, cay của ớt.Tất cả hòa quyện tạo nêm hương vị món ăn càng độc đáo.','\nGà om nấm hương- món ăn ngon dịp cuối tuần, bỏ một chút thời gian là đã có ngay món ăn hấp dẫn chiêu đãi gia đình, thịt gà thì dai nhưng mềm ngấm đều vị, nấm hương ngon, n\ngọt, thêm chút vị thơm, cay của ớt.Tất cả hòa quyện tạo nêm hương vị món ăn càng độc đáo.','Gà om nấm hương- món ăn ngon dịp cuối tuần, bỏ một chút thời gian là đã có ng\nay món ăn hấp dẫn chiêu đãi gia đình, thịt gà thì dai nhưng mềm ngấm đều vị, nấm hương ngon, ngọt, thêm chút vị thơm, cay của ớt.Tất cả hòa quyện tạo nêm hương vị món ăn càng độc \nđáo.',15,1,'Kho-Om','Thịt gia cầm',40,'Việt Nam','Tết nguyên đán','Món chính','Gia đình',1,'2020-07-01 04:04:16','NULL'),(1247,1,'https://www.youtube.com/v/Od36uUcUKs4&feature=youtu.be','Lẩu\n lươn chua cay','Lẩu Lươn Chua Cay là món ăn mang đặc trưng của miền sông nước. Vị nước dùng đậm đà, chua chua cay cay kết hợp với vị ngọt, dai của lươn hòa cùng\n vị thanh mát của các loại rau ăn kèm. Vừa thơm ngon lại bổ dưỡng, ai ăn một lần cũng nhớ mãi. Hãy chuẩn bị nguyên liệu và thực hiện theo cách bước sau nhé!','Lẩu Lư\n ơn Chua Cay là món ăn mang đặc trưng của miền sông nước. Vị nước dùng đậm đà, chua chua cay cay kết hợp với vị ngọt, dai của lươn hòa cùng vị thanh mát của các loại rau\n ăn kèm. Vừa thơm ngon lại bổ dưỡng, ai ăn một lần cũng nhớ mãi. Hãy chuẩn bị nguyên liệu và thực hiện theo cách bước sau nhé!','Lẩu Lươn Chua Cay là món ăn mang đặc \n trưng của miền sông nước. Vị nước dùng đậm đà, chua chua cay cay kết hợp với vị ngọt, dai của lươn hòa cùng vị thanh mát của các loại rau ăn kèm. Vừa thơm ngon lại b\n ổ dưỡng, ai ăn một lần cũng nhớ mãi. Hãy chuẩn bị nguyên liệu và thực hiện theo cách bước sau nhé!',15,1,'Canh-Súp','Hải sản',30,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',8,'2020-06-25 17:03:40','NULL'),(1249,1,'https://www.youtube.com/v/sFypgh2MnUQ','Miến gà Nam Định','Cách nấu Miến Gà thơm ngon hấp dẫn','B1. Hầm xương gà với 2.7L nước lọc trong 90 phút.\n r\nB2. Tim, trứng, đùi gà rửa sạch. Hấp trọng 30 phút\r\nB3. Dập hành, gừng. Sau đó nướng trên bếp than trong 3 phút. Bỏ hành, gừng đã nướng vào nồi nước hầm gà và tiếp tục đun.\r\nB\n 4. Luộc măng trong 5 phút rồi vớt ra để ráo. Thái nhỏ hành lá, rau răm, ngò rí, lá chanh và quất ra chuẩn bị sẵn.\r\nB5. Lúc này, gà hấp đã chín. Gắp ra và thái miếng vừa ăn. \r\nNêm nướ\n c dùng theo tỉ lệ muối 15g, hạt nêm 20g, bột ngọt 30g, đường phèn 30g, tiêu 1g, nước mắm 20g.\r\nHành tím băm nhỏ rồi phi thơm. Đổ hành vào nồi nước dùng.\r\nB6. Trần miến. Trình bà\n y và thưởng thức','Gia cầm\r\nGà ta 1con (1kg)\r\nGừng 1 củ\r\nHành tím 4-5 củ\r\nRau thơm (hành lá, ngò rí, rau răm, lá chanh, tắc)\r\nGia vị, hạt nêm\r\nMiến rong',60,4,'Canh-Súp','Thịt gia cầm',150,'Việt Nam','Mâm cỗ','Ăn sáng','Gia đình',1,'2020-07-06 18:12:44','NULL'),(1251,1,'https://www.youtube.com/v/0rcs6tCgpZI','Gà hấp xì dầu độc đáo','Mặc dù không phải là món ăn xa lạ nhưng gà hấp xì dầu vẫn luôn được nhiều người yêu t\n hích. Không chỉ có hương vị vô cùng tuyệt vời, hấp dẫn và gà hấp xì dầu còn mang tới nhiều công dụng rất tốt cho sức khỏe.','Bước 1: Sau khi ướp thịt gà 30 phút, cho phần thịt gà vào l\n ò vi sóng quay khoảng 3 phút hoặc cho vào lò nướng khoảng 5 phút. Điều này giúp phần da gà săn lại, khi ăn có độ dai dai của phần da gà và ngon hơn.\r\n\r\nBước 2: Cho phần gà\n trên vào nồi hấp, cho thêm vài lát gừng đã thái vào. Gà được hấp trong vòng khoảng 30 phút. Nếu các bạn làm khẩu phần cả con gà, thời gian hấp có thể kéo dài lâu hơn 10 – 15 phút.\n \r\n\r\nBước 3: Sau khi hấp 30 phút, thịt gà chín, phần da ngoài vẫn còn độ dai và không bị nhão. Trong khi đó, phần thịt bên trong vẫn mềm, đậm đà thơm của vị xì dầu cùng với tỏi\n băm.\r\n\r\nBước 4: Chặt gà, xếp ra dĩa trang trí và thưởng thức thôi nào!','Thịt gà tùy theo khẩu phần ăn của gia đình từ 1/2 con – 1 con gà. Công thức dưới đây dành cho khẩu p\n hần 1/2 con gà.\r\n1 ít gừng và 3 tép tỏi.\r\n3 muỗng canh xì dầu.\r\n30 ml rượu trắng.\r\n1/2 thìa cà phê muối.\r\n1/2 thìa cà phê bột ngọt.\r\n1/2 thìa cà phê đường.',15,3,'Luộc-H\n ầm/Hấp','Thịt gia cầm',99,'Trung Quốc','Ngày lễ khác','Món chính','Người gầy',1,'2020-07-06 18:12:44','NULL'),(1256,1,'https://www.youtube.com/v/vLBoSX8L4rY','Mỳ vịt tiềmmmmmmm','Mì \n vịt tiềm là món ăn ngon nổi tiếng của người Hoa và có giá trị dinh dưỡng cao, trong thịt vịt có vị ngọt, tính hàn, có tác dụng dưỡng vị, bổ thận. Tuy món ăn này kết hợp rất nhiều nguyê\n n liệu nhưng lại dễ nấu vô cùng. Hãy cùng Yummy thực hiện và mang đến món ăn bồi bổ cho cả nhà thôi nào!','Bước 1: Sơ chế nguyên liệu:\r\nĐùi vịt góc tư sau khi mua về rửa thật sạch rồ\n i ngâm trong 350 ml nước, 150 ml rượu, 30 gr gừng để khử mùi hôi của vịt, giúp thịt vịt thơm ngon hơn, sau 10 phút bạn vớt vịt ra để ráo.\r\nThịt vịt ráo nước bạn ướp chung với 1 muỗng\n gia vị, ướp khoảng 10 - 15 phút.\r\nVịt sau khi ướp, bạn chuẩn bị sẵn chảo dầu nóng, cho vịt vào chiên ngập dầu (dùng khoảng 500 ml dầu ăn) cho đến khi vàng đều.\r\n\r\nBước 2: Nấu \n nước dùng (Nước lèo)\r\nChuẩn bị thêm 1 nồi khác bắc lên bếp, cho 2 lít nước hầm xương đã chuẩn bị với 1 muỗng canh nước tương, 1 muỗng canh muối, 50 gr đường phèn, 1 muỗng canh hạ\n t nêm, 1 muỗng canh hắc xì dầu, 15 gr hành tím nướng với 5 cánh hoa hồi, 5gr quế, 5 gr trần bì (đã rang thơm).\r\nNấu nước dùng sôi, để lửa nhỏ 15 phút rồi vớt hoa hồi, quế, trần bì\n .\r\n\r\nBước 3: Hầm thịt vịt trong nước dùng đã \r\nBạn cho 50 gr củ sen, 50 gr bạch quả, 30 gr táo tàu, 50 gr nấm hương vào nồi cùng với đùi vịt đã chiên vàng vào nấu chung với nước \n dùng trên bếp cho sôi lại 1 lần nữa rồi để lửa nhỏ ninh trong 1-2 tiếng nữa.\r\nSau khi ninh đủ thời gian, bạn tắt bếp là đã hoàn thành xong phần nước dùng cho món mì vịt tiềm đầy bổ\n dưỡng.\r\n\r\nBước 4: Sơ chế mì\r\nBạn luộc 2 vắt mì tươi với nước thật sôi khoảng 2 phút, thêm 2 muỗng cà phê dầu ăn vào nồi nước giúp mì không bị dính lại với nhau.\r\nBạn kiểm tra \n mì mềm hay chưa bằng cách vớt 1 sợi mì ra, dùng tay ngắt thử xem vừa ăn hay chưa, nếu chưa thì cho sôi thêm 1 - 2 phút nữa.','Mì vắt 2 vắt (mì tươi).\r\nĐùi vịt 1 cái (đùi góc tư)\n .\r\nRượu trắng 150 ml.\r\nGừng 30 gr.\r\nTỏi băm 1 muỗng cà phê.\r\nHạt nêm 2 muỗng canh.\r\nTiêu 1 muỗng cà phê.\r\nMuối 2 muỗng canh.\r\nDầu mè 1 muỗng canh.\r\nHắc xì dầu 2 muỗng\n canh.\r\nNước tương 1 muỗng canh.\r\nHoa hồi 5 cái.\r\nThanh quế 5 gr.\r\nTrần bì 5 gr.\r\nNước dùng 2 lít (1 kg xương heo bạn có thể hầm với 5 lít nước trong 1-2 tiếng còn lại 4 lít\n là vừa ngon).\r\nĐường phèn 50 gr.\r\nHành tím 15 gr.\r\nTáo tàu 30 gr.\r\nBạch quả 50 gr.\r\nCủ sen 50 gr.\r\nNấm hương 50 gr.\r\nDầu ăn 510 ml.',60,2,'Canh-Súp','Thịt gia cầm',110,'Trung Quốc','Mâm cỗ','Bánh','Gia đình',1,'2020-07-06 18:12:44','NULL'),(1258,1,'https://www.youtube.com/v/T1UEk7kBJ0s','Vịt khìa nước dừa','Những miếng thịt vịt chín mềm, đậm đà, thơm \n phức quyện cùng vị ngọt của nước dừa thực sự hấp dẫn bạn đặc biệt trong những ngày trời lạnh.\r\nKhìa là món ăn gần giống với kho nhưng thơm hơn và dậy mùi của nước cốt dừa.','Bước 1:\n Gừng cạo vỏ, rửa sạch, đập giập, bằm nhỏ. Hành tỏi bóc vỏ rửa sạch, đập dập, bằm nhỏ.\r\n\r\nBước 2: Thịt vịt làm sạch, dùng muối, gừng đã đập dập ở trên xoa đều xung quanh miếng thịt \n vịt. Thêm chút rượu trắng vào cùng, bóp kỹ, sau đó rửa thật sạch cho hết mùi hôi. Tiếp theo lọc bỏ bớt xương, thái thịt thành 2-3 miếng lớn, dùng dao khía nhẹ xung quanh để khi ướp thịt\n được ngấm gia vị hơn\r\n\r\nBước 3: Ướp thịt vịt với ngũ vị hương, hành, tỏi đã bằm nhỏ ở trên, thêm chút bột nêm, tiêu, nước tương trộn đều để khoảng 1h cho thịt ngấm gia vị..\n \r\n\r\nBước 4: Bắc chảo lên bếp, cho dầu ăn vào chảo. Khi dầu ăn nóng già cho thịt vịt vào chiên vàng 2 mặt. Chú ý gạt bỏ lại hành tỏi để khi chiên không bị cháy. \r\n\r\nBước 5:\n Khi vịt chín vàng 2 mặt, gạn hết dầu ăn trong chảo ra ngoài, thêm nước dừa tươi cùng chút bột nêm vào, om nhỏ lửa đến khi vịt chín mềm và phần nước sền sệt.\r\nTắt bếp, lấy thịt vịt \n khìa nước dừa ra thái miếng vừa ăn, xếp ra đĩa, rưới chút nước sốt lên trên cho thịt thêm đậm đà.','\r\n1,5kg đến 2kg vịt\r\n1 quả dừa to\r\n6 thìa canh rượu gừng (6 tbsp)\r\n1 thìa \n canh muối (1tbsp)\r\nƯớp Thịt\r\n1/2 thìa cafe tiêu (1/2tsp)\r\n2/3 thìa cafe muối (2/3tsp)\r\n1 thìa cafe hạt nêm (1tsp)\r\n1,5 thìa cafe bột ngọt (1,5tsp)\r\n4 thìa cafe đường (4tsp)\n \r\n3 thìa cafe nước mắm (3tsp)\r\n6 thìa cafe dầu màu điều (6tsp)\r\n3 đến 6 thìa cafe rượu ngũ vi hương (3tsp) không có thay thế bằng 1/2 thìa cafe bột ngũ vị hương\r\nmột ít hành tỏi\n \r\n',15,3,'Luộc-Hầm-Hấp','Thịt gia cầm',89,'Việt Nam','Tết nguyên đán','Món chính','Gia đình',2,'2020-07-06 18:12:44','NULL'),(1260,1,'https://www.youtube.com/v/7Hj8j4sEyJg','Vịt kho gừngggggggg','Vịt kho gừng đậm đà lại nóng ấm, sẽ thích hợp để ăn cùng cơm mùa lạnh.','Bước 1: Thịt vịt xát muối ngoài da rồi rửa thật sạch, để ráo nước. Sau đó, chặt thịt vịt thành những miếng vừa ăn\n , ướp cùng chút gia vị và gừng băm nhỏ.\r\n \r\n \r\nBước 2: Gừng, sả rửa sạch thái lát hoặc thái sợi.\r\n \r\n Bước 3: Đặt chảo lên bếp cho chút dầu ăn để chảo nóng rồi cho gừng, sả vào\n đảo đều khoảng 2 phút thì cho thịt vịt đã tẩm ướp vào.\r\n Nấu tới khi thịt vịt săn lại thì múc ra đĩa. Vịt kho gừng ăn cùng cơm nóng rất tuyệt!\r\n ','Thịt vịt: 1kg\r\n- Gừng: 1 củ vừa\r\n \n- Sả: 3 nhánh\r\n- Gia vị vừa đủ',15,2,'Kho-Om','Thịt gia cầm',66,'Việt Nam','Ngày lễ khác','Ăn sáng','Người gầy',1,'2020-07-06 18:12:44','NULL'),(1269,1,'https://www.youtube.com/v/2qML8Lz3OHM','Cánh gà kho gừng','Cánh gà kho gừng thơm ngon vàng ruộm đưa cơm khiến cả nhà đều mê.','Bước 1: Cánh gà sơ chế sạch sau đó ta chần qua nước sôi (để khi nấu sẽ không bị bọt), gừng rửa\n sạch và đập dập.\r\nBước 2: Cánh gà sau đó chặt đôi thành miếng vừa, cho vào âu to và ướp gia vị; mắm, đường và gừng trong 15-20 phút.\r\nBước 3: Sau khi ướp gà xong, cho vào nồi \n đun đến khi sôi. Cho thêm 1 bát nhỏ nước nóng vào và kho 30-40 phút nhỏ lửa đến khi gà chín thơm vàng là được. Tắt bếp và cho 1 thìa mì chính nếu muốn.','- 500gr cánh gà\r\n- 1 \n củ gừng, 3-4 thìa nhỏ nước mắm, 1 thìa cà phê đường, 1 thìa cà phê mì chính, 1 bát nhỏ nước sôi.',45,4,'Kho-Om','Thịt gia cầm',45,'Việt Nam','Ngày lễ khác','Khai vị','Người tập\n GYM',1,'2020-07-06 18:12:44','NULL'),(1284,1,'https://www.youtube.com/v/SPmfCxmjtQ4','Đậu phụ kho trứng','Đậu phụ kho trứng đậm đà, ấm áp','Bước 1: Đậu phụ cắt thành miếng vừa ăn.\n \r\nBước 2: Đun nóng dầu ăn trong chảo, sau đó cho đậu vào chiên vàng hai mặt.\r\nBước 3: Đun sôi một nồi nước, sau đó cho các nguyên liệu làm gia vị vào, trừ đậu và trứng.\r\nBước 4:\n Sau khi đun sôi, thêm trứng và đậu vào, nấu trong 20-30 phút là tắt bếp.\r\nSau đó, cho đậu phụ kho trứng ra đĩa, bổ đôi trứng ra rồi thưởng thức với cơm trắng nhé!\r\n\r\n','- \n 1 miếng đậu phụ to\r\n\r\n- 2 quả trứng gà luộc, bóc vỏ; 20g hành lá; 20g gừng; 20g hạt tiêu đen; 2 mảnh quế; 3 bông hoa hồi; 15ml xì dầu; 750ml nước; 2 lá nguyệt quế; 50g đường, \n muối vừa ăn',30,2,'Kho-Om','Thịt gia cầm',35,'Việt Nam','Ngày lễ khác','Ăn sáng','Người tập GYM',1,'2020-07-06 18:12:44','NULL'),(1286,1,'https://www.youtube.com/v/yQB_xKBmNWQ','Xôi chim \n bồ câu','Cách nấu xôi chim ngon dẻo, bổ dưỡng và đẹp mắt mà đơn giản. Xôi chim xuất phát là một món ăn nhà quê dân dã, nay đã trở thành một món ăn được nhiều người ưa thích bởi vị\n ngon cũng như giá trị dinh dưỡng mà nó mang lại. ','Bước 1: Chim bồ câu làm sạch. Bỏ đầu, bỏ xương cẳng chân rồi băm nhuyễn cùng nấm hương.\r\n\r\nBước 2: Ướp thịt đã băm với chút\n dầu hào, nước mắm, tiêu khoảng 15 phút. Sau đó, xào chín thịt chim\r\n\r\nBước 3: Đồ xôi gần được (khoảng 20p) thì cho 1 chút thịt chim vào trộn đều với xôi rồi đồ tiếp khoảng 10p \n đến khi chín hẳn thì xới ra đĩa. Rắc chỗ thịt chim còn lại lên trên.\r\n\r\nBước 4: Thái hành khô để chuẩn bị phi.\r\nĐồ dầu vào chảo (ước lượng dầu ngập hành) đợi dầu thật nóng rồi\n thả hành vào phi đến khi vàng nhạt thì tắt bếp, vớt hành ra rắc lên đĩa xôi chim.','1 con chim bồ câu khoảng 4 lạng\r\n500 g gạo nếp\r\nNấm hương\r\nHành tím',30,4,'Luộc-Hầm-Hấp','Thịt gia cầm',95,'Trung Quốc','Mâm cỗ','Khai vị','Người gầy',1,'2020-07-06 18:12:44','NULL'),(1295,1,'https://www.youtube.com/v/0XUasOt5kvI&feature=youtu.be','Thịt nạc kho \n tiêu','Thịt Kho Tiêu là món ăn ngon phổ biến trong mâm cơm của người Việt. Cách nấu thịt kho tiêu cay thơm, ngấm vị mặn ngọt đậm đà thì bạn cần thái nhỏ thịt heo, ướp đầy đủ các gia\n vị trong thời gian đủ dài. Với cách làm thịt kho tiêu này, bữa cơm gia đình sẽ thêm hấp dẫn. Hãy lưu ngay cách kho thịt nạc hấp dẫn này cho bữa cơm gia đình nhé!','B1: Rửa sạch thịt\n heo, cắt nhỏ vừa ăn. Bóc vỏ hành tím, chẻ nhỏ múi cau. Cắt ớt thành những khoanh nhỏ. Ướp thịt heo với 2 muỗng canh nước mắm, 1/2 muỗng cà phê muối, 2 muỗng cà phê đường, 1 muỗng cà \n phê bột ngọt, 3 muỗng cà phê tỏi, ớt cắt nhỏ và 1.5 muỗng cà phê tiêu trong khoảng 15-20 phút.\r\nB2: Hòa tan 1 muỗng cà phê nước màu với 1 chén nhỏ nước ấm rồi cho vào tô thịt đã ướp, \n trộn đều lên sẽ làm thịt kho tiêu trông đẹp mắt và ngon miệng hơn.\r\nB3: Cách làm thịt kho tiêu: Phi thơm 1 muỗng cà phê tỏi băm với 3 muỗng canh dầu ăn rồi gắp thịt đã ướp cho vào\n nồi đảo đều tay.\r\nB4: Khi những miếng thịt kho săn lại thì thêm nước ướp thịt vào, mở lửa to. Đun đến khi nước gần cạn, sệt lại và bám lên thịt kho tiêu là hoàn tất.\r\nB5: Cho thịt \n kho tiêu ra đĩa và thưởng thức với cơm trắng rất ngon. Cách làm thịt kho tiêu với cơm nóng hay cơm nguội đều ngon hết sẩy. Để bữa cơm đầy đủ chất dinh dưỡng hơn thì bạn có thể chuẩn bị \n thêm dưa leo, rau sống, chấm cùng nước thịt kho rất tiện lợi và ngon miệng.','Thịt heo 500 gr\r\n Tỏi băm 4 muỗng cà phê\r\n Hành tím 6 củ\r\n Tiêu 1 1/2 muỗng cà phê\r\n Nước mắm 2 1/2\n muỗng canh\r\n Muối 1/2 muỗng cà phê\r\n Đường trắng 2 muỗng cà phê\r\n Bột ngọt 1 muỗng cà phê\r\n Nước màu 1 muỗng cà phê\r\n Dầu ăn 3 muỗng canh',30,4,'Kho-Om','Thịt lợn',80,'Nhật\n Bản','Ngày lễ khác','Món chính','Người gầy',1,'2020-07-06 18:12:44','NULL'),(1296,1,'https://www.youtube.com/v/851AKC0pftg&feature=youtu.be','Thịt viên sốt chua ngọt','Trong những ngày bận rộn\n không có nhiều thời gian nhưng bạn vẫn muốn tự tay chuẩn bị bữa cơm cho gia đình thì đừng bỏ qua món thịt viên sốt chua ngọt sau đây của Cooky nha. Món ăn làm rất nhanh và nguyên liệu \n đơn giản nhưng rất đưa cơm bởi từng viên thịt chiên vàng hấp dẫn, đậm vị sốt chua ngọt rất dễ ăn.\r\n\r\n','B1: Các bạn chọn mua phần thịt heo vai hoặc nạc dăm, rửa sạch thịt với muối \n và xay nhỏ thịt. Cho 300gr thịt heo xay vào tô, thêm 10gr tỏi băm, 10gr hành tím băm và 5gr gừng; nêm gia vị gồm 3gr muối, 10ml nước mắm, 20ml nước tương và 10gr bột năng. Dùng muỗng \n trộn đều và quết thịt để thịt được thấm gia vị. Sau đó vo tròn từng viên thịt.\r\nB2: Đun nóng ít dầu ăn trong chảo. Cho từng viên thịt vào chiên sơ đến khi thịt có màu vàng đẹp mắt.\n Không nên chiên quá lâu thịt sẽ bị khô. Thường xuyên trở mặt thịt để thịt vàng đều.\r\nB3: Thịt sau khi chiên xong, chắt bỏ phần dầu thừa trong chảo. Thêm gia vị gồm 30gr đường, 20ml\n giấm, 30gr tương cà và 20ml nước. Dùng đũa khuấy đều các gia vị với nhau, sau đó đảo và trở từng viên thịt vào phần sốt sao cho sốt bọc bao quanh. Rim thịt viên trong sốt ở lửa nhỏ \n khoảng 15 phút thì tắt bếp.\r\nB4: Món thịt viên sốt chua ngọt dùng nóng với cơm rất ngon, khi ăn rắc thêm hạt tiêu cho thơm. Món ăn có vị chua ngọt rất đưa cơm, thêm vị ngon béo của\n thịt viên chắc chắn các bé nhà bạn sẽ rất thích. Cách làm rất dễ và nguyên liệu đơn giản sẽ giúp bạn tiết kiệm thời gian rất nhiều đó. Đừng bỏ qua món cơm gia đình thơm ngon này nhé.\n ',' Thịt heo 300 gr\r\n(Thịt heo xay)\r\n Tỏi băm 10 gr\r\n Hành tím băm 10 gr\r\n Gừng băm 5 gr\r\n Muối 3 gr\r\n Nước mắm 10 ml\r\n Nước tương 20 ml\r\n Bột năng 10 gr\r\n Đường tr\n ắng 30 gr\r\n Giấm 20 ml\r\n Tương cà 30 gr\r\n Nước 20 ml',15,3,'Xào-Rang-Rim','Thịt lợn',60,'Châu Âu','Mâm cỗ','Món chính','Người gầy',1,'2020-07-06 18:12:44','NULL'),(1320,1,'https://www.youtube.com/v/pYS6ephyVDQ&feature=youtu.be','Chà bông heooooo','Không cần phải mua chà bông làm sẵn ở ngoài không đảm bảo vệ sinh thực phẩm, c\n hỉ với công thức và cách làm chà bông đơn giản là bạn có thể làm cho gia đình mình chà bông heo tươi ngon với nguồn thịt heo rõ nguồn gốc và khẩu vị thích hợp với gia đình mình.','B1:\n Cắt thịt theo thớ dọc rồi ướp 4 muỗng canh nước mắm và 1 muỗng canh bột ngọt vào để thịt ướp 2-3 tiếng cho thấm đều gia vị.\r\nB2: Thịt sau khi ướp xong bắt lên bếp cho thêm 50ml nước\n lọc rim đến khi khô cho thịt thấm hết gia vị. Dùng cối giã thịt cho mềm nhừ, sau đó dùng tay xé thịt thành sợi nhuyễn rồi bắt lên bếp để lửa thật nhỏ sấy cho chà bông thật khô ráo.\r\n nB3: Đảo đều tay khoảng 30 phút là hoàn thành chà bông, chà bông thành công là chà bông tơi bông và có màu vàng đẹp mắt. Bạn bảo quản chà bông trong hủ thủy tinh kín để dành ăn chung \n với cơm hay xôi, bánh mì đều ngon.',' Thịt heo 600 gr\r\n(thịt nạc vai)\r\n Nước mắm 4 muỗng canh\r\n Bột ngọt 1 muỗng canh',45,4,'Chiên-Rán','Thịt lợn',40,'Trung Quốc','Tết nguyên đán','Ăn vặt','Người béo phì',1,'2020-07-06 18:12:44','NULL'),(1362,1,'https://www.youtube.com/v/LYgWnujmQsA&feature=youtu.be','Thịt heo nướng mè đen','Món thịt nướng rất được nhiều người yêu \n thích vì hương vị thơm ngon. Công thức mình chia sẻ sau đây là một biến tấu mới – Thịt heo nướng mè đen. Từng miếng thịt được nướng thơm phức, với hạt mè đen làm dậy nên vị thơm ngon \n của món ăn. Hãy cùng tham khảo và tự làm để có thể chiêu đãi gia đình và người thân món thịt heo nướng mè đen tại nhà vào những ngày cuối tuần nha.','B1: Mè đen và mè trắng mua về rửa\n sạch, rang trên chảo đến khi mè khô ráo, hơi vàng và có mùi thơm. Nên cho mè trắng nhiều hơn mè đen thì khi nướng màu sẽ đẹp hơn.\r\nB2: Sốt ướp: Cho vào máy xay thực phẩm 50gr mè, 10\n gr gừng cắt lát, 20gr tỏi băm, 10gr hành phi, tiếp theo cho 1 muỗng cà phê hạt nêm, 1 muỗng cà phê tiêu, 2 muỗng canh nước tương, 3 muỗng canh dầu hào, 1 muỗng cà phê dầu mè, 40gr sữa\n đặc ( 1 hộp sữa Ông Thọ). Bấm nút xay đến khi các nguyên liệu hòa quyện với nhau, sốt ướp mịn mượt.\r\nB3: Thịt heo mua về rửa sạch, dùng khăn giấy ăn thấm khô, cắt thành từng miếng \n vừa ăn và cho vào dĩa lớn. Đổ sốt ướp vào 500gr thịt, dùng đũa trộn đều thịt vào sốt và ướp thịt trong thời gian 60 phút.\r\nB4: Chuẩn bị than và lò nướng, cho thịt lên vỉ nướng đến \n khi thịt chín và dậy mùi thơm nức. Khi nướng nên trở thịt nhiều lần để thịt không bị cháy.\r\nB5: Thịt heo nướng mè đen có mùi thơm thật sự rất hấp dẫn, vị bùi thơm của hạt mè càng l\n àm tăng hương vị cho món ăn, đặc biệt công thức sử dụng sữa đặc sẽ làm thịt mềm và vị béo ngọt rất đặc trưng. Món thịt nướng ăn kèm với cơm nóng, bún hay ăn vặt không cũng rất ngon, \n Cùng vào bếp làm ngay thôi nào!',' Thịt heo 500 gr\r\n(Thịt ba chỉ, thịt nạc vai)\r\n Mè trắng 30 gr\r\n Mè đen 20 gr\r\n Gừng 10 gr\r\n Tỏi băm 20 gr\r\n Hành phi 10 gr\r\n Hạt nêm \n 1 muỗng cà phê\r\n Tiêu 1 muỗng cà phê\r\n Nước tương 2 muỗng canh\r\n Dầu hào 3 muỗng canh\r\n Dầu mè 1 muỗng cà phê\r\n Sữa đặc 40 gr\r\n(1 Hộp sữa đặc Ông Thọ)',60,3,'Nướng','Thịt\n lợn',69,'Việt Nam','Mâm cỗ','Món chính','Gia đình',1,'2020-07-06 18:12:44','NULL'),(1375,1,'https://www.youtube.com/v/XrdkhJIvR34&feature=youtu.be','Chân giò hầm măng khô','Chân giò hầ\n m măng khô là món ăn ngon rất dân dã với sự kết hợp hoàn hảo của chân giò bổ dưỡng và măng khô. Từng miếng thịt được hầm mềm đậm vị, thêm miếng măng giòn ngon. Rất hấp dẫn phải không\n nào.','B1: Các bạn ngâm 200gr măng khô với 1 lít nước ấm trong khoảng 30 phút cho măng nở mềm. Sau đó luộc chín măng trong khoảng 15 phút, đến khi măng mềm. Tiếp theo rửa sạch măng v\n à xé nhỏ măng thành từng sợi.\r\nB2: Chân giò mua về rửa sạch, chặt thành từng khoanh tròn. Sau đó ướp 700gr chân giò với 15gr hành tím, 1 muỗng canh hạt nêm, 1/2 muỗng canh tiêu và \n 1 muỗng canh nước mắm. Để chân giò ngấm gia vị trong khoảng 30 phút.\r\nB3: Đun nóng 2 muỗng canh dầu ăn, phi thơm 15gr hành tím và cho chân giò đã ướp vào xào săn, thêm 500ml nước d\n ừa tươi vào nấu đến khi sôi thì cho tiếp măng khô xé sợi vào. Nêm gia vị gồm hạt nêm 1 muỗng canh, muối 1 muỗng canh, nước mắm 1 muỗng canh và 200ml nước. Hầm lửa nhỏ trong khoảng 60\n phút đến khi nước cạn và chân giò, măng đã chín mềm.\r\nB4: Chân giò hầm măng khô ăn nóng là ngon nhất, có thể ăn cùng cơm nóng hay bánh chưng bánh tét đều thích hợp. Khi ăn rắc thêm\n tiêu và hành lá cho thơm. Chỉ 3 bước đơn giản đã có ngay món ngon đãi cả nhà trong dịp Tết rồi đó.','Măng khô 200 gr\r\n Nước 1 1/4 lít\r\n Giò heo 700 gr\r\n Hành tím 30 gr\r\n Hạt \n nêm 2 muỗng canh\r\n Tiêu 1/2 muỗng canh\r\n Nước mắm 2 muỗng canh\r\n Dầu ăn 2 muỗng canh\r\n Nước dừa tươi 500 ml\r\n Muối 1 muỗng canh',45,2,'Luộc-Hầm-Hấp','Thịt lợn',90,'Việt Nam\n ','Mâm cỗ','Món chính','Gia đình',1,'2020-07-06 18:12:44','NULL'),(1381,1,'https://www.youtube.com/v/EMzMMuKJits&feature=youtu.be','Canh chua đậu hũ thịt bằm','Canh chua đậu hũ\n thịt bằm ăn thanh và ngon vô cùng luôn. Có thể thêm cá hoặc tôm nếu thích ạ!','B1: Sơ chế thực phẩm. Rửa sạch rau và cắt khúc vừa ăn\r\nB2: Me cho vào chén. Cho nước ấm vào, dùng muỗn\n g khuấy cho tan, vớt hột. Cho nồi lên bếp, đổ 250ml nước lọc, cho thịt heo xay vào, đun sôi, vớt bọt. Đổ me vào nồi. Cho thơm và cà chua vào trước. Nêm nếm gia vị.\r\nB3: Cho đậu bắp \n vào. Đun sôi 3 phút. Cho bạc hà và giá đỗ vào, dùng muỗng canh trộn đều cho chín.\r\nB4: Cắt ớt cho vào chén nước mắm. Múc canh ra tô ăn ngay cùng cơm nóng, bao ngon bao ghiền luôn. C\n húc cả nhà thành công nhé!','Đậu hũ chiên 2 miếng\r\n Thơm 1/4 trái\r\n Bạc hà 3 cây\r\n Cà chua 1 quả\r\n Đậu bắp 7 trái\r\n Ngò om 30 gr\r\n Ớt 3 quả\r\n Thịt heo bằm 50 gr\r\n Nước\n mắm 2 muỗng canh\r\n Hạt nêm 2 muỗng cà phê\r\n Tiêu 1 muỗng cà phê\r\n Đường trắng 1 muỗng cà phê',45,2,'Canh-Súp','Thịt lợn',40,'Việt Nam','Ngày lễ khác','Khai vị','Người tập GYM',1,'2020-07-06 18:12:44','NULL'),(1397,1,'https://www.youtube.com/v/WfXDsuSS9ps&feature=youtu.be','Mực dồn thịt kho tộ','Khi nói đến mực, mọi người thường nghĩ đến các cách chế biến phổ \n thông như mực xào, mực hấp, mực nướng, mực chiên. Bạn đã bao giờ nghĩ đến mực kho chưa? Hôm nay Hằng sẽ hướng dẫn các bạn món mực kho giúp các bạn đổi vị nhé!','B1: Mực làm sạch,\n chỉ tách phần đầu ra, giữ nguyên thân, lột bỏ da. Nấm mèo ngâm cho nở, rửa sạch, băm nhỏ. Hành, tỏi băm nhỏ.\r\nB2: Chuẩn bị 1 cái tô lớn cho thịt, nấm mèo vào cho hết phần hành,\n tỏi băm, gia vị đã chuẩn bị bên trên phần nguyên liệu vào đảo đều cho gia vị thấm đều nhé. để 15 phút rồi mình mới nhồi vào mực nhé. Sau khi ướp xong bạn nhồi hỗn hợp thịt vào thân \n mực (không nhồi quá đầy nhé, chút mực teo lại sẽ đẩy thịt tràn ra ngoài) rồi lấy tăm nhọn ghim đầu mực vào thân mực. Làm hết những con mực mà các bạn chuẩn bị.\r\nB3: Chuẩn bị 1 cái\n chảo cho vào 1 ít dầu, chiên sơ mực cho săn lại cũng như ra bớt nước. Chuẩn bị 1 cái chảo khác phi màu đường đến khi vàng nâu (nếu không làm được màu đường các bạn sử dụng loại màu\n kho bán sẵn nhé) cho mực vào đảo khoảng 30 giây là tắt bếp.\r\nB4: Chuẩn bị 1 cái nồi đất để kho mực : cho phần đầu hành lá xuống đáy nồi, cho mực vào, cho hết phần nước trong chảo\n mà lúc nãy ta đảo mực vào luôn, nêm thêm 2 muỗng canh nước mắm, 1/2 muỗng cà phê bột ngọt, 1 muỗng cà phê hạt nêm. Để lửa nhỏ, kho đến khi nào nước sền sệt, quẹo lại là được nhé.\r\nB5:\n Mực chín, các bạn cho thêm hành lá, ớt cắt lát và tiêu lên cho thơm nhé, Món này ăn kèm cơm nóng, dưa leo, cà chua là tuyệt vời nhất đó bạn. Chúc các bạn thành công, HANG CAN COOK.','Mực ống 7 con\r\n(To bằng 3 ngón tay)\r\n Nấm mèo 50 gr\r\n Thịt heo bằm 300 gr\r\n Tỏi 3 tép\r\n Hành tím 3 củ\r\n Hạt nêm 1 muỗng cà phê\r\n Muối 1/2 muỗng cà phê\r\n Đường trắng \n 1 muỗng cà phê\r\n Bột ngọt 1/2 muỗng cà phê\r\n Nước mắm 2 muỗng cà phê\r\n Tiêu 1 muỗng cà phê\r\n Hành lá 1 cây',15,1,'Luộc-Hầm-Hấp','Thịt lợn',90,'Trung Quốc','Mâm cỗ','Món chính','Gia đình',1,'2020-07-06 18:12:44','NULL'),(1400,1,'https://www.youtube.com/v/b5oVThCCwoE&feature=youtu.be','Cà chua nhồi thịt','Cà chua nhồi thịt là món ăn quen thuộc của nhiều gia đình\n vì vừa ngon vừa tốt cho sức khỏe. Hôm nay mình chia sẻ một cách làm khác, theo phong cách Âu, sử dụng rượu vang và lá gia vị làm tăng hương thơm và hấp dẫn của món ăn. Cùng theo dõi cách \n làm sau nhé.','B1: Hành tây lột vỏ, để nguyên củ không thái. Cho 2 muỗng canh dầu olive vào chảo rồi cho hành tây vào đun tới khi vàng cháy cạnh. Sau đó thêm 500gr thịt bằm vào chảo, \n dùng muỗng đảo nhanh và tách thành từng miếng nhỏ. Đun tới khi thịt cạn nước, thịt săn lại chuyển màu nâu nhạt.\r\nB2: Thêm 1 muỗng canh tỏi băm vào, rang tiếp trong khoảng 1 phút. \n Sau đó cho 75ml rượu vang trắng, đợi rượu bốc hơi hết thì thêm 400gr sốt cà chua vào. Đảo đều, nêm 1 muỗng cà phê muối, 1 muỗng cà phê tiêu. Để lửa nhỏ và nấu trong khoảng 5 phút.\r\nB3: \n Sau đó cho 200gr gạo và 150ml nước vào nấu trong khoảng 10 phút tới khi chín. Để nguội và rắc các loại lá đã cắt nhỏ lên trên gồm 10gr lá kinh giới, 10gr lá bạc hà, 10gr lá mùi tây.\r\nB4: \n Cà chua chọn mua những trái có kích cỡ tương tự nhau, trái to tròn, vỏ còn cứng, không chín đỏ quá. Rửa sạch cà chua và cắt bỏ phần ruột. Nhồi hỗn hợp thịt vào trong trái cà chua.\r\nB5: \n Khoai tây rửa sạch, gọt vỏ và cắt thành miếng vuông khoảng 2x2 cm, đổ vào nồi đa năng Elmich, thêm 1 muỗng cà phê muối và 1 muỗng cà phê tiêu, 2 muỗng canh dầu ăn rồi xóc nhẹ. Xếp cà chua \n lên trên lớp khoai tây sao cho cà chua không bị chạm vào thành nồi, rắc thêm 3 nhánh cỏ hương thảo và bật nút nướng 40 phút, tới khi khoai tây và cà chua chín.\r\nB6: Món cà chua nhồi thịt\n nướng nhìn rất ngon miệng và bắt mắt. Vị chua dịu cà chua kết hợp ngọt ngon từ thịt và hương thơm của lá gia vị làm tăng sự hấp dẫn. Ăn kèm sốt dưa leo Tzatziki rất ngon. Đặc biệt món\n ăn có gạo nấu chín bên trong rồi nên đây là một bữa ăn đơn giản nhưng đầy dinh dưỡng.','Cà chua 8 trái\r\n Thịt heo bằm 500 gr\r\n Gạo 200 gr\r\n Khoai tây 500 gr\r\n Hành tây 1 \n củ\r\n Rượu vang 75 ml\r\n(Rượu vang trắng)\r\n Sốt cà chua 400 gr\r\n Rau kinh giới 10 gr\r\n Lá bạc hà 10 gr\r\n Ngò tây 10 gr\r\n(Mùi tây)\r\n Lá hương thảo 3 nhánh\r\n Tỏi băm \n 1 muỗng cà phê\r\n Muối 2 muỗng cà phê\r\n Tiêu 2 muỗng cà phê\r\n Dầu olive 2 muỗng canh',30,2,'Luộc-Hầm-Hấp','Thịt lợn',60,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',1,'2020-07-06 18:12:44','NULL'),(1421,1,'https://www.youtube.com/v/LRl_Fb8qJ_M&feature=youtu.be','Thịt heo giả khô bò','Thịt heo giả khô bò với mùi thơm cuốn hút đến từ các loại gia vị,\n từng thớ thịt dai dai đậm vị rất dễ gây nghiện, khiến bạn cứ phải ăn hoài không dừng được. Hãy thử tập tành làm ngay món thịt kho cho cả nhà nhâm nhi nhé!','B1: 1kg thịt nạc heo\n lựa phần có thớ dài, cắt thịt thành những lát mỏng khoảng 1cm. 60gr sả chia làm 2, lấy 30gr phần gốc trắng băm nhuyễn, 30gr phần thân xanh cắt khúc 5cm và đập dập. Băm nhuyễn 50gr \n hành tím, 40gr tỏi, 30gr ớt, 20gr gừng. Đun một nồi nước sôi, cho thịt nạc heo vào trụng sơ 30 giây. Sau đó chắc bỏ phần nước dơ, giữ lại thịt trong nồi.\r\nB2: Bắc nồi lại lên bếp, \n chế nước vào sâm sấp mặt thịt, luộc thịt 5 phút để thịt chín. Gắp thịt ra để riêng ở dĩa, lọc nước luộc cho sạch. Thịt để hơi nguội rồi dùng chày hoặc búa dần cho thịt mềm và thớ thịt tơi ra.\n \r\nB3: Nước luộc thịt sau khi được lọc bỏ cặn bẩn và bọt thì cho vào 1 gói Bò Kho - xốt gia vị hoàn chỉnh Barona vào cùng với các nguyên liệu: sả, tỏi, hành tím, gừng, ớt băm, 55gr đường \n trắng, 15gr bột ngọt, 15ml nước mắm, 10ml dầu điều, 7gr ngũ vị hương, 1gr bột nghệ. Khuấy đều cho gia vị tan. \r\nB4: Cho thịt heo đã đập vào nồi nước gia vị, đun lửa vừa và thường xuyên \n trở mặt thịt để tất cả đều được ngấm gia vị. Sau khi nước gia vị cạn gần hết, bạn gắp thịt ra xếp vào khay nướng đã được lót giấy chống dính, cho vào lò nướng đem sấy 2 lửa ở 120 độ trong\n vòng 30-45 phút hoặc đến khi thịt khô lại và chuyển màu nâu đỏ giống khô bò.\r\nB5: Nếu bạn là \"fan ruột\" của món \"khô bò vắt chanh\" mà ngại giá thành đắt đỏ thì món thịt heo giả\n khô bò này chính là giải pháp, vừa an toàn vừa ngon mà chất lượng và hình thức thì lại rất giống khô bò. Thịt heo giả khô bò vừa dùng để ăn chơi, để tiếp đãi bạn bè hoặc làm mồi nhậu\n đều được, một giải pháp quá kinh tế đúng không nào.','Thịt heo 1 kg\r\n Xốt bò kho Barona 1 gói\r\n(Xốt gia vị hoàn chỉnh Barona)\r\n Sả 60 gr\r\n Tỏi 40 gr\r\n Hành tím 50 gr\r\n Gừng\n 20 gr\r\n Ớt 30 gr\r\n Đường trắng 55 gr\r\n Bột ngọt 15 gr\r\n Nước mắm 15 ml\r\n Dầu điều 10 ml\r\n Bột nghệ 1 gr\r\n Bột ngũ vị hương 7 gr',30,3,'Chiên-Rán','Thịt lợn',80,'Việt Nam','Ngày lễ khác','Ăn vặt','Gia đình',1,'2020-07-06 18:12:44','NULL'),(1423,1,'https://www.youtube.com/v/cCoS2ylXlsY&feature=youtu.be','Bò xốt vangggggg','Cách nấu thịt Bò sốt vang với những\n miếng thịt bò nạm có xen kẻ gân với thịt được nấu chín mềm thơm, quyện cùng nước xốt vang vị chua và chát nhẹ giúp tạo hương vị đặc trưng của món ăn. Dùng chấm bánh mì là hết sẩy.','B1: 200gr cà rốt, 100gr hành tây cắt khúc. 30gr gừng cắt lát, đập dập. 30gr tỏi và 30gr hành tím băm nhỏ. 400gr thịt bò nạm cắt miếng vừa ăn. Ướp thịt bò với 1 gói Bò kho - \n Xốt gia vị hoàn chỉnh Barona và gừng đập dập trong 30 phút cho thịt bò ngấm gia vị.\r\nB2: Bắc nồi, làm nóng 2 muỗng canh dầu ăn, cho hành tím và tỏi vào phi vàng thơm. Tiếp tục \n cho thịt bò đã ướp vào xào đều tay, đến khi thịt bò săn lại thì chế vào nồi 1 lít nước. Đầu tiên đun lửa lớn cho nước hầm sôi lên, vớt bỏ bọt cặn đi rồi hạ nhỏ lửa hầm 1 tiếng \n cho thịt bò mềm.\r\nB3: Sau 1 tiếng, cho cà rốt vào nồi thịt bò, tiếp tục hầm lửa nhỏ thêm 40 phút hoặc đến khi cà rốt mềm. Tiếp theo cho hành tây cắt khúc, nấu thêm 5 phút nữa thì \n chế 40ml rượu vang đỏ vào, khuấy đều cho hòa quyện. Pha sẵn 20gr bột năng với 50ml nước, chế hỗn hợp bột vào nồi, khuấy đều để tạo độ sệt.\r\nB4: Thành phẩm là những miếng thịt bò\n thơm nức và ngọt mềm, không bị khô vì được hầm quá lâu do được nấu từ thịt bò nạm có xen kẻ gân với thịt. Nước xốt vang mang vị chua và chát nhẹ đặc trưng của rượu, tạo nhiều cung\n bậc hương vị khi ăn. Bò xốt vang dễ dàng kết hợp với bánh mì, cơm, bún tạo ra một bữa ăn ngon miệng và đầy đủ dưỡng chất.','Thịt nạm bò 400 gr\r\n Xốt gia vị 1 gói\r\n(Bò Kho - \n Xốt gia vị hoàn chỉnh Barona)\r\n Rượu vang 40 ml\r\n(Đỏ)\r\n Cà rốt 200 gr\r\n Hành tây 100 gr\r\n Tỏi 30 gr\r\n Hành tím 30 gr\r\n Gừng 30 gr\r\n Bột năng 20 gr\r\n Dầu ăn 2 muỗng\n canh\r\n',45,4,'Canh-Súp','Thịt bò',80,'Việt Nam','Ngày lễ khác','Món chính','Gia đình',2,'2020-07-06 18:12:44','NULL'),(1428,1,'https://www.youtube.com/v/TSrUbQBqUIc&feature=youtu.be','Salad bò sốt dầu giấm','Salad thịt bò trộn dầu giấm là một món ăn thơm ngon, bổ dưỡng và thích hợp với mọi lứa tuổi. Mang phong cách phương Tây, món ăn có vị chua ngọt từ nước sốt\n hòa lẫn vào vị ngọt của thị bò và rau củ tươi mát sẽ khiến bữa ăn của cả nhà trở nên thú vị và cực kì hấp dẫn. Món ăn này thường được dùng vào những ngày nóng vì salad có tính lành,\n giúp hạ nhiệt và giải độc tố trong cơ thể rất tốt. Cùng khám phá công thức chi tiết của món salad bò sốt dầu giấm và thực hiện ngay thôi!','B1: Rửa sạch 200gr thịt thăn bò, cắt lát \n mỏng. Ướp thịt bò với ½ muỗng cà phê tiêu, 1 muỗng canh mật ong, 1 muỗng canh nước tương Maggi trong 15 phút cho thấm gia vị.\r\nB2: Làm nóng chảo với dầu ăn, phi thơm 10gr tỏi băm\n nhuyễn, sau đó cho thịt bò áp chảo nhanh hai mặt với lửa lớn.\r\nB3: Cắt sợi 100gr ớt chuông vàng, 100gr bắp cải tím và 50gr hành tây. Cắt nhỏ xà lách lô lô xanh và cà chua bi.\r\nB4:\n Pha sốt dầu giấm: Trộn đều 2 muỗng canh giấm, 2 muỗng canh mật ong, 1 muỗng canh dầu olive, ½ muỗng cà phê muối và ½ muỗng cà phê tiêu lại với nhau.\r\nB5: Cho tất cả các nguyên liệu \n đã chuẩn bị vào tô lớn, rưới sốt và trộn đều lên. Vậy là món salad đã hoàn thành.\r\nB6: Bày salad ra đĩa, rắc thêm mè trắng rang vàng lên mặt cho đẹp mắt. Salad bò sốt dầu giấm là \n một món ăn thơm ngon, bổ dưỡng và thích hợp với mọi lứa tuổi. Với cách làm đơn giản, màu sắc bắt mắt, món ăn này thường được dùng vào những ngày nóng vì salad có tính lành, giúp hạ \n nhiệt và giải độc tố trong cơ thể rất tốt. Với thịt bò thơm mềm, các loại rau quả tươi mát kết hợp cùng nước sốt chua ngọt, đây sẽ là món khai vị ngon miệng, tốt cho sức khoẻ gia đình\n bạn.','Thịt thăn bò 200 gr\r\n Nước tương Maggi 1 muỗng canh\r\n Mật ong 1 muỗng canh\r\n Tiêu xay 1/2 muỗng cà phê\r\n Bắp cải tím 100 gr\r\n Ớt chuông 100 gr\r\n(Vàng)\r\n Cà chua\n 100 gr\r\n Xà lách lolo xanh 100 gr\r\n Hành tây 50 gr\r\n Mè rang 10 gr\r\nSốt dầu giấm\r\n Giấm 2 muỗng canh\r\n Mật ong 2 muỗng canh\r\n Dầu olive 1 muỗng canh\r\n Muối 1/2 muỗng \n cà phê\r\n Tiêu 1/2 muỗng cà phê',30,2,'Nướng','Thịt bò',40,'Việt Nam','Mâm cỗ','Ăn vặt','Đối tượng khác',1,'2020-07-06 18:12:44','NULL'),(1455,1,'https://www.youtube.com/v/GYq-Zaky5Is&feature=youtu.be','Súp khoai tây thịt bò','Súp thịt bò khoai tây cho là món ngon \ncho bé, đặc biệt là các bé biếng ăn. Cách nấu súp thịt bò khoai tây đơn giản, với vị khoai tây bùi béo, mịn mượt và thịt bò ngọt tự nhiên,\n sẽ kích thích bé ăn ngon. Súp thịt bò cho bé vừa ngon vừa bổ dưỡng, mà ngoài các bé, các mẹ còn có thể làm súp bò khoai tây mời cả nhà ăn sáng ngon miệng nữa đấy!','B1: Gọt vỏ cà rốt và khoai tây. Lột vỏ hành tây. Cắt khúc khoai tây, đem hấp chín rồi tán (hoặc xay) nhuyễn.\r\nB2: Cắt cà rốt và hành tây thành hạt lựu. Phi thơm hành \n tây trong 2 muỗng cà phê dầu ăn rồi cho thịt bò vào xào.\r\nB3: Tiếp theo, đổ cà rốt vào xào chung. Chế 400ml nước lọc vào nồi, nấu tới khi cà rốt mềm (hớt bọt nổi lên trong quá\n trình nước sôi).\r\nB4: Cho khoai tây đã hấp chín vào, khuấy súp thịt bò khoai tây đều tay. Tắt bếp, nêm chút muối vào. Nếu người lớn ăn thì dùng hạt nêm.\r\nB5: Múc súp thịt bò khoai\n tây ra chén, thêm ngò rí xắt nhỏ và chút tiêu xay vào cho thơm. Súp thịt bò khoai tây thích hợp cho bé ăn vào bữa xế hoặc dùng cho bữa sáng của gia đình. Chúc các mẹ sẽ nấu thành công\n súp thịt bò khoai tây và bé ăn ngon miệng.','Khoai tây 150 gr\r\n Cà rốt 100 gr\r\n Thịt bò băm 100 gr\r\n Ngò rí 10 gr\r\n Hành tây 20 gr\r\n Muối 1/2 muỗng cà phê',15,2,'Canh-Súp','Thịt bò',40,'Việt Nam','Mâm cỗ','Món chính','Người gầy',1,'2020-07-06 18:12:44','NULL'),(1500,1,'https://www.youtube.com/v/stDilFRo-Cg','Bánh xèo kim chi hải sản ','Bánh xèo kim chi hải sản là món ăn phổ biến trong những bữa cơm gia đình Hàn Quốc. Sở dĩ bánh này được yêu thích như vậy cũng vì sự ngon miệng mà nó mang lại, vỏ bánh giòn rụm kết hợp với hải sản tươi ngon, xen kẻ là nấm kim châm dai dai và kim chi chua cay hấp dẫn. Món ăn thú vị này có thể làm món ăn kèm nhưng cũng có thể dùng ăn no được luôn đấy nhé. Cùng tìm hiểu một nét văn hóa Hàn Quốc với món Bánh xèo kim chi hải sản ngon mắt này nha.','\"B1: 300gr kim chi Bibigo gắp ra khỏi hộp có thể vắt cho ráo bớt nước rồi đem cắt nhỏ. Ướp kim chi cắt \r\nnhỏ với 1 muỗng canh đường rồi trộn lên cho đều. Để kim chi ướp 30 phút cho thấm.\r\nB2: Trong thời gian ướp kim chi ta sẽ chuẩn bị những thành phần khác. 80gr tôm tươi đã lột vỏ đem cắt \r\nthành những miếng nhỏ. Nghêu luộc chín với 1 ít sả, bóc lấy 80gr thịt nghêu (sử dụng khoảng 300gr \r\nnghêu nguyên vỏ). Cho tôm và nghêu vào chung với nhau rồi ướp với 1 muỗng canh dầu mè.\r\nB3: Cắt khúc ngắn 70gr nấm kim châm và 50gr hẹ.\r\nB4: Trộn chung 150gr bột mì đa dụng với 15gr bột bắp, 1 muỗng cà phê muối, 1/2 muỗng cà phê tiêu \r\nvà 300ml nước lọc, khuấy lên cho bột hòa tan với nước. Sau đó cho thêm kim chi, hẹ, nấm kim châm, \r\nthịt tôm và nghêu, 2 quả trứng gà vào tô, khuấy đều lên cho tất cả hòa trộn lại với nhau.\r\nB5: Bắc chảo lên bếp, làm nóng 2 muỗng canh dầu ăn, dùng vá múc từng vá bột bánh xèo vào chảo \r\nthành những miếng bánh tròn. Chiên bánh xèo cho vàng giòn 2 mặt. Làm lần lượt cho đến khi hết số bột.\r\nB6: Dọn bánh xèo ra đĩa, có thể chấm cùng với nước tương cũng rất ngon. Bánh xèo kim chi là một món \r\năn kèm rất phổ biến trong bữa cơm của những gia đình người Hàn. Bánh có được sự giòn rụm ở lớp vỏ, \r\ncó các loại hải sản tươi ngon cùng với kim chi chua chua cay the, vị tổng thể beo béo rất nịnh miệng. Nếu \r\nai là fan cứng của phim Hàn Quốc và trót phải lòng những chiếc bánh xèo thơm ngon này thì hãy xắn tay \r\náo vào bếp thực hiện ngay nhé!\"','Kimchi bibigo 300 gr\r\n Tôm tươi 80 gr\r\n Nghêu 80 gr\r\n Bột mì 150 gr\r\n(Bột mì đa dụng)\r\n Nấm kim châm 70 gr\r\n Hẹ 50 gr\r\n Bột bắp 15 gr\r\n Trứng gà 2 quả\r\n Đường trắng 1 muỗng canh\r\n Muối 1 muỗng cà phê\r\n Dầu mè 1 muỗng canh\r\n Tiêu 1/2 muỗng canh',45,4,'Chiên-Rán','Hải sản',70,'Các nước khác','Ngày lễ khác','Bánh','Đối tượng khác',11,'2020-07-07 00:18:05','NULL'),(1502,1,'https://www.youtube.com/v/kzvdyVK8Or8','CÁCH LÀM BÁNH TRUNG THU THẬP CẨM','Nếu bạn là một tín đồ của các loại bánh Trung thu ngon tuyệt cú mèo thì chắc chắn bạn không thể bỏ qua cách làm bánh Trung thu thập cẩm trong hướng dẫn dưới đây của chúng tôi','Làm Nước Đường Bánh Nướng\r\nBắc nồi lên bếp, cho đường nâu, đường trắng và nước vào nồi, dùng muỗng khuấy đều. Đợi cho hỗn hợp sôi lên rồi thêm 4 lát chanh vào là xong phần nước đường.\r\nLàm Nhân Bánh Trung Thu Thập Cẩm\r\nRang mè trắng, hạt dưa và hạt điều đến khi chín vàng thơm. Tiếp theo, cắt lạp xưởng thành hạt lựu. Áp chảo lạp xưởng, đảo đều để lạp xưởng không bị cháy. Sau đó lần lượt xay mứt gừng vàng, hạt dưa, hạt điều, mứt sen, mứt gừng đỏ và mứt bí cho nhuyễn ra. Cắt lá chanh và trần bì thành sợi nhỏ\r\nLàm Hỗn Hợp Gia Vị Nhân Và Bột Dầu\r\nTrộn đều các nguyên liệu làm gia vị nhân đã chuẩn bị là thành hỗn hợp gia vị nhân.\r\nĐổ dầu ăn vào bột bánh in, trộn đều lên làm hỗn hợp bột dầu\r\nHoàn Thành Nhân Bánh\r\nCho tất cả nguyên liệu đã chuẩn bị làm nhân bánh vào âu, dùng tay trộn. Tiếp theo, tách lòng đỏ trứng muối cho vào ngâm với rượu Mai Quế Lộ để khử bớt mùi tanh rồi mang đi hấp trong khoảng 12 phút là trứng chín.','-Đường nâu 300gr\r\n-Nước 200gr\r\n-Chanh 4 lát\r\n-Mè trằng: 150gr\r\n-Hạt điều: 150gr\r\n-Mứt gừng vàng 150 gr\r\n-Hạt dưa 150 gr\r\n-Lạp xưởng 150gr\r\n-Trứng muối 4 quả\r\n-Mứt bí, mứt sen, mứt gừng đỏ\r\n-Lá Chanh\r\n-Lòng đỏ trứng 3 quả\r\n-Dầu ăn\r\n-Bột mỳ\r\n- Dầu mè',30,4,'Nướng','Nguyên liệu khác',60,'Việt Nam','Trung thu','Bánh','Đối tượng khác',1,'2020-08-14 06:22:43','NULL'),(1503,1,'https://www.youtube.com/v/LI6OkwYKvPY','Bánh Sinh Nhật Tiramisu','Tiramisu vốn là loại bánh rất khó tạo hình, trang trí nhưng với cách làm dưới đây giới thiệu thì không có gì là khó cả! Cùng làm bánh Tiramisu thành bánh sinh nhật nhé!','1.\r\nTách riêng lòng đỏ và lòng trắng trứng gà, sau đó cho lòng đỏ trứng vào tô. Đánh tan lòng đỏ trứng với 10g đường rồi đặt lên đun cách thủy khoảng 3 phút.\r\n\r\n2.\r\nĐể mềm 100g phô mai rồi cho trứng ở trên vào trộn đến khi được hỗn hợp mịn mượt. Trong 1 tô khác cho kem whipping vào và đánh bông kem lên, sau đó cũng cho kem vào tô hỗn hợp trứng ở trên, trộn đều.\r\n\r\n3.\r\nXếp 1 lớp bánh vào đáy chiếc khuôn rời. Sau đó, quét đều hỗn hợp nước cà phê đen pha với rượu rum lên mặt.\r\n\r\n4.\r\nCho một nửa hỗn hợp kem phô mai vào. Sau đó, lại xếp tiếp 1 lớp bánh tẩm cà phê rượu khác và cho hết kem phô mai lên. Với lớp kem ở trên cùng, cần dùng dao, gạt cho phẳng.\r\n\r\n5.\r\nĐể trang trí, cắt giấy thành hình nào đó đẹp đẹp, dễ thương rồi đặt nhẹ lên bánh và rây bột cacao là xong!','Bánh bông lan 2 Miếng\r\nCà phê đen 40 ml\r\nRượu rum 15 ml\r\nTrứng gà 2 Quả\r\nĐường trắng 10 Gr\r\nPhô mai 100 Gr\r\nKem whipping 150 Gr\r\nBột cacao 15 Gr',60,4,'Có cách nấu khác','Nguyên liệu khác',55,'Các nước khác','Sinh nhật','Bánh','Đối tượng khác',1,'2020-08-14 16:15:06','NULL'),(1504,1,'https://www.youtube.com/v/gGZH6WDJoUY','Bánh Gato Bằng Nồi Cơm Điện','Nồi cơm điện không chỉ để nấu cơm mà còn có thể dùng để làm nhiều món khác nữa. Bánh gato giờ trở nên đơn giản hơn với nồi cơm điện đấy! Cùng học các làm dưới đây nhé!','1.\r\nNgâm 6 quả trứng gà vào tô nước ấm trong vòng 5 phút với tỉ lệ 2 cốc nước lạnh pha với 3 cốc nước sôi. Kế đến, trộn đều 120g bột mì, 20g bột bắp với 1 muỗng cà phê bột nở, rây mịn để bột không bị vón cục. Lau khô 6 quả trứng, đập trứng vào tô ráo nước, cho 120g đường rồi đánh cho trứng nổi bông lên. Kiểm tra bằng cách nhấc dụng cụ đánh trứng lên cao, nếu trứng rớt thành dòng là đạt.\r\n\r\n2.\r\nChia bột thành 3 phần để rây và lần lượt trộn vào tô trứng, trộn nhẹ tay để tránh làm trứng bị xẹp. Khi làm bánh bằng nồi cơm điện, bạn nhớ lót giấy nến vào đáy nồi cơm rồi mới đổ bột vào để bánh không bị dính, đập đập nhẹ cái nồi để bọt khí bay ra. Đặt nồi vào trong nồi cơm, đậy nắp lại rồi nhấn nút Cook. Sau khoảng 10 phút nồi sẽ chuyển sang nút giữ ấm (Warm), bạn đợi khoảng 5 đến 7 phút rồi bấm nút Cook thêm một lần nữa. Trường hợp nồi không nhảy sang nút Cook thì bạn đừng cố nhấn mà hãy đợi một chút cho nồi nguội bớt rồi mới nhấn nút lần nữa.\r\n\r\n3.\r\nKhoảng 60 phút sau, bạn mở nắp nồi cơm, kiểm tra bánh đã chín chưa bằng cách nhấn ngón tay vào bánh, nếu bánh gato không bị xẹp xuống là đã chín.\r\n\r\n4.\r\nLấy nồi cơm ra, lật nồi lại để lấy bánh ra ngoài, tháo giấy lót rồi úp bánh lại để trên vỉ nướng cho khô. Công đoạn làm bánh bằng nồi cơm điện đã hoàn thành rồi.\r\n\r\n5.\r\nVậy là đế bánh sinh nhật đã được hoàn thành, bạn có thể thưởng thức ngay hoặc có thể trang trí cho tiệc sinh nhật của người thân yêu!','Bột mì 120 Gr\r\nBột bắp 20 Gr\r\nĐường trắng 120 Gr\r\nBột nở (baking powder) 1 Muỗng cà phê\r\nTrứng gà 6 Quả',45,4,'Có cách nấu khác','Nguyên liệu khác',45,'Các nước khác','Sinh nhật','Bánh','Đối tượng khác',1,'2020-08-14 16:15:06','NULL'),(1505,1,'https://www.youtube.com/v/gd80XH1WB1k&feature=youtu.be','Bánh Trung Thu Nhân Trứng Muối Tan Chảy','Bánh trung thu lava trứng muối sẽ làm mới mâm cỗ ngày Tết Trung thu. Bánh trung thu trứng muối lava với vị độc đáo, bùi béo của nhân trứng muối tan chảy, tạo cảm giác ngon miệng, đổi vị cho bánh trung','1.\r\nĐể làm phần nhân của bánh trung thu lava, ngoài nhân trứng muối đang được làm đông ta chuẩn bị thêm 20 viên nhân đậu xanh dẻo, mỗi viên nặng 50gr (nhân đậu xanh có thể dễ dàng mua ở các cửa hàng bán dụng cụ nguyên liệu làm bánh hoặc những khu chợ lớn). Nhân trứng muối chảy sau khi làm đông, lấy ra khỏi khuôn. Dàn từng viên nhân đậu xanh ra thành miếng dẹt đủ bao bọc được nhân trứng muối, cho nhân trứng muối chảy vào rồi bọc lại thành những viên tròn.\r\n\r\n2.\r\nChuẩn bị phần nhân trứng muối chảy. Trứng muối hấp hoặc nướng chín, cho vào máy xay cùng với 60ml sữa cô đặc rồi xay mịn. Chuẩn bị một chiếc tô, cho 50gr sữa bột, 50gr bột custard, 120gr sữa đặc vào khuấy đều. Sau đó cho thêm 60ml sữa cô đặc còn lại cùng 60ml nước cốt dừa rồi khuấy đều. Lọc hỗn hợp quay rây để lấy được một hỗn hợp mịn mượt.\r\n\r\n3.\r\nTiếp theo cho trứng muối đã xay vào hỗn hợp, thêm 60gr bơ lạt đun chảy, khuấy dều cho mịn mượt. Đổ hỗn hợp ra khuôn silicon, cho vào tủ đông 4 tiếng hoặc qua đêm để nhân đông lại.\r\n\r\n\r\n4.\r\nVỏ bánh sau khi nghỉ đem chia thành những viên tròn nặng 40gr. Cán vỏ bánh thành những miếng mỏng tròn, cho viên nhân vào chính giữa rồi bọc vỏ bánh lại. Chuẩn bị sẵn khuôn bánh, phủ một lớp bột mì mỏng bên trong khuôn rồi cho viên bánh trung thu vào, nhấn mạnh để tạo thành nét rồi lấy bánh ra khỏi khuôn\r\n\r\n5.\r\nChuẩn bị phần vỏ bánh trung thu trứng muối lava: chuẩn bị một chiếc tô sạch, cho 300gr nước đường, 50ml dầu ăn, 1 lòng đỏ trứng gà vào tô rồi đánh tan đều. Tiếp theo thêm 500gr bột mì đa dụng vào tô rồi dùng trộn đều, sau đó nhồi sơ để tạo thành một khối bột mịn. Bọc kín bột rồi để bột nghỉ 1 tiếng.\r\n\r\n6.\r\nMở sẵn lò nướng ở 220 độ, 2 lửa. Xếp bánh vào khay nướng đã lót giấy nến chống dính, cho bánh vào lò nướng 10 phút. Lấy bánh ra khỏi lò, xịt ngay một lớp nước mỏng lên bánh rồi để bánh nguội bớt.\r\n\r\n7.\r\nTrong lúc đó chuẩn bị hỗn hợp phết bánh gồm 1 lòng đỏ trứng gà, 1/2 lòng trắng trứng gà, 1 muỗng cà phê sữa tươi không đường, 1 muỗng cà phê dầu mè và 1 muỗng cà phê nước đường bánh nướng. Phết lên bánh 1 lớp mỏng, sau đó cho vào lò nướng thêm 10 phút nữa ở 220 độ. Sau khi nướng lấy bánh ra khỏi lò, để nguội rồi cất nơi khô ráo thoáng mát, để yên 2 ngày cho bánh trung thu trứng muối mềm rồi thưởng thức.\r\n\r\n8.\r\nKhi ăn hâm lại bánh trung thu lava trứng muối trong lò vi sóng cho lớp nhân trứng muối tan chảy. Vỏ bánh nướng mềm và không bị khô bao bọc bên trong 2 lớp nhân đậu xanh và nhân trứng muối tan chảy vàng ươm beo béo cực hấp dẫn. Có công thức rồi, triển ngay cho mùa trung thu này thêm đặc biệt và ý nghĩa cả nhà nhé!','Bột mì500 Gr\r\nNước đường300 Gr\r\nDầu ăn50 Gr\r\nLòng đỏ trứng gà1 Cái\r\nTrứng vịt muối6 Quả\r\nSữa tươi120 ml\r\nSữa đặc120 Gr\r\nNước cốt dừa60 ml\r\nBơ lạt60 Gr\r\nBột Lion custard50 Gr\r\nSữa bột50 Gr\r\nLòng đỏ trứng gà1 Cái\r\nLòng trắng trứng gà1/2 Cá\r\niNước đường1 Muỗng cà phê\r\nDầu mè1 Muỗng cà phê\r\nSữa tươi không đường1 Muỗng cà phê',70,5,'Có cách nấu khác','Nguyên liệu khác',100,'Các nước khác','Trung thu','Bánh','Đối tượng khác',1,'2020-08-14 16:15:06','NULL'),(1506,1,'https://www.youtube.com/v/MI2x5YiKrbM&feature=youtu.be','Bánh Trung Thu Rau Câu Nhân Flan','Bánh trung thu luôn là món quà mà bạn nghĩ đến làm quà tặng cho người thân vào mùa Trung thu mỗi năm. Thay vì đi mua những chiếc bánh ở hàng quán thì tự tay mình làm mang tặng sẽ ý nghĩa nhường nào. C','1.\r\nLá dứa rửa sạch, cắt từng khúc ngắn vừa phải cho vào máy xay sinh tố. Sau đó thêm 150ml nước lạnh vào xay nhuyễn rồi lọc qua rây lấy nước.\r\n\r\n2.\r\nLá cẩm cũng rửa sạch, cắt ngắn cho gọn. Đổ vào nồi nước 200ml rồi cho lá cẩm vào nồi. Bật bếp nấu khoảng 15 phút để lá cẩm ra màu. Cũng tiến hành vớt bỏ xác lọc lấy nước để pha màu rau câu.\r\n\r\n3.\r\nCho một nồi khác lên bếp, thêm 2.5 lít nước lạnh vào, thêm 25gr bột rau câu giòn và 10gr bột rau câu dẻo vào nồi. Dùng muôi khuấy cho tan hoàn toàn rau câu mới tiến hành bật bếp nấu cho rau câu tan đều. Cho tiếp 250gr đường trắng vào, vừa cho vừa khuấy để đường tan đều.\r\n\r\n4.\r\nSau khi rau câu đã tan hoàn toàn đường thì tắt bếp. Lấy 3 tô múc nước rau câu ra từng tô để pha màu. Tô thứ nhất pha với 40gr sữa đặc, tô thứ 2 pha với nước lá dứa, tô thứ 3 pha với nước lá cẩm.\r\n\r\n5.\r\nSau khi đã hòa tan đều các loại màu, tiếp theo đổ màu lá cẩm vào khuôn đầu tiên khoảng 1/3 khuôn bánh trung thu, để vào ngăn mát tủ lạnh khoảng 5-10 phút để rau câu đông lại. Sau đó lấy khuôn rau câu ra, cho bánh flan vào rồi đổ lớp màu trắng của sữa đặc vào. Cũng để khoảng 5-10 phút để rau câu đông. Và lớp cuối cùng là lớp màu xanh lá dứa. Đưa khuôn vào tủ lạnh để khoảng 1-2 tiếng cho bánh đông hẳn.\r\n\r\n6.\r\nBánh trung thu rau câu nhân flan đã đông cứng, chỉ cần lấy ra và thưởng thức thôi nhé!','Lá dứa1 Bó\r\nLá cẩm1 Bó\r\nBột rau câu dẻo10 Gr\r\nBột rau câu giòn25 Gr\r\nĐường trắng250 Gr\r\nSữa đặc40 Gr',15,1,'Có cách nấu khác','Nguyên liệu khác',60,'Các nước khác','Trung thu','Bánh','Đối tượng khác',1,'2020-08-14 16:15:06','NULL'),(1507,1,'https://www.youtube.com/v/X8jXO9IGju4','Bánh Mousse Dâu Tây','Bạn có thể tự tay làm bánh mousse dâu tây đơn giản, không cần lò nướng dành tặng người thân vừa ý nghĩa lại ngon miệng. Làm quà sinh nhật thì sao ta?','1.\r\nDâu tây rửa sạch, cắt bỏ cuống, cắt lát vừa ăn. Trộn đường vào chén dâu tây, để khoảng 10 phút.\r\n\r\n2.\r\nCho dâu tây vào máy sinh tố xay nhuyễn với một muỗng canh sữa tươi.\r\n\r\n3.\r\nDùng dụng cụ lọc bỏ bã, giữ lại phần dâu tây đã xay nhuyễn. Múc ra chén riêng 3 muỗng canh dâu tây đã xay để dành làm phần thạch trên bề mặt, phần còn lại cho vào nồi, đun sôi nhẹ.\r\n\r\n4.\r\nGelatin bột cho vào chén nhỏ, thêm một ít nước ấm, hòa cho tan. Cho chén gelatin vào nồi dâu tây đã đun, dùng muôi khuấy nhẹ để gelatin tan hoàn toàn, nhấc nồi ra khỏi bếp, để nguội.\r\n\r\n5.\r\nKem sữa tươi cho vào thau sạch, dùng máy đánh trứng đánh bông nhẹ.\r\n\r\n6.\r\nNồi dâu tây đã trộn gelatin sau khi nguội thì trộn phần kem sữa tươi vào, dùng muôi trộn đều thành hỗn hợp đồng nhất.\r\n\r\n7.\r\nMiếng bánh bông lan xẻ làm 3 phần hay 2 phần tùy thích.\r\n\r\n8.\r\nXếp một bạt bánh vào đáy khuôn, xung quanh bạn lót một lớp nhựa mỏng để có thể dễ dàng lấy ra.\r\n\r\n9.\r\nĐổ một lớp mỏng phần mousse dâu lên bề mặt bánh, tiếp theo đó lót một lớp bánh bông lan, rồi đến một lớp mousse, làm tiếp tục cho hết phần bánh và phần mousse. Cho ổ bánh vào tủ lạnh, để 5-6 tiếng để bề mặt mousse đông cứng lại.\r\n\r\n10.\r\nPhần thạch trên bề mặt: dùng 3 muỗng canh để dành ở trên và hòa với 50-60 ml nước lọc cho vào nồi nhỏ đun sôi. Gelatin cho ra chén thêm vào một ít nước ấm, hòa cho tan nhẹ thì đổ gelatin vào nồi thạch đã đun, khuấy nhẹ tay để gelatin tan đều, để nguội.\r\n\r\n11.\r\nPhần thạch sau khi nguội thì để phần mousse dâu tây ở bước 10 đông cứng, nhẹ nhàng đổ lên bề mặt dâu tây, tiếp tục để vào tủ lạnh khoảng 3-4 tiếng đến khi phần thạch đông cứng hẳn.\r\n\r\n12.\r\nPhần bánh sau khi đông lấy ra khỏi khuôn, gỡ bỏ phần lớp nhựa lót xung quanh, trang trí tùy thích, và bánh sau khi trang trí phải được giữ lạnh trước khi đem ra dùng.','Bánh bông lan1 Miếng\r\nDâu tây300 Gr\r\nSữa tươi1 Muỗng canh\r\nKem sữa tươi200 ml\r\nBột gelatine9 Gr\r\nĐường trắng70 Gr',60,3,'Có cách nấu khác','Nguyên liệu khác',80,'Các nước khác','Sinh nhật','Bánh','Đối tượng khác',1,'2020-08-14 16:15:06','NULL'),(1508,1,'https://www.youtube.com/v/HhlrjVFRwj0&feature=youtu.be','Canh Miso Nhật Bản','Canh Miso là loại canh truyền thống của Nhật Bản bao gồm hai thành phần chính là nước dùng Dashi và tương Miso. Món canh này rất lành tính và mát nên thích hợp cho những ngày trời nóng bức.','1.\r\nCho vào nồi 500ml nước lọc, 2 muỗng cà phê bột Dashi rồi khuấy đều cho bột tan ra. Bật bếp nấu cho sôi lên là ta có nước dùng Dashi.\r\n\r\n2.\r\nCho thêm rong biển khô, đậu hũ non cắt nhỏ vào nấu đến khi rong biển nở to lên.\r\n\r\n3.\r\nTắt bếp, múc 1 muỗng canh tương Miso trắng vào chén, thêm nước canh vào rồi khuấy đều cho tương Miso hòa tan với nước. Sau đó đổ hỗn hợp vừa pha vào lại nồi, khuấy đều rồi bật bếp hâm lại cho canh nóng thì nhắc xuống (không cần sôi).\r\n\r\n4.\r\nMúc canh Miso ra tô, ăn kèm với cơm nóng là ngon nhất. Canh Miso rất lành tính và mát nên thích hợp cho những ngày trời nóng bức hay những ngày mưa chuyển mùa ẩm ướt.','Bột dashi2 Muỗng cà phê\r\nĐậu hũ non200 Gr\r\nRong biển khô3 Gr\r\nTương Miso1 Muỗng canh',15,1,'Có cách nấu khác','Nguyên liệu khác',70,'Nhật Bản','Ngày lễ khác','Bánh','Đối tượng khác',1,'2020-08-14 16:15:06','NULL');
INSERT INTO `tipnutri` VALUES (1,'Mẹo nhỏ khi nấu cháo','  Mẹo vặt để có được món cháo ngon là khi nấu cháo bạn nên ngâm gạo trước 1 tiếng sau đó đãi sạch, cho thêm một nắm nhỏ gạo nếp vào đun cùng. Khi nồi cháo sôi, cho vào nồi cháo một thìa dầu ăn nhằm mục đích tránh cháo bị trào ra ngoài đồng thời lại làm tăng hương vị của nồi cháo.','Mẹo hay','Dương Thị Uyên','Mẹo hay chế biến nguyên liệu','21.jpg',1,'2020-08-13 15:36:45.202'),(2,'Mẹo nhỏ khi luộc mì sợi',' Mì Ý là một trong các món ăn được nhiều người yêu thích, để có được một đĩa mì Ý ngon công đoạn luộc mì là vô cùng quan trọng. Khi luộc đợi nước bắt đầu nổi bọt lăn tăn thì bạn cho mì vào đảo qua đảo lại để mì chìm xuống hẳn nồi nước, và cho thêm 1 thìa dầu ô liu khuấy đều, đậy vung lại đến khi nước sôi thì cho thêm một ít nước lạnh, tiếp tục đun sôi trở lại rồi thôi, sau đó vớt mì ra rửa lại bằng nước lạnh.','Mẹo hay','Dương Thị Uyên','Bí quyết nấu nướng','22.jpg',1,'2020-08-12 17:59:10.017'),(3,'Mẹo nhỏ nêm muối đúng cách',' Không phải món ăn nào chúng ta cũng cho trực tiếp muối vào ngay từ khi nấu. Đối với các món ăn có các loại củ nên cho muối vào sớm hơn để muối ngấm đều vào củ còn đối với món rau luộc thì chỉ nên nêm muối trước khi bắc nồi xuống tránh cho việc các chất dinh dưỡng trong rau mất đi.','Mẹo hay','Dương Thị Uyên','Bí quyết nấu nướng','23.jpg',1,'2020-08-12 17:59:10.017'),(4,'Mẹo nhỏ cho xì dầu',' Trong xì dầu có chứa thành phần là đường nên nếu cho xì dầu sớm vào món ăn đường sẽ bị phân hủy ra mùi chua, đắng cho món ăn vì vậy mẹo vặt để có món ăn ngon là xì dầu chỉ nên cho vào món ăn trước khi nhắc nồi xuống.','Mẹo hay','Dương Thị Uyên','Bí quyết nấu nướng','24.jpg',1,'2020-08-12 17:59:10.017'),(5,'Dùng thêm nước khi làm món chiên xào',' Khi xào rau hay món ăn thường bị khô đồng thời hàm lượng nước dinh dưỡng có trong rau hay bị bay hơi mất vì thế để giữ lại độ dinh dưỡng cho rau, khi chảo nóng bạn nên cho thêm 2-3 thìa canh nước để hơi nước nóng có thể làm rau chín tới mà không tiêu hao quá nhiều lượng nước ngọt có trong thành phần của rau, củ.','Mẹo hay','Dương Thị Uyên','Bí quyết nấu nướng','27.jpg',1,'2020-08-12 17:59:10.017'),(6,'Mẹo nhỏ khi hấp cá','   Khi hấp cá nếu thoa đều một chút trứng gà lên cá, cá sẽ hấp thụ các chất dinh dưỡng trong trứng làm thịt cá mềm, thơm và bổ hơn.','Mẹo hay','Dương Thị Uyên','Bí quyết nấu nướng','ca-tuoi.jpg',1,'2020-08-12 17:59:10.017'),(7,'Cách khử mùi tanh của cá',' Cá mua về muốn khử sạch mùi tanh có thể dùng mẹo vặt là ngâm cá trong dung dịch 1 lít nước có pha thêm 3 thìa canh rượu trắng và gừng đập dập.','Mẹo hay','Dương Thị Uyên','Mẹo hay chế biến nguyên liệu','ca-tuoi.jpg',1,'2020-08-12 17:59:10.017'),(8,'Cách chiên rán không bị bắn mỡ',' Muốn món chiên rán không bị bắn mỡ trước khi cho thịt cá vào chiên thì bạn nên rắc thêm một chút bột mì vào chảo trước. Nếu muốn những món chiên rán có màu vàng đẹp mắt, đặc biệt là món nem, khi đun nóng dầu bạn vắt vào chảo dầu một vài giọt nước chanh.','Mẹo hay','Dương Thị Uyên','Bí quyết nấu nướng','27.jpg',1,'2020-08-12 17:59:10.017'),(9,'Thịt nạc trắng','  Các loại thịt trắng cung cấp một nguồn protein phong phú cho chế độ ăn uống của các võ sĩ quyền Anh. Những loại thịt này chứa ít calo hơn và hàm lượng chất béo bão hòa thấp hơn so với các loại thịt khác. Một con gà chứa khoảng 20-30g protein và axit amin thiết yếu, như leucine, đóng một vai trò quan trọng trong kích thích tăng trưởng cơ. Thịt nạc như thịt gà và gà tây chứa các khoáng chất thiết yếu, vitamin và các chất dinh dưỡng khác để ngăn ngừa tổn thương cơ.','Dinh dưỡng','Citigym','Dinh dưỡng cho người tập Gym','19.jpg',1,'2020-08-12 17:59:10.017'),(12,'Cá - Nguồn dinh dưỡng dồi dào',' Các loại cá, như cá hồi, cá ngừ, cá tuyết, cá rô phi… là nguồn cung cấp protein rất chất lượng. 100g cá hồi chứa khoảng 25g protein. Chế độ ăn của các võ sĩ quyền Anh nên bao gồm các loại dầu cá để đảm bảo cơ thể sẽ hấp thụ được các chất dinh dưỡng khác nhau bao gồm vitamin D, axit béo omega-3 và selenium.','Dinh dưỡng','Citigym','Dinh dưỡng cho người tập Gym','43.jpg',1,'2020-08-12 17:59:10.017'),(13,'Protein',' Khi bạn tập luyện, các cơ của bạn sẽ bị tổn thương và chúng cần chất dinh dưỡng để bù đắp và phát triển. Protein đóng vai trò là chất giúp xây dựng và sửa chữa lại các cơ bị tổn thương và kích thích các cơ mới phát triển. Ngoài ra, protein còn đóng vai trò như một chất xúc tác giúp các cơ của bạn hoạt động một cách dễ dàng hơn. Hàng ngày, bạn phải đảm bảo tiêu thụ từ 40 đến 60% protein. Những nguồn thực phẩm giàu protein nhất bao gồm: thịt bò, thịt gà, cá, trứng, các loại đậu…','Dinh dưỡng','thehinhnam','Dinh dưỡng cho người tập Gym','3.jpg',1,'2020-08-13 15:36:45.202'),(14,'Chất béo',' Sau protein là chất béo, các nghiên cứu về dinh dưỡng đã đưa ra các chỉ số thuyết phục về chế độ dinh dưỡng cho người tập thể hình. 20 đến 30% lượng chất béo hấp thụ vào cơ thể một ngày sẽ có rất nhiều lợi ích cho sự phát triển và phục hồi cơ bắp. Chúng tạo ra cơ chế hoạt động trơn tru, xây dựng các mô tế bào, các màng tế bào, giúp việc hấp thụ năng lượng protein diễn ra nhanh chóng và tốt hơn.','Dinh dưỡng','thehinhnam','Dinh dưỡng cho người tập Gym','31.jpg',1,'2020-08-13 15:36:45.202'),(15,'Nước tinh khiết',' Trong quá trình tập luyện thì tuyệt đối không thể thiếu nước. Ai cũng biết nước quan trọng nhưng chưa ai quan tâm nhiều đến nó. Khi bạn đạt được một kích thước cơ nhất định thì nước giúp cho việc duy trì cơ bắp diễn ra suôn sẻ hơn. Chỉ cần 8 ly nước trong 1 ngày đã đủ cho quá trình tập luyện. Có thể bạn sẽ nhàm chán quá trình uống nước tinh khiết vì vậy có thẻ thay thế bằng các loại thức uống khác. Hãy thử các loại nước ép, sinh tố để thay đổi khẩu vị.','Dinh dưỡng','thehinhnam','Dinh dưỡng cho người tập Gym','35.jpg',1,'2020-08-13 15:36:45.202'),(16,'Tăng lượng tinh bột',' Bạn cần ăn khoảng 2-3 gam tinh bột mỗi ngày trên mỗi 450g trọng lượng cơ thể. Nếu chất đạm là chất dinh dưỡng cần thiết nhất cho quá trình tăng trưởng cơ bắp thì đường bột là chất mang tính quyết định trọng yếu thứ hai. Tinh bột được lưu trữ ở cơ bắp dưới dạng glycogen, giúp cho các cơ bắp săn chắc, khỏe mạnh và cung cấp năng lượng trong suốt quá trình tập luyên. Để tăng cân, tăng cơ thì một người mới tập nặng 81kg cần 360-450g đường bột hàng ngày. Nên ưu tiên các chất tinh bột hấp thụ chậm như ngũ cốc nguyên hạt, yến mạch, khoai lang, các loại đậu, hoa quả, rau…','Dinh dưỡng','thehinhnam','Dinh dưỡng cho người tập Gym','38.jpg',1,'2020-08-13 15:36:45.202'),(17,'Trứng',' Đã có một thời gian dài người ta quy kết cho trứng là nguyên nhân khiến nhiều người mắc bệnh về gan, tim mạch và cao huyết áp. Trong khoảng 10 năm gần đây, nhiều nghiên cứu khoa học đã chứng minh cholesterol trong lòng đỏ trứng không hại như nhiều người vẫn nghĩ. Người bình thường ăn trung bình khoảng 2 quả/ngày (14 quả/ tuần) cũng không hề bị gì cả. Người tập thể hình có thể ăn 2-3 quả trứng với lòng đỏ/ngày cùng với 4 quả lấy lòng trắng bằng cách luộc hoặc đánh lên để tráng.','Dinh dưỡng','thehinhnam','Dinh dưỡng cho người tập Gym','37.jpg',1,'2020-08-13 15:36:45.202'),(18,'Các loại đậu','  Trong giới thực vật, đậu nành có lẽ là món được ưa chuộng nhất bởi những người đang có nhu cầu bổ sung protein từ nguồn không phải là thịt. Hương vị thơm ngon và dễ ăn cũng là một lợi thế nổi bật của đậu nành.','Dinh dưỡng','thehinhnam','Dinh dưỡng cho người tập Gym','38.jpg',1,'2020-08-13 15:36:45.202'),(19,'Các loại hạt',' Hạt điều, đậu phộng, hạt bí, hạt dẻ cười, hạnh nhân… đều là các loại hạt có chứa chất béo tốt cho cơ thể và cung cấp đáng kể lượng calorie. Điều quan trọng khác khi ăn các loại hạt có chất dầu là nó giúp bạn giảm được cảm giác đói cồn cào của bao tử, nhất là với những người thừa cân muốn tập để siết cơ bắp sắc nét. Vì vậy, các loại hạt có dầu được coi là thực phẩm lý tưởng để ăn nhẹ trước và sau mỗi buổi tập.','Dinh dưỡng','thehinhnam','Dinh dưỡng cho người tập Gym','39.jpg',1,'2020-08-13 15:36:45.202'),(20,'Cách Chế Biến Dọc Mùng Không Ngứa',' Rửa sạch cây dọc mùng cho khỏi bùn đất. Tước bỏ phần xơ phía bên ngoài như tước vỏ chuối. Sau đó dùng dao cắt hết phần bụng của dọc mùng (phần cong bên trong).','Mẹo hay','thegardenkitchen','Mẹo hay chế biến nguyên liệu','docmung.jpg',1,'2020-08-13 15:36:45.202'),(21,'Mẹo Làm Thịt Vịt Không Bị Hôi',' Để món vịt không bị hôi, trước khi luộc nên bóp với chút muối, tiêu, gừng đập dập, có thể cả chút rượu trắng, để chừng 30 phút rồi rửa sạch, để ráo, đem luộc. Cho một mẩu gừng đập dập vào nồi luộc.','Mẹo hay','thegardenkitchen','Mẹo hay chế biến nguyên liệu','thitvit.jpg',1,'2020-08-13 15:36:45.202'),(22,'Mẹo Khử Mùi Các Nguyên Liệu Tươi Sống',' Đun nước thật sôi, dùng muối hột khi hấp hay cho các loại gia vị tạo mùi như sả, gừng, hành để tạo hương thơm, làm giảm mùi tanh của nguyên liệu. Hấp hạn chế dầu mỡ, giúp món ăn giữ hương vị thơm ngon tự nhiên. Các gia vị thường được cho vào trong món hấp là gừng, sả, hành, hạn chế tối đa tỏi hay các gia vị nồng khác. Món ăn vì thế mà không bị át hay lẫn mùi.','Mẹo hay','thegardenkitchen','Mẹo hay chế biến nguyên liệu','29.jpg',1,'2020-08-13 15:36:45.202'),(23,'Mẹo Rửa Sạch Ốc Bươu Hết Chất Nhờn',' Sử dụng một thau đựng đầy nước vo gạo và ngâm ốc trong khoảng 1 - 2 tiếng đồng hồ, tất cả chất bùn trong miệng ốc sẽ tự động nhả ra và vón thành từng mảng chất nhầy. Chỉ cần bạn rửa lại với nước sạch là có những con ốc sạch để chế biến món ăn ngon.','Mẹo hay','thegardenkitchen','Mẹo hay chế biến nguyên liệu','ocbuou.jpg',1,'2020-08-13 15:36:45.202'),(24,'Chần Sơ Thực Phẩm',' Trước khi chế biến với mục đích loại bỏ chất bẩn trước khi nấu, loại bỏ các mùi nặng hoặc vị đắng của thực phẩm để bảo toàn nguyên hương vị của món ăn sau khi chế biến. Bạn có thể đổ nước lạnh vào nhập thực phẩm và đun sôi, sau đó, lại rửa sạch bằng nước lạnh. Hoặc nhúng thực phẩm vào nước hay dầu ăn đang sôi trong 1 thời gian ngắn, sau đó, vớt ra cho vào tủ lạnh hoặc ngâm vào nước lạnh.','Mẹo hay','huongnghiepaau','Mẹo hay chế biến nguyên liệu','chanso.jpg',1,'2020-08-13 15:36:45.202'),(25,'Ngâm Thực Phẩm',' Trước khi chế biến là cách làm của nhiều đầu bếp chuyên nghiệp. Cách làm này giúp làm mềm một số loại rau quả khô hoặc làm phai tiết của thịt, cá tươi. Thực phẩm khô như rau, củ, nấm… được ngâm trong nước lạnh trước khi chế biến. Khi ngâm nước sẽ ngấm vào thực phẩm và làm mềm chúng một cách dễ dàng. Tiết của thịt, cá tươi sẽ được cuốn trôi nếu bạn ngâm chúng với nước lạnh. Cách này còn giúp màu sắc của thịt, cá tươi hơn và đẹp mắt hơn.','Mẹo hay','huongnghiepaau','Mẹo hay chế biến nguyên liệu','ngamthucpham.jpg',1,'2020-08-13 15:36:45.202'),(26,'Giữ cá sống',' Nếu chưa kịp cho cá vào nước, ta có thể lấy một miếng giấy mỏng thấm nước gián vào mắt cá, làm như vậy có thể giúp cá sau 3-4 giờ nếu tiếp tục thả vào nước thì vẫn có thể bơi lội tung tăng như thường. Lý do ta làm cách làm này là vì: Trong thần kinh thị giác của cá có một tổ chức tuyến trạng rất quan trọng. Khi cá ra khỏi môi trường nước, dây tổ chức tuyến trạng này sẽ bị đứt làm cho cá chết. Lấy giấy ướt che chắn của mắt cá chính là kéo dài thời gian bị đứt của dây tổ chức tuyến trạng, bởi vậy cách làm này đã giúp cá sống thêm được một thời gian trong vòng 3-4 tiếng nữa.','Mẹo hay','Ban biên tập Cổng thông tin Tư Vấn Hỗ Trợ','Mẹo hay chế biến nguyên liệu','32.jpg',1,'2020-08-13 15:36:45.202'),(27,'Dinh dưỡng cho trẻ em giúp cao lớn khỏe mạnh',' Dinh dưỡng cho trẻ em dưới 3 tuổi có ý nghĩa quan trọng tới sức khỏe và cả quá trình phát triển của trẻ. Trong những năm đầu tốc độ phát triển của mô, các cơ quan cùng với sự phát triển sinh lý và tinh thần của trẻ rất nhanh. Khi dinh dưỡng của trẻ không đáp ứng đầy đủ sẽ dẫn đến chậm phát triển và cả những biến đổi về hóa sinh sẽ ảnh hưởng đến cơ thể ở rất nhiều mức độ khác nhau. Do vậy, việc chú ý bổ sung dinh dưỡng đúng cách để trẻ khỏe mạnh và phát triển cân đối là vô cùng quan trọng. Với những phương pháp chăm sóc bé khỏe mạnh mau lớn dưới đây, các bà mẹ sẽ có thêm cho mình nhiều kiến thức cần thiết để làm thế nào giúp trẻ khỏe mạnh mau lớn phát triển toàn diện.\r\n\r\n1. Chú ý đến chế độ dinh dưỡng cho trẻ em một cách hợp lý\r\nDinh dưỡng là yếu tố hàng đầu quyết định sự phát triển của trẻ. Bé sẽ phát triển khỏe mạnh và thông minh khi được bú sữa mẹ ít nhất trong 6 tháng đầu đời vì sữa mẹ giúp cơ thể xây dựng hệ miễn dịch kháng lại bệnh tật.\r\nĐối với các trẻ ở giai đoạn ăn dặm và trẻ lớn, các bậc cha mẹ cần cung cấp cho bé chế độ ăn đầy đủ, cân đối giữa 5 thành phần dinh dưỡng là chất đạm (thịt, cá, trứng..), tinh bột (gạo, khoai, ngô…), chất béo (dầu, mỡ..), rau củ trái cây và sữa. Chất đạm, chất béo và tinh bột sẽ cung cấp năng lượng cho trẻ; các chất dinh dưỡng từ rau quả sẽ giúp cơ thể bé sản xuất kháng thể, tăng cường khả năng miễn dịch như một hàng rào chống lại sự xâm nhập của virus vào cơ thể\r\n\r\n2. Khuyến khích trẻ vận động thường xuyên\r\nVận động giúp trẻ phát triển trí não và thể chất khỏe mạnh.\r\nVì vậy, cha mẹ nên khuyến khích trẻ rèn luyện thói quen vận động thường xuyên (bằng những hoạt động đơn giản, vừa sức như đi bộ, làm việc nhà, leo cầu thang…) và luyện tập đúng cách các môn thể thao ngoài trời (khoảng 1-2 giờ mỗi ngày) để bé có thể phát triển toàn diện cả về thể chất lẫn trí lực.\r\nĐộ tuổi nào thì trẻ cũng cần vận động vì vừa tiêu hao năng lượng dư thừa vừa giúp cơ thể thêm sảng khoái, sức đề kháng được tăng lên và ăn uống ngon miệng. Đồng thời, đây cũng là cách để học hỏi các kỹ năng, khám phá thế giới xung quanh.\r\n\r\n3. Sử dụng thêm sữa ngoài sữa mẹ\r\nSữa là nguồn cung cấp dưỡng chất dồi dào, nguồn dinh dưỡng cho trẻ em rất cần thiết cho sự phát triển và dễ hấp thu của trẻ. Uống sữa thường xuyên sẽ cung cấp hiệu quả các dưỡng chất mà cơ thể trẻ cần như vitamin A, B, D, canxi. Vì vậy, mẹ đừng quên cho con uống sữa đều đặn mỗi ngày để con có đủ nguồn dưỡng chất thiết yếu và phát triển khỏe mạnh.\r\n\r\n4. Lựa chọn môi trường giáo dục tốt cho trẻ\r\nYếu tố môi trường cũng tác động tới sự phát triển của trẻ. Việc sống trong môi trường sạch sẽ, trong lành, giữ vệ sinh đúng cách sẽ giúp con bạn hạn chế sự xâm nhập của vi khuẩn. Môi trường kích thích trẻ học hỏi (cho bé tiếp xúc với đồ chơi phát triển trí não, khám phá thiên nhiên và cuộc sống xung quanh…) cũng là phương pháp tốt để bé phát triển trí tuệ.\r\n\r\n5. Các nội dung chăm sóc khác\r\nGiấc ngủ đặc biệt quan trọng với sự phát triển thể chất và tinh thần của trẻ. Bé thiếu ngủ thường hay quấy khóc, khó chịu, chậm lớn và dễ bị bệnh vặt. Do đó, các khuyến nghị cho biết trẻ từ 3 đến 35 tháng phải ngủ 12-14 giờ một ngày, trẻ 3-5 tuổi ngủ 11-13 giờ một ngày, trẻ từ 6-10 tuổi cần ngủ 10-11 giờ một ngày.\r\nTất cả trẻ nhỏ đều cần được tiêm phòng đầy đủ theo chương trình quốc gia. Đây là biện pháp phòng bệnh hiệu quả. Bé sẽ dồn sức để phát triển thay vì phải chống chọi với nhiều căn bệnh dễ mắc vì hệ miễn dịch còn yếu chưa tự đối phó. Hơn nữa, tiêm chủng có thể phòng ngừa được cho bé những căn bệnh nguy hiểm đến tính mạng.','Dinh dưỡng','VIỆN Y HỌC ỨNG DỤNG VIỆT NAM - viamclinic.vn','Dinh dưỡng cho trẻ','1.jpg',1,'2020-08-11 21:57:05.975'),(28,'Chăm sóc, dinh dưỡng cho bé phù hợp theo từng độ tuổi','Dinh dưỡng cho trẻ em dựa trên các nguyên tắc giống như dinh dưỡng cho người lớn. Mọi người đều cần các loại chất dinh dưỡng giống nhau, chẳng hạn như vitamin, khoáng chất, carbohydrate, protein và chất béo. Tuy nhiên, ở mỗi độ tuổi của trẻ, cơ thể sẽ cần thành phần cũng như số lượng các dưỡng chất khác nhau.\r\n\r\n1. Dinh dưỡng cho trẻ sơ sinh\r\nTrong 6 tháng đầu đời, trẻ sơ sinh không cần bất kỳ chất dinh dưỡng nào khác ngoài sữa mẹ hoặc sữa công thức hay sự kết hợp của cả hai. Nhu cầu dinh dưỡng của trẻ sơ sinh được đáp ứng tốt nhất bởi người mẹ, sữa mẹ giúp xây dựng khả năng miễn dịch của trẻ.\r\n\r\nNếu bú mẹ, một trẻ sơ sinh cần được cho bú từ 8 đến 12 lần mỗi ngày hoặc tùy theo nhu cầu của bé. Khoảng 4 tháng, số lần bú có thể giảm xuống còn 4 - 6 lần mỗi ngày, tuy nhiên lượng sữa mẹ trong mỗi lần bú sẽ tăng lên.\r\n\r\nBé được nuôi bằng sữa thay thế nên được cho bú khoảng 6 - 8 lần mỗi ngày, trẻ sơ sinh bắt đầu với 57 – 85g sữa bột cho mỗi lần (tổng cộng khoảng 450 – 680g mỗi ngày). Tương tự với trường hợp trẻ bú sữa mẹ, số lần cho bú sẽ giảm khi bé lớn hơn nhưng lượng sữa thay thế sẽ tăng khoảng từ 170 - 227g/lần.\r\n\r\nSau 4 tháng, hệ tiêu hóa của trẻ đã tương đối hoàn chỉnh, có thể giúp bé tiêu hóa những thức ăn khác ngoài sữa. Do đó, trong khoảng thời gian từ 4-6 tháng tuổi, bên cạnh bú sữa, có thể bắt đầu cho trẻ tập ăn dặm thêm những thức ăn lỏng. Không nên cho trẻ ăn những loại thức ăn đặc, nó có thể khiến cho bé bị nghẹt thở do cơ thể chưa thích nghi được.\r\n\r\nKhi trẻ từ 6 tháng trở lên, hầu hết các bé đã sẵn sàng để bắt đầu ăn các loại thực phẩm rắn như ngũ cốc cho trẻ sơ sinh và các loại trái cây, rau quả và thịt xay nhuyễn. Cần bổ sung các loại thực phẩm này bởi vì sữa mẹ có thể không cung cấp đủ sắt và kẽm cho sự phát triển của trẻ.\r\n\r\n2. Trẻ từ 6-12 tháng\r\nTừ 6-8 tháng tuổi, tiếp tục cho bé bú mẹ hoặc sữa thay thế từ 3-5 lần một ngày. Bé sẽ bắt đầu bú ít sữa mẹ hoặc sữa thay thế khi mà thức ăn đặc trở thành nguồn dinh dưỡng chính. Thời điểm này, bạn có thể bắt đầu cho trẻ ăn các loại thực phẩm có nguồn gốc thực vật khác: rau (khoai tây, đậu xanh, cà rốt và đậu Hà Lan) đều là những lựa chọn tốt, chúng nên được nấu chín kỹ và nghiền, trái cây (chẳng hạn như chuối nghiền, bơ, đào, hoặc táo).\r\n\r\nTừ 8-12 tháng tuổi, nên tiếp tục cho trẻ bú sữa mẹ hoặc sữa thay thế 3-4 lần/ ngày. Ở độ tuổi này, cần bổ sung thêm các loại thịt hầm, thịt băm vào khẩu phần ăn của trẻ.\r\n\r\n3. Dinh dưỡng cho trẻ từ 1 tuổi\r\nKhi trẻ được 1 tuổi, nên tăng dần lượng thức ăn dặm, bé sẽ bú hoặc uống ít sữa hơn. Lúc này bé cần được cung cấp nhiều chất dinh dưỡng từ thịt, trái cây, rau, bánh mì và hạt ngũ cốc và nhóm sữa, đặt biệt sữa nguyên kem. Việc này sẽ giúp đảm bảo đầy đủ lượng vitamin và khoáng chất cho trẻ. Tuy nhiên, sữa mẹ hoặc sữa công thức vẫn là nguồn dinh dưỡng chính cho bé trong suốt năm đầu đời, do đó ở thời điểm này, sữa vẫn nên chiếm khoảng 70% khẩu phần ăn.\r\n\r\nCần lưu ý rằng trong thời gian này trẻ bắt đầu học cách bò và đi nên sẽ ăn ít thức ăn trong 1 bữa ăn, nhưng sẽ ăn thường xuyên hơn (4-6 lần) trong cả ngày, vì vậy, bố mẹ nên thêm những cử ăn nhẹ ngoài khẩu phần cho trẻ.\r\n\r\n4. Trẻ từ 2-5 tuổi\r\nSau 24 tháng, hầu hết các bé đã mọc đủ răng và cứng chắc hơn giai đoạn 1 tuổi. Lúc này bé không còn ăn cháo, bột nữa mà có thể ăn những thức ăn giống người lớn, bố mẹ nên cho trẻ ăn cơm cùng gia đình để tạo thói quen tốt trong ăn uống, các món ăn dành cho bé có thể là cháo đặc, súp đặc, cơm,... đồng thời vẫn cho trẻ uống sữa ít nhất 1 lần/ngày. Bên cạnh 3 bữa ăn chính cùng gia đình, bạn có thể cho trẻ ăn thêm 2 bữa phụ vào giữa buổi sáng và giữa buổi chiều, bữa phụ giúp trẻ không bị đói, ăn uống ngon miệng hơn, trong bữa phụ này, bạn nên cho trẻ ăn các loại trái cây, sữa, sữa chua để hỗ trợ hệ tiêu hóa.\r\n\r\nTóm lại, ở bất kể độ tuổi nào, dù là trẻ sơ sinh hay tuổi mẫu giáo thì dinh dưỡng luôn đóng vai trò quan trọng đối với sự phát triển thể chất và tinh thần của bé. Ở mỗi độ tuổi khác nhau, bố mẹ cần thay đổi chế độ ăn hợp lý để đáp ứng được nhu cầu dinh dưỡng cho bé theo độ tuổi.','Dinh dưỡng','Bệnh viện Đa khoa Quốc tế Vinmec - vinmec.com','Dinh dưỡng cho trẻ','2.png',1,'2020-08-11 21:57:05.975'),(29,'Dinh dưỡng cho trẻ từ 1 đến 3 tuổi','Trẻ từ 1 đến 3 tuổi phát triển nhanh cả về thể lực và trí tuệ, nếu được chǎm sóc, nuôi dưỡng đầy đủ sẽ phát triển tốt, ít ốm đau bệnh tật. Bộ máy tiêu hóa và các chức nǎng tiêu hóa, hấp thu của trẻ chưa thật hoàn chỉnh nên các thiếu sót trong nuôi dưỡng và chǎm sóc trẻ có thể dẫn đến suy dinh dưỡng. Nên tận dụng sữa mẹ để hỗ trợ thêm cho bé dinh dưỡng và kháng thể, cố gắng cho trẻ bú đến 18 - 24 tháng. Khẩu phần ǎn của trẻ cần được cung cấp đủ nǎng lượng, chất đạm, chất béo, vitamin và muối khoáng.\r\n\r\n1. Nǎng lượng: Cung cấp đủ năng lượng giúp trẻ phát triển toàn diện, sự tiêu hao nǎng lượng của trẻ lớn do trẻ chơi, đùa nghịch nhiều. Nhu cầu nǎng lượng ở lứa tuổi này là 100-110Kcal/kg cân nặng, trẻ nặng khoảng 9 - 13 kg thì nǎng lượng cung cấp là 900 - 1300 kcal. Nǎng lượng được cung cấp đủ qua bữa ǎn của trẻ gồm có: Chất bột như bột, cháo, chất đạm, chất béo, sữa... Tỷ lệ giữa các thành phần sinh nǎng lượng nên là: Đạm: Béo: Đường bột = 15: 20: 65.\r\n\r\n2. Chất đạm: Chất đạm rất cần cho sự phát triển cơ thể trẻ, cần ưu tiên các loại đạm động vật có giá trị cao, đủ acid amin, giàu các yếu tố vi lượng như sắt, kẽm, vitamin: thịt, sữa, trứng, cá, tôm…. Lượng đạm động vật trong khẩu phần ǎn của trẻ nên đạt từ 50 - 60%. Nên phối hợp tốt đạm động vật với đạm thực vật (đậu đỗ, vừng, lạc...) sẽ tạo được sự cân đối giúp hấp thu và sử dụng đạm tốt hơn. Nhu cầu chất đạm là 35-44g/ ngày. Chế độ ǎn thiếu đạm sẽ làm cho trẻ suy dinh dưỡng, chậm lớn, kém thông minh, nhưng nếu trẻ ǎn quá nhiều đạm sẽ là gánh nặng cho gan, thận. Mặt khác, trong quá trình tiêu hóa chất đạm tạo ra nhiều sản phẩm gây thối rữa, độc hại. \r\n\r\n3. Chất béo: Dầu mỡ cung cấp nǎng lượng cao, làm tǎng cảm giác ngon miệng lại giúp trẻ hấp thu và sử dụng tốt các vitamin tan trong chất béo như vitamin A, D, K, E. Mỗi bát bột, bát cháo cần cho thêm 1 -2 thìa cà phê mỡ hoặc dầu. Mỡ lợn, mỡ gà có các axit béo không no cần thiết như: axit linoleic, axit arachidonic rất cần thiết cho quá trình phát triển của trẻ, nhất là não bộ. Nếu trẻ đã ǎn cơm thì nên cho mỡ hoặc dầu vào xào, rán, kho với thức ǎn để trẻ ǎn được (20-40g dầu mỡ/ ngày)\r\n\r\n4. Các chất khoáng: Có vai trò quan trọng trong sự tạo xương, tạo rǎng, tạo máu và các hoạt động chức nǎng sinh lý của cơ thể. Can xi và photpho cần được chú ý để cung cấp đủ cho trẻ, khoảng 500 - 600mg/ngày. Canxi có nhiều trong sữa và các loại nhuyễn thể (tôm, cua, ốc, trai...). Photpho có nhiều trong các loại lương thực, ngũ cốc. Tỷ lệ tốt nhất giữa canxi/ photpho = 1/1,5-1/1,8. Chuyển hóa canxi và photpho trong cơ thể được điều hòa bởi vitamin D, vitamin D lại có trong lòng đỏ trứng, thịt, gan và dưới tác dụng của ánh nắng mặt trời tiền vitamin D dưới dạng dự trữ dưới da sẽ chuyển thành vitamin D hoạt động. Do vậy ngoài ǎn uống đủ, cần cho trẻ ra ngoài tắm nắng mỗi ngày khoảng 15-20 phút trước 8h sáng.\r\nChất sắt tham gia vào quá trình tạo máu. Mỗi ngày trẻ cần 7-8 mg/ngày thức ǎn. Nguồn sắt tốt có trong thức ǎn động vật là các nội tạng: tim, gan, bầu dục, ở thực vật là đậu đỗ và các loại rau có màu xanh sẫm: rau muống, rau ngót. Sắt trong thức ǎn động vật hấp thu tốt hơn trong thức ǎn thực vật. Vitamin C trong rau giúp cơ thể hấp thu và sử dụng sắt có hiệu quả hơn.\r\nKẽm là 1 vi chất dinh dưỡng ảnh hưởng đến qua trình tăng trưởng và tiêu hóa. Trẻ em thiếu kẽm thường còi cọc, hay mắc các bệnh nhiễm khuẩn về đường hô hấp, nôn trớ, rối loạn giấc ngủ… Nhu cầu kẽm khoảng 8-10mg/ngày. Kẽm có nhiều trong các loại thức ăn động vật như: hải sản, trai, hến, sò huyết, thịt cá…\r\n\r\n5. Vitamin:  Lứa tuổi này người ta quan tâm đến vitamin A, D, C, chúng cần cho sự phát triển xương, răng, sự tạo máu, tǎng sức đề kháng cho cơ thể. Nhu cầu Vitamin A: 400-450 mcg/ngày, Vitamin D 400UI/ngày, Vitamin C: 30mg/ngày, Vitamin A có trong các trứng, gan. . . Rau quả có màu vàng, đỏ, da cam vừa là nguồn cung cấp caroten (tiền vitamin A) vừa là nguồn cung cấp vitamin C. Để đảm bảo nhu cầu vitamin cần cho trẻ ǎn rau, quả thường xuyên.\r\n\r\nNhững điểm cần lưu ý trong nuôi dưỡng trẻ từ 1 đến 3 tuổi: \r\n- Thức ǎn cần chế biến từ mềm đến cứng, từ ít đến nhiều để trẻ quen dần. Khi trẻ đã mọc rǎng hàm, cần tạo điều kiện cho trẻ luyện rǎng, luyện cơ nhai, do vậy không cần thiết phải cho mọi thức ǎn vào máy xay sinh tố nghiền nát mà nên thái, bǎm từ rất nhỏ đến nhỏ vừa; nấu từ rất mềm đến mềm vừa đến cứng để tạo cảm giác ngon miệng và giúp rǎng lợi, cơ nhai, cơ tiêu hóa phát triển. \r\n- Sau khi cai sữa cần có chế độ ǎn riêng cho trẻ, không bắt trẻ ǎn chung quá sớm với người lớn sẽ ảnh hường tới tiêu hóa của trẻ, đặc biệt là lượng muối trong thức ăn.\r\n- Thường xuyên thay đổi cách chế biến để tạo cảm giác ngon miệng. \r\n- Hạn chế cho trẻ ǎn đồ ngọt (đường, bánh kẹo). Đường ngọt làm cho trẻ có cảm giác no giả tạo nên không muốn ǎn các thức ǎn khác, mặt khác nó còn ứ lại trong miệng rồi chuyển thành axit dễ làm hỏng rǎng. Chỉ nên cho trẻ ǎn bánh, kẹo sau bữa ǎn. \r\n- Cần cho trẻ uống đủ nước: 100ml/kg/ngày, nước giúp trẻ tiêu hóa và hấp thu tốt các loại dinh dưỡng, nước còn có vai trò vận chuyển giúp thải trừ các sản phẩm cặn bã, độc hại của quá trình chuyển hóa ra khỏi cơ thể. ','Dinh dưỡng','Bệnh viện trung ương quân đội 108 - benhvien108.vn','Dinh dưỡng cho trẻ','3.jpg',1,'2020-08-11 21:57:05.975'),(30,'Dinh dưỡng cho trẻ sơ sinh và trẻ nhỏ','Với những bậc cha mẹ, nắm rõ những loại thực phẩmthiết yếu có lợi cho bé của bạn ở từng giai đoạn tăng trưởng và phát triển là vô cùng cần thiết. Đây là bước cơ bản nhất để bắt đầu cho bé một chế độ ăn uống lành mạnh.\r\n\r\nDinh dưỡng cho trẻ sơ sinh\r\nTrong giai đoạn đầu của sự phát triển, sữa mẹ hoặc sữa công thức hoặc sự kết hợp của cả hai là nguồn thức ăn tốt nhất để bé nhận được nguồn dinh dưỡng cần thiết cho sự phát triển của mình.\r\n\r\nNgay cả khi bé bắt đầu ăn dặm, sữa mẹ và sữa bột vẫn còn nguồn cung cấp dưỡng chất chính cho bé. Ở những ngày đầu của quá trình ăn dặm, thức ăn chỉ là nguồn bổ sung để tập cho bé làm quen với những loại thức ăn cũng như các phản xạ nhai, nuốt. Đây là thời điểm tốt để bạn nên bắt đầu chú ý hơn về vấn đề dinh dưỡng trong các thức ăn dặm của bé, vì bé sẽ  từ từ chuyển sang thức ăn đặc và càng ngày càng ít cần đến sữa mẹ hoặc sữa công thức như một nguồn dinh dưỡng chính.\r\n\r\nNhu cầu dinh dưỡng khi cho bé ăn dặm.\r\nĐiều quan trọng là cung cấp cho bé một chế độ  dinh dưỡng cân bằng bằng việc kết hợp các loại thực phẩm như ngũ cốc, cháo, hoa quả nghiền, rau xay nhuyễn, thịt, cá... để cung cấp lượng sắt, canxi, protein, và các vitamin C và A cần thiết cho bé. Hãy tham khảo việc làm sao để tạo nên một chế độ ăn uống cân bằng bằng cách tìm hiểu các nhóm thực phẩm cơ bản và tập cho bé làm quen với tất cả những loại thức ăn này khi bé bắt đầu ăn dặm.\r\nMặc dù sữa rất giàu canxi và các chất dinh dưỡng khác, tốt nhất bạn vẫn  không nên cho bé uống sữa bò trước khi bé tròn 12 tháng tuổi. Tuy nhiên, khi bé được 7-8 tháng tuổi, bạn có thể tập cho bé làm quen với các sản phẩm từ sữa khác, chẳng hạn như sữa chua và pho mát...\r\n\r\nCùng với kẹo và sôcôla, các loại thực phẩm có đường khác luôn có nguy cơ làm bé bị sâu răng, đường cũng không tốt cho sức khỏe của bé. Vì vậy, hãy khuyến khích bé ăn trái cây tươi thay vì kẹo, uống nước loại thay vì nước ngọt hay nước trái cây đóng hộp.\r\n\r\nBên cạnh đó, hãy tham khảo danh sách các loại phụ gia thực phẩm của chúng tôi để hiểu hơn đồng thời có cách giảm thiểu các chất phụ gia trong chế độ ăn uống của con bạn.\r\n\r\nKhi bé kén ăn\r\nNếu bé kén ăn, ăn ít và làm bạn lo lắng rằng bé có thể không nhận được đủ chất dinh dưỡng, hãy cùng Huggies đánh giá ăn uống của bé.\r\n\r\nKhuyến khích bé đề nghị món ăn bé thích và cho bé ham gia vào việc chuẩn bị cho bữa ăn sẽ làm cho bé phấn khích và cố gắng ăn nhiều hơn. Hãy tham khảo ngân hàng công thức của chúng tôi để tìm ra những công thức hoặc ý tưởng để nấu nướng cho con của bạn.','Dinh dưỡng','hang bim huggies.com.vn','Dinh dưỡng cho trẻ','4.jpg',1,'2020-08-11 21:57:05.975'),(31,'Dinh dưỡng phát triển trí não trẻ em',' Làm cha mẹ ai cũng mong muốn có đứa con thông minh và khỏe mạnh. Vậy trí thông minh do những yếu tố nào quyết định? Thực sự có những loại thực phẩm nào ăn vào để phát triển sự thông minh của trẻ hay không?\r\n\r\nSự phát triển trí thông minh ở trẻ phụ thuộc vào 3 yếu tố:\r\n\r\n- Do di truyền (do gen)\r\n\r\n- Do chế độ dinh dưỡng\r\n\r\n- Do sự rèn luyện, học tập, môi trường sống Như vậy thực sự không thể có một loại thực phẩm nào có thể làm trẻ thông minh, mà các chất dinh dưỡng chỉ có tác dụng hỗ trợ để phát huy những tiềm năng di truyền về gen thông minh đã sẵn có. Ngoài ra sự giáo dục, rèn luyện học tập cũng có tác dụng hỗ trợ cho trí thông minh phát triển.\r\n\r\nTuy nhiên nhiều nghiên cứu đã chỉ ra rằng một số chất dinh dưỡng nếu bị thiếu hụt cũng ảnh hưởng đến sự phát triển trí não ở trẻ em đó là:\r\n\r\n1. Chất đạm (protein): Protein là vật liệu xây dựng nên các tế bào mô, cơ quan, cung cấp các nguyên liệu cần thiết cho sự hình thành các dịch tiêu hóa, các nội tiết tố, các men và các vitamin. Khi thiếu chất đạm sẽ ảnh hưởng sự phát triển của cơ thể nói chung và não bộ nói riêng.\r\n\r\n2. Iốt: Khi trong thực phẩm thiếu iốt thì không những lượng iốt di chuyển qua nhau thai của người mẹ sang bào thai sẽ không đủ để đáp ứng sự phát triển não bộ tối ưu, mà nguy cơ hàm lượng iốt trong sữa mẹ cũng sẽ rất thiếu dẫn đến suy giảm sự phát triển não bộ và dẫn đến bệnh đần độn do thiểu năng tuyến giáp.\r\n\r\n3. Sắt: Sắt là thành phần của huyết sắc tố myoglobin, các xitrocrom và nhiều engym khác. Sắt vận chuyển oxy và giữ vai trò quan trọng trong hô hấp tế bào. Nhiều nghiên cứu đã chứng minh thiếu sắt đã ảnh hưởng nghiêm trọng đến sự phát triển não bộ trong thời thơ ấu. Trẻ bị thiếu máu thiếu sắt có chỉ số phát triển tâm thần và vận động thấp hơn trẻ cùng trang lứa cùng môi trường sống. Khi trẻ đã lớn thiếu máu thiếu sắt cũng ảnh hưởng đến sự phát triển trí nhớ và ảnh hưởng đến kết quả học tập do ngủ gật trong giờ học và thiếu oxy não.\r\n\r\n4. Các axit béo không no chuỗi dài: Thành phần của não bộ có đến 60% là chất béo. Trong đó DHA (Docosahexaenoic Acid) và ARA (Arachidonic Acid) là các thành phần lipid chính của não. Trong thời gian có thai người mẹ huy động DHA và ARA để hỗ trợ cho sự phát triển não bộ thai nhi. Người mẹ tiếp tục cung cấp hai dưỡng chất quan trọng này qua nguồn sữa của mình nên nuôi con bằng sữa mẹ làm tăng chỉ số thông minh của trẻ. Trẻ được bú sữa mẹ có chỉ số thông minh (IQ) cao hơn trẻ bú bình từ 3 - 5 điểm.\r\n\r\nNgoài 4 chất dinh dưỡng kể trên còn nhiều các vi chất dinh dưỡng khác như: kẽm, magiê, đồng, crom, selen…cũng rất quan trọng cho sự phát triển cơ thể của trẻ cũng như não bộ. Vậy muốn con thông minh, các bà mẹ cần phải làm gì? Trước và trong thời kỳ mang thai, thời gian cho con bú người mẹ phải ăn uống đầy đủ các dưỡng chất quan trọng để phát triển tốt não đó là: chất đạm, iốt, sắt, axit folic, các axit béo chưa no (DHA, ARA) có trong các loại thực phẩm như thịt, cá, tôm, cua, trứng, sữa, rau xanh và quả chín. Người mẹ nên ăn nhiều cá nhất là các loại cá biển có chứa nhiều axit béo chưa no (DHA, ARA), uống thêm dầu gan cá, ăn các loại dầu thực vật (dầu đậu nành, dầu oliu…) cũng cung cấp các tiền DHAvà ARA, đó là các anpha linolenic, axit linoleic…khi vào trong cơ thể sẽ được tổng hợp thành DHA và ARA. Nhiều nghiên cứu đã được chứng minh khi có thai bà mẹ ăn cá thường xuyên > 5 lần/tuần sinh ra con có chỉ số IQ cao hơn 8 điểm so với các bà mẹ không ăn cá trong thời kỳ mang thai. Tiếp tục 2 năm cho con bú nếu bà mẹ thường xuyên ăn cá, dầu thực vật thì nguồn DHA và ARA trong sữa cao giúp cho sự phát triển thị lực và trí não của trẻ.\r\n\r\nTrong giai đoạn quan trọng của 2 năm đầu đời, thời kỳ ăn dặm (từ 7 tháng - 3 tuổi): Trẻ cần được ăn đầy đủ các chất dinh dưỡng để cung cấp chất đạm, chất sắt, qua nguồn thức ăn động vật như thịt, cá, trứng, sữa, ăn muối, nước mắm có bổ sung iốt. Các axit béo không no từ dầu ăn, dầu gan cá, cá biển, các loại vitamin và muối khoáng khác từ nguồn rau xanh và quả chín, uống các loại sữa có bổ sung DHA, ARA, Iốt, sắt, taurin và các vi chất dinh dưỡng khác.\r\n\r\nNhư vậy muốn có đứa con thông minh, khỏe mạnh các bà mẹ phải chuẩn bị từ trước khi mang thai 1 - 3 tháng. Ăn uống đầy đủ khi mang thai, nuôi con bằng sữa mẹ, cho trẻ ăn bổ sung đầy đủ và hợp lý thì trẻ sẽ phát triển tốt về thể lực và trí não, kết hợp với việc giáo dục và môi trường sống tốt chúng ta sẽ có những trẻ em thông minh và khỏe mạnh, trở thành nguồn nhân tài cho đất nước mai sau.','Dinh dưỡng','benh vien viendinhduong.vn','Dinh dưỡng cho trẻ','1.jpg',1,'2020-08-12 17:59:10.017'),(32,'CHỌN THỰC PHẨM DỰA TRÊN CHỈ SỐ TIỂU ĐƯỜNG CHO NGƯỜI BỆNH','Khi nhắc đến chế độ ăn cho người bệnh tiểu đường (đái tháo đường), điều quan trọng nhất là một chế độ dinh dưỡng cân bằng để vừa giúp ổn định lượng đường trong máu và vừa đảm bảo dưỡng chất đầy đủ cho cơ thể hoạt động, giúp ngăn ngừa cảm giác đói và thèm ăn. Theo khuyến cáo của Hiệp Hội Đái Tháo Đường Châu Âu (EASD), chế độ ăn cho người đái tháo đường nên bao gồm 45-60% chất bột đường trong đó nên sử dụng thực phẩm có chỉ số đường huyết thấp hơn 55 và giàu chất xơ, 10-20% chất đạm như thịt, cá và trứng và 25-35% chất béo!\r\nKhi nhắc đến chế độ ăn cho người bệnh tiểu đường (đái tháo đường), điều quan trọng nhất là một chế độ dinh dưỡng cân bằng để vừa giúp ổn định lượng đường trong máu và vừa đảm bảo dưỡng chất đầy đủ cho cơ thể hoạt động, giúp ngăn ngừa cảm giác đói và thèm ăn. Theo khuyến cáo của Hiệp Hội Đái Tháo Đường Châu Âu (EASD), chế độ ăn cho người đái tháo đường nên bao gồm 45-60% chất bột đường trong đó nên sử dụng thực phẩm có chỉ số đường huyết thấp hơn 55 và giàu chất xơ, 10-20% chất đạm như thịt, cá và trứng và 25-35% chất béo!\r\n\r\nChọn Thực Phẩm Dựa Trên Chỉ Số Tiểu Đường Cho Người Bệnh\r\nĐiều quan trọng mà người bệnh tiểu đường (đái tháo đường) cần lưu ý là khi lựa chọn thực phẩm thuộc nhóm bột đường, cần ưu tiên sử dụng những thực phẩm có chỉ số đường huyết thấp (dưới 55) chứ không phải cắt bỏ hoàn toàn nhóm thực phẩm này. Các loại thức ăn nào khi ăn vào cơ thể sản xuất ra lượng đường (glucose) khác nhau, nghĩa là chỉ số đường huyết khác nhau, ví dụ như cơm sẽ làm tăng đường huyết cao hơn bánh mì. Làm thế nào để biết loại thức ăn nào làm tăng đường huyết ở mức độ ra sao? Chỉ số đường huyết của thức ăn sẽ cho bạn biết điều đó.\r\n\r\nVậy chỉ số đường huyết (GI) là gì?\r\n\r\nChỉ số đường huyết chính là thước đo khả năng làm tăng đường huyết sau khi ăn của một loại thực phẩm so với thực phẩm chuẩn (như bánh mì trắng). Giá trị của chỉ số đường huyết được xếp loại thành THẤP (<55), VỪA (56-74) và CAO (75)2.\r\n\r\nMột điểm cộng nữa khi chọn thức ăn dựa trên chỉ số GI, theo khuyến cáo từ Hội Đái Tháo Đường Hoa Kỳ3, chính là bạn có thể cân bằng chế độ ăn của mình bằng cách kết hợp giữa các món có chỉ số GI cao (bánh mì, gạo nguyên cám) và thấp (như rau củ hoặc sản phẩm dinh dưỡng chuyên biệt Glucerna) với nhau.','Dinh dưỡng','glucerna.com.vnnnnnn','Chế độ ăn cho người bệnh','6.jpg',1,'2020-08-11 21:57:05.975'),(33,'Chế độ ăn uống lành mạnh cho tim: 8 bước để ngăn ngừa bệnh tim','Nếu bạn muốn xây dựng cho mình một chế độ ăn uống tốt cho tim mạch, hãy thực hiện theo tám bước để ngăn ngừa bệnh tim dưới đây.\r\n\r\n1. Kiểm soát khẩu phần ăn\r\nLượng thức ăn bạn tiêu thụ vào cơ thể cũng quan trọng như việc bạn ăn các loại thực phẩm nào. Nếu bạn ăn quá nhiều hoặc ăn theo kiểu nhồi đến khi bị căng tức bụng sẽ dẫn đến dư thừa lượng calo hơn mức cần thiết.\r\n\r\nTốt nhất, bạn nên chọn cho mình những thực phẩm ít năng lượng, giàu chất dinh dưỡng, chẳng hạn như trái cây, rau quả tươi; hạn chế ăn những loại thức ăn chứa hàm lượng calo cao, giàu natri, ví dụ như thực phẩm chế biến sẵn hoặc đồ ăn nhanh. Chiến lược này có thể giúp bạn cải thiện chế độ ăn uống lành mạnh hơn, mang lại một trái tim khỏe mạnh và một vòng eo lý tưởng.\r\n\r\nNgoài ra, bạn cần thường xuyên theo dõi số lượng thức ăn dung nạp vào cơ thể. Số lượng khẩu phần được khuyến nghị cho mỗi nhóm thực phẩm có thể thay đổi tùy thuộc vào chế độ ăn uống cụ thể mà bạn đang tuân theo. Có thể ban đầu bạn vẫn chưa quen với việc thực hiện xác định khẩu phần ăn, bạn nên sử dụng cốc đo lường, thìa, bát hoặc cân cho đến khi bạn cảm thấy tự tin với phán đoán của mình.\r\n\r\n2. Ăn nhiều rau xanh và trái cây\r\nRau xanh và trái cây luôn là những loại thực phẩm cung cấp nguồn vitamin và khoáng chất dồi dào cho cơ thể. Đặc điểm nổi trội của loại thực phẩm này là ít năng lượng (calo), giàu chất xơ, vitamin và khoáng chất. Rau và trái cây cũng giống như các loại thực vật hoặc thực phẩm có nguồn gốc thực vật khác, có chứa các chất có thể giúp ngăn ngừa bệnh tim mạch hiệu quả. Ăn nhiều trái cây và rau quả có thể giúp bạn cắt giảm thực phẩm có lượng calo cao hơn, chẳng hạn như thịt, phô mai và đồ ăn nhanh.\r\n\r\nTrong khẩu phần ăn, rau và hoa quả đều rất dễ ăn. Bạn có thể rửa rau sạch sẽ và cất trong tủ lạnh để sử dụng dần. Chọn công thức nấu ăn có rau hoặc trái cây làm nguyên liệu chính, chẳng hạn như rau xào hoặc trái cây tươi trộn vào món salad.\r\n\r\nNhững loại rau và trái cây nên ăn:\r\n\r\nTrái cây và rau tươi.\r\nRau đóng hộp ít natri\r\nTrái cây đóng hộp kèm nước trái cây.\r\nNhững loại rau và trái cây không nên ăn:\r\n\r\nRau kèm sốt béo.\r\nRau chiên giòn.\r\nTrái cây đóng hộp kèm sirô đường.\r\nTrái cây đông lạnh có bỏ thêm đường.\r\n\r\n3. Chọn ngũ cốc nguyên hạt\r\nNgũ cốc nguyên hạt là nguồn chất xơ tốt và có chứa nhiều chất dinh dưỡng khác có vai trò thiết yếu trong việc điều hòa huyết áp và sức khỏe của tim. Bạn có thể tăng lượng ngũ cốc nguyên hạt trong chế độ ăn có lợi cho tim bằng cách thay thế cho các sản phẩm ngũ cốc tinh chế. Một số loại ngũ cốc nguyên hạt bạn có thể sử dụng ví dụ như ngũ cốc nguyên hạt farro, quinoa hoặc lúa mạch...\r\nCác loại ngũ cốc nên ăn:\r\nCác loại ngũ cốc nguyên hạt.\r\nBánh mì làm từ ngũ cốc nguyên hạt.\r\nNgũ cốc giàu chất xơ (có hơn 5g xơ trong mỗi phần ăn).\r\nMì làm từ ngũ cốc nguyên hạt.\r\nYến mạch.\r\nCác loại ngũ cốc nên hạn chế sử dụng:\r\n\r\nBột mì tinh chế.\r\nBánh mì trắng.\r\nBánh quế\r\nBánh Muffins.\r\nBánh vòng\r\nBánh mì bắp.\r\nBánh bông lan\r\nBánh quy.\r\nMì trứng gà.\r\nBắp rang bơ.\r\nBánh cracker.\r\n\r\n4. Hạn chế sử dụng các chất béo không lành mạnh\r\nMột trong những bước quan trọng để giảm nồng độ cholesterol trong máu và giảm nguy cơ mắc bệnh động mạch vành là hạn chế sử dụng chất béo bão hòa và chất béo chuyển hóa. Nồng độ cholesterol trong máu cao có thể dẫn đến sự tích tụ các mảng bám trong động mạch, gây ra bệnh xơ vữa động mạch, có thể làm tăng nguy cơ đau tim và đột quỵ.\r\n\r\nDưới đây là các khuyến nghị về lượng chất béo nên ăn trong chế độ ăn hỗ trợ cho tim của Hiệp hội Tim mạch Hoa Kỳ:\r\n\r\nChất béo bão hoà không vượt quá 5%- 6% tổng lượng calo hàng ngày của bạn, hoặc không quá 11 đến 13g chất béo bão hòa nếu bạn thực hiện chế độ ăn 2.000 calo mỗi ngày.\r\nTránh ăn các thực phẩm có chất béo chuyển hóa.\r\nBạn có thể giảm lượng béo bão hòa trong chế độ ăn của mình bằng cách chọn thịt nạc hoặc cắt bỏ bớt lượng mỡ trong thịt. Khi nấu ăn, nên giảm lượng bơ, margarine.\r\n\r\nNên kiểm tra nhãn thực phẩm của một số loại bánh quy, bánh ngọt và khoai tây chiên, bởi vì có khả năng một trong số những loại này (kể cả những loại được dán nhãn “giảm chất béo”) đều có thể được làm bằng các loại dầu có chứa chất béo chuyển hóa. Nếu trong danh sách thành phần có cụm từ “hydro hóa một phần” có nghĩa là thực phẩm đó có chứa chất béo chuyển hóa. Khi sử dụng chất béo, hãy chọn các chất béo không bão hòa đơn, chẳng hạn như dầu ô liu hoặc dầu hạt cải. Chất béo không bão hòa đa, được tìm thấy trong một số loại cá, bơ và các loại hạt cũng là những lựa chọn tốt cho chế độ ăn có lợi cho tim. Tuy nhiên, hầu hết các loại chất béo đều có lượng calo cao nên cần lưu ý sử dụng chất béo một cách điều độ.\r\n\r\nMột số chất béo nên sử dụng:\r\n\r\nDầu oliu.\r\nDầu hạt cải.\r\nMargarine không béo chuyển đổi.\r\nTrái bơ\r\nMột số chất béo nên hạn chế:\r\nBơ sữa.\r\nMỡ heo.\r\nNước sốt thịt.\r\nSốt kem.\r\nBơ cacao.\r\nDầu cọ, dầu dừa.\r\n\r\n5. Chọn nguồn đạm ít béo\r\nMột số thực phẩm cung cấp nguồn protein tốt nhất cho bạn như thịt nạc, thịt gia cầm và cá, các sản phẩm từ sữa ít béo và trứng những loại ít chất béo hơn, chẳng hạn như sữa tách kem thay vì sữa nguyên chất và ức gà không da thay vì gà rán.\r\n\r\nCá là một lựa chọn lý tưởng thay cho các loại thịt nhiều chất béo. Các loại cá giàu axit béo omega-3 ví dụ như cá hồi, cá thu và cá trích, giúp làm giảm triglyceride trong máu.\r\n\r\nNhững nguồn đạm ít chất béo khác là hạt lanh, quả óc chó, đậu nành và dầu hạt cải. Các loại đậu như đậu Hà Lan và đậu lăng chứa nguồn protein phong phú, ít chất béo và không có cholesterol. Ngoài ra, bạn cũng nên hạn chế sử dụng protein từ động vật để giảm lượng chất béo, cholesterol và tăng lượng chất xơ.\r\n\r\n6. Giảm lượng muối trong thức ăn\r\nĂn nhiều muối có thể góp phần gây ra huyết áp cao, nguy cơ cao bị mắc các bệnh tim mạch. Để có một trái tim khỏe mạnh, bạn nên giảm lượng muối tiêu thụ trong chế độ ăn hàng ngày. Hiệp hội Tim mạch Hoa Kỳ khuyến nghị rằng: đối với những người trưởng thành khỏe mạnh nên sử dụng không quá 2.300 miligam (mg) muối mỗi ngày (khoảng một muỗng cà phê muối). Mức lý tưởng nhất là dưới 1.500mg muối mỗi ngày.\r\n\r\n7. Lên kế hoạch: lập thực đơn hàng ngày\r\nĐể có một thực đơn hữu ích cho tim mạch, bạn nên lên kế hoạch mỗi ngày. Trước hết xác định những thực phẩm nào cần có trong chế độ ăn uống lành mạnh cho tim và những loại nào nên hạn chế.\r\n\r\nXây dựng thực đơn hàng ngày thông qua sáu lời khuyên được liệt kê ở trên. Hãy ưu tiên rau, trái cây và ngũ cốc khi lựa chọn thực phẩm cho mỗi bữa ăn chính và bữa ăn nhẹ. Hạn chế các thực phẩm mặn, nhiều muối, nên chọn các nguồn protein nạc và chất béo lành mạnh. Ngoài ra, thường xuyên thay đổi menu cho các bữa ăn để thêm phần đa dạng và thú vị hơn. Điều này giúp đảm bảo cơ thể bạn được cung cấp đầy đủ các loại chất dinh dưỡng cần thiết.\r\n\r\n8. Đôi khi, hãy cho phép bản thân được ăn thoả thích\r\nĐôi khi, hãy cho phép bản thân được ăn thoải mái. Mặc dù một thanh kẹo hay một ít khoai tây chiên sẽ không làm hỏng chế độ ăn uống lành mạnh của bạn, nhưng đừng để nó biến thành cái cớ để từ bỏ kế hoạch ăn uống. Điều quan trọng là bạn ăn thực phẩm lành mạnh hàng ngày, hạn chế những thực phẩm không tốt cho tim mạch.\r\n\r\nHãy bắt tay lên kế hoạch và kết hợp thực hiện theo tám lời khuyên này vào cuộc sống, bạn sẽ cảm thấy rằng ăn uống lành mạnh cho tim vừa dễ dàng thực hiện mà lại không kém phần thú vị.\r\n\r\nVà cuối cùng, bạn có thể tham khảo ý kiến của các chuyên gia dinh dưỡng, đặc biệt là dinh dưỡng tim mạch để có chế độ ăn thích hợp.','Dinh dưỡng','vinmec.commmmmmmmmmm','Chế độ ăn cho người bệnh','7.jpg',1,'2020-08-11 21:57:05.975'),(34,'Chế độ dinh dưỡng cho bệnh nhân thận từng giai đoạn','Chế độ dinh dưỡng là một vấn đề quan trọng đối với bệnh nhân thận. Chế độ dinh dưỡng khoa học, hợp lí sẽ hỗ trợ tối đa quá trình điều trị bệnh thận. Ở mỗi giai đoạn khác nhau của bệnh thận lại cần một chế độ dinh dưỡng riêng, một loại sữa tốt cho người bệnh thận riêng biệt. Trong bài viết dưới đây sẽ giúp bạn tìm hiểu về chế độ dinh dưỡng theo từng giai đoạn của bệnh thận, đặc biệt là mách bạn loại sữa tốt cho người bệnh thận trước và sau lọc máu.\r\n\r\nThận là cơ quan đảm nhận nhiều chức năng sinh lý quan trọng của cơ thể bao gồm: đào thải các sản phẩm chuyển hóa (ure, creatinin, acid uric…- sản phẩm chuyển hóa của protein), điều chỉnh cân bằng nước và điện giải, cân bằng toan kiểm, sản xuất một số yếu tố nội tiết…Khi thận bị tổn thương, các chức năng của thận bị suy giảm, dẫn tới hàng loạt biến đổi về sinh hóa và lâm sàng của các cơ quan trong cơ thể.\r\nCác chuyên gia dinh dưỡng khuyến cáo rằng: Ở 2 giai đoạn trước và sau lọc máu, người bệnh thận cần một chế độ dinh dưỡng khác nhau để đảm bảo hỗ trợ tốt nhất cho quá trình điều trị bệnh.\r\nGiai đoạn trước khi lọc máu\r\nĐặc điểm của bệnh thận trong giai đoạn này: Thận bị tổn thương, chức năng thận bị suy giảm từ nhẹ đến nặng; Bệnh nhân có ure máu tăng, rối loạn cân bằng nước, điện giải; Chế độ ăn cần ngăn ngừa và hạn chế tăng urê máu, làm chậm bước tiến của quá trình suy thận và giảm ảnh hưởng thứ phát của bệnh thận. Các bác sĩ khuyến cáo chung về chế độ ăn: Ít Protein: Lượng protein từ 0.2-0.8g/kg/ngày. Dùng protein có giá trị sinh học cao, hạn chế thức ăn nhiều phosphat. Cung cấp chế độ dinh dưỡng cho cơ thể giàu năng lượng: 35-40kcal/kg/ngày; đủ vitamin, yếu tố vi lượng, chống thiếu máu; đảm bảo đủ nước, ít muối, giàu calci, ít phosphat.\r\n\r\nSữa tốt cho người bệnh thận ở giai đoạn này được khuyến cáo là Nepro 1. Thành phần dinh dưỡng của Nepro 1 đã được chứng minh và kiểm nghiệm là sản phẩm dinh dưỡng cho người bệnh thận trước lọc máu. Bởi Nepro 1 là sản phẩm dinh dưỡng giảm Proteindành cho người bệnh thận có urê huyết tăng; Ít natri, ít kali, ít phospho; Giàu dinh dưỡng, năng lượng; đầy đủ vitamin và khoáng chất (đặc biệt calci, các yếu tố tạo máu); Tốt cho tiêu hóa; Không có Cholesterol, không đường lactose.\r\nGiai đoạn sau lọc máu\r\nĐặc điểm của bệnh thận trong giai đoạn này: Thận bị suy hoàn toàn, chức năng thận rất kém, cần thực hiện chạy thận nhân tạo hoặc lọc màng bụng (lọc máu ngoài thận) để loại bỏ chất độc trong máu. Sau chu kỳ lọc, chỉ số sinh hóa (Urê, creatinin,…), yếu tố nội môi được điều chỉnh tốt và giảm đến mức an toàn. Khi đến trước chu kỳ, các chỉ số ure, creatinin máu lại tăng, nội môi lại bị rối loạn (nhiều hay ít phụ thuộc chế độ ăn uống). Quá trình lọc máu làm mất một lượng protein của cơ thể àcơ thể có nguy cơ suy dinh dưỡng do thiếu đạm. Ngoài ra máy lọc còn làm vỡ hồng cầuàthiếu máu. Các chuyên gia dinh dưỡng khuyến cáo, ở giai đoạn này, bệnh nhân thận nên bổ sung thêm protein bên cạnh những yêu cầu về năng lương, vitamin, khoáng chất.\r\nNepro 2 là loại sản phẩm dinh dưỡng tốt dành cho bệnh nhân thận giai đoạn sau lọc máu. Thành phần của Nepro giàu Proteindành cho người bệnh thận có lọc máu ngoài thận; Ít natri, ít kali, ít phospho; Giàu dinh dưỡng và năng lượng, được xem là loại sản phẩm tốt nhất cho bệnh nhân thận giai đoạn sau lọc máu trên thị trường sữa dinh dưỡng Việt Nam.\r\nDinh dưỡng giàu protein?\r\nCùng với thực đơn tăng đạm giảm muối mỗi ngày, sau mỗi giờ nằm lọc máu 1 ly Nepro 2 thật sự cần thiết với người bệnh. Sản phẩm cung cấp nguồn đạm quý có giá trị sinh học cao, dễ hấp thu và an toàn với bệnh nhân thận. Với chất xơ tự nhiên FOS giúp tiêu hóa tốt hơn bên cạnh đó Nepro 2 hoàn toàn không có cholesterol, cải thiện thành phần lipid máu phòng tránh các bệnh tim mạch và cao huyết áp.\r\nCung cấp đầy đủ dưỡng chất, thể dục hàng ngày, tinh thần luôn lạc quan là một trong những cách để người bệnh tăng cường sức khỏe “chiến đấu” lâu dài với căn bệnh nan y này, để mỗi ngày qua đi sẽ làm thêm được nhiều việc cho cuộc sống ý nghĩa hơn','Dinh dưỡng','vitadairy.vnnnnnnnnnnn','Chế độ ăn cho người bệnh','8.png',1,'2020-08-11 21:57:05.975'),(35,'CHẾ ĐỘ ĂN CHO NGƯỜI BỊ XƠ GAN','Một người bị bệnh gan có thể không nhận đủ năng lượng từ thức ăn, dẫn tới suy dinh dưỡng, giảm cân, teo cơ nhưng tình trạng này có thể điều chỉnh được với chế độ ăn uống phù hợp. Với những người bị bệnh xơ gan, điều quan trọng là cần duy trì chế độ ăn uống tốt để phục hồi chức năng gan.\r\n\r\n	Trong giai đoạn đầu của bệnh xơ gan, chế độ ăn kiêng đặc biệt không cần thiết mà nên ăn uống cân bằng và duy trì cân nặng khỏe mạnh. Nên ăn tất cả các nhóm thực phẩm đa dạng bao gồm gạo, ngũ cốc, trái cây và rau quả, các loại đậu và thịt nạc. Mỗi nhóm thực phẩm đều cung cấp cho cơ thể các chất dinh dưỡng thiết yếu, vì vậy, nên ăn đầy đủ, đặc biệt là protein như đậu và thịt. Trước khi đi ngủ, nên ăn nhẹ để tránh hạ đường huyết ban đêm.\r\n\r\n	Trong giai đoạn xơ gan tiến triển hơn thì cần có chế độ ăn kiêng đặc biệt. Các triệu chứng như chán ăn, buồn nôn, mệt, phù chân, bụng to do cổ trướng (bụng có nước), ... sẽ xuất hiện thường xuyên khiến người bệnh khó chịu. Việc ăn uống sẽ trở nên khó khăn, đặc biệt khi ăn thức ăn nhiều đạm (protein), vì vậy, cơ thể không được cung cấp đủ các dưỡng chất cần thiết. Chế độ ăn uống không đầy đủ kèm theo sự suy giảm chức năng gan gây sự suy dinh dưỡng và teo cơ (nhất là ở cánh tay, quanh vai, ngực và lưng). Vì vậy, khi đang ở giai đoạn này của xơ gan, người bệnh cần sự tư vấn từ bác sĩ hoặc chuyên gia dinh dưỡng.\r\n\r\nChế độ ăn cho người bị xơ gan\r\n\r\n	Chế độ ăn giàu đạm: Nếu được tư vấn một chế độ giàu protein, thực phẩm nên có trong các bữa ăn bao gồm: thịt lợn, thịt gia cầm, hải sản, trứng, sữa, sữa chua, pho-mai, các loại hạt, đỗ, các loại đậu và sản phẩm từ đậu nành. Các bác sĩ có thể đề nghị bạn bổ sung protein đặc biệt như acid amin chuỗi nhánh BCAA (branched-chain amino acids) dưới dạng các chế phẩm chuyên biệt cho gan.\r\n\r\n	Chế độ ăn ít muối: Nếu bác sĩ tư vấn chế độ ăn ít muối thì không nên thêm muối vào các món ăn thường ngày và cũng nên tránh các thức ăn hoặc thực phẩm chế biến nhiều muối và gia vị như cà muối, dưa muối,.... Chế độ ăn nhiều muối sẽ làm cơ thể giữ nhiều nước, gây phù và dịch sẽ tích lũy trong bụng.\r\n\r\n	Nước uống: Hầu hết người xơ gan không cần hạn chế uống nước, trừ khi lượng natri của cơ thể ở mức dưới 125mmol/L. Thường bác sĩ sẽ yêu cầu hạn chế uống nước hàng ngày khi xơ gan tiến triển. Kể cả khi hạn chế nước, lượng nước tối thiểu hàng ngày bạn uống vẫn phải đạt 800- 1000ml.\r\n\r\n	Chế độ nhiều bữa nhỏ: Hầu hết những người mắc bệnh gan đang tiến triển nên ăn nhiều bữa nhỏ mỗi ngày, nhất là khi đã có tình trạng chán ăn và ăn ít. Bệnh nhân có thể ăn 6- 8 bữa ăn nhỏ mỗi ngày và có 1 bữa ăn nhẹ gồm ít đạm và carbohydrate (đường phức) như cơm, bún, miến, khoai... trước khi đi ngủ. Nên nhớ là không bỏ ăn quá 7- 8 tiếng.\r\n\r\n	Bên cạnh đó, thay đổi lối sống đóng một vai trò quan trọng trong làm chậm tiến triển bệnh xơ gan và tăng cường sức khỏe tổng thể. Bệnh nhân xơ gan cần tích cực:\r\n	Duy trì trọng lượng khỏe mạnh: Một số dạng bệnh gan bao gồm xơ gan liên quan đến sự tích tụ chất béo.\r\n\r\n	Giảm nguy cơ bị viêm gan: Không dùng chung kim tiêm, đảm bảo rằng bạn đã chủng ngừa viêm gan B và C đồng thời phải nhận thức được nguy cơ viêm gan khi đi du lịch nước ngoài.\r\n\r\n	Tránh nhiễm khuẩn: Xơ gan làm giảm sức đề kháng chống lại tình trạng nhiễm trùng. Tránh những người bị bệnh lây nhiễm và rửa tay thường xuyên.\r\n\r\n	Không hút thuốc lá: Các thành phần có trong khói thuốc lá chất độc cho gan.\r\n\r\n	Tránh các hành vi tình dục nguy hiểm: Tránh có nhiều bạn tình và quan hệ tình dục không được bảo vệ vì có thể làm tăng nguy cơ viêm gan do virut và các bệnh nhiễm trùng khác.\r\n\r\n	Quản lý tốt các chỉ số sức khỏe khác: Kiểm soát huyết áp, cholesterol và đường máu bởi vì tất cả các rối loạn các chỉ số vừa nêu đều có thể gây hại cho gan.\r\n\r\n	Tránh các loại thuốc có hại: Chú ý tránh các thuốc được chuyển hóa qua gan vì một số loại thuốc có thể làm nặng tình trạng bệnh gan đang sẵn có.','Dinh dưỡng','soyte.namdinh.gov.vn/','Chế độ ăn cho người bệnh','9.jpg',1,'2020-08-11 21:57:05.975'),(36,'Huyết áp cao nên ăn gì?','ĐỐI TƯỢNG\r\nTheo Tổ chức Y tế Thế giới (WHO), mỗi năm có 17,5 triệu người chết do các bệnh tim mạch trên thế giới, nhiều hơn gấp 4 lần tổng số người tử vong của 3 bệnh lý HIV/AIDS, sốt rét và lao phổi. Trong đó, bệnh nhân tử vong vì tăng huyết áp và biến chứng của tăng huyết áp trên 7 triệu người.\r\n\r\nTăng huyết áp là nguyên nhân gây suy tim, đột quỵ não, nhồi máu cơ tim cấp dẫn đến cái chết của hàng triệu người mỗi năm. Đáng lưu ý rằng tỷ lệ người mắc tăng huyết áp ngày càng tăng và tuổi mắc bệnh cũng ngày càng trẻ hóa.\r\n\r\nNăm 2000, toàn thế giới có tới 972 triệu người bị tăng huyết áp và ước tính đến năm 2025 sẽ lên đến 15,6 tỷ người.\r\n\r\nTại Việt Nam, năm 2000 có khoảng 16,3% người lớn bị tăng huyết áp, đến năm 2009 tỷ lệ tăng huyết áp ở người lớn là 25,4% và năm 2016 con số này lên đến 48%, mức báo động đỏ trong thời điểm hiện tại.\r\n\r\nPGS.TS.BS Lê Bạch Mai, Nguyên Phó Viện trưởng Viện Dinh dưỡng Quốc gia, Giám đốc Y khoa miền Bắc Hệ thống Trung tâm Dinh dưỡng – Y học Vận động Nutrihome cho biết, bất kỳ ai cũng có nguy cơ bị tăng huyết áp, kể cả trẻ em và người trẻ tuổi. Tăng huyết áp còn được gọi là “kẻ giết người” thầm lặng vì bệnh thường không có biểu hiện hay triệu chứng cảnh báo rõ rệt và thậm chí có người còn không biết mình bị bệnh. Có người thấy đau đầu, buồn nôn, chóng mặt, đi khám phát hiện mắc bệnh; nhưng có người phát hiện bệnh tăng huyết áp thì đã có biến chứng ở tim, mắt, thận, mạch máu não…\r\n\r\n“Vì thế, dù ở độ tuổi nào, chế độ ăn uống lành mạnh để nâng cao sức khỏe, tăng cường vận động – tập luyện thể dục thể thao, sinh hoạt điều độ sẽ góp phần giảm thiểu nguy cơ tăng huyết áp cũng như phòng ngừa các bệnh tim mạch”, PGS.TS.BS Lê Bạch Mai nhấn mạnh.\r\n\r\nTHÔNG TIN CHUNG\r\nMột người được xác định là bị tăng huyết áp khi huyết áp tâm thu ≥ 140 mmHg và/hoặc huyết áp tâm trương ≥ 90 mmHg.\r\n\r\nTăng huyết áp giai đoạn đầu thường không có triệu chứng hoặc ít triệu chứng với các biểu hiện không rõ rệt như: nhức đầu, chóng mặt, đỏ phừng mặt, dị cảm, tê tay chân, buồn nôn, nôn…\r\n\r\nYếu tố nguy cơ dẫn đến tăng huyết áp\r\n\r\nKhoảng trên 90% bệnh nhân tăng huyết áp không có nguyên nhân nên được gọi là tăng huyết áp nguyên phát và khoảng 10% trường hợp còn lại có thể do một số nguyên nhân (tăng huyết áp thứ phát). Và các yếu tố nguy cơ dẫn đến tăng huyết áp bao gồm:\r\n\r\nTuổi tác: Nguy cơ tăng huyết áp càng cao khi nam giới > 55 tuổi và nữ giới > 65 tuổi.\r\nTiền sử gia đình: Huyết áp cao có xu hướng di truyền, nên bạn hãy quan tâm sức khỏe nếu có người thân (cha mẹ, anh chị em ruột) bị căn bệnh này.\r\nThừa cân hoặc béo phì: Chỉ số BMI của bạn càng cao, cơ thể càng cần nhiều máu để cung cấp oxy và chất dinh dưỡng cho các mô. Khi thể tích máu lưu thông qua các mạch máu tăng lên, áp lực lên thành động mạch cũng tăng theo.\r\nKhông hoạt động thể lực: Những người ít vận động thường có nhịp tim cao hơn. Nhịp tim càng cao, tim càng phải hoạt động mạnh với mỗi cơn co thắt và lực tác động lên động mạch càng mạnh.\r\nHút thuốc lá\r\nChế độ dinh dưỡng không cân đối: chẳng hạn ăn nhiều muối (natri) khiến cơ thể giữ nước, dẫn đến tăng huyết áp; ăn quá ít kali khiến cơ thể tích lũy quá nhiều natri trong máu do kali giúp cân bằng lượng natri trong các tế bào.\r\nUống nhiều rượu\r\nStress: Thường xuyên căng thẳng là nguyên nhân dẫn đến tăng huyết áp tạm thời.\r\nMột số bệnh mạn tính: bệnh thận mạn, đái tháo đường, xơ vữa động mạch, béo phì…\r\nKhiếm khuyết bẩm sinh trong các mạch máu (hẹp động mạch) và chứng ngưng thở khi ngủ\r\nSử dụng một số loại thuốc: thuốc tránh thai, thuốc cảm, thuốc thông mũi, thuốc giảm đau không kê đơn và một số loại thuốc theo toa\r\nBiến chứng của bệnh tăng huyết áp\r\n\r\nTình trạng tăng huyết áp nếu không được kiểm soát có thể dẫn đến các biến chứng như:\r\n\r\nNhồi máu cơ tim hoặc đột quỵ: Tăng huyết áp gây xơ cứng và làm dày lên các động mạch (xơ vữa động mạch), dẫn đến thiếu máu cơ tim và nặng hơn là nhồi máu cơ tim, đột quỵ hoặc các biến chứng nguy hiểm khác.\r\nVỡ phình động mạch: Khi huyết áp tăng lên khiến các chỗ phình động mạch bẩm sinh sẵn có vỡ ra, đe dọa tính mạng bệnh nhân.\r\nSuy tim: Để bơm máu chống lại áp lực cao trong các mạch máu, tim phải làm việc nhiều hơn. Điều này làm cho các thành của buồng bơm dày lên (phì đại thất trái) và dẫn đến suy tim.\r\nSuy thận do nguy cơ thu hẹp động mạch thận vì bị tăng huyết áp.\r\nXuất huyết võng mạc mắt gây suy giảm thị lực thậm chí gây mù lòa.\r\nSa sút trí tuệ do di chứng tai biến mạch máu não (xuất huyết não, nhũn não)\r\nTầm quan trọng của chế độ dinh dưỡng trong phòng ngừa tăng huyết áp','Dinh dưỡng','nutrihome.vnnnnnnnnnn','Chế độ ăn cho người bệnh','10.jpg',1,'2020-08-11 21:57:05.975'),(37,'Dinh dưỡng để người cao tuổi luôn khỏe mạnh','Người cao tuổi cần một chế độ dinh dưỡng đa dạng và hợp lý để tránh bệnh tật. Nhưng đôi khi họ quên cả ăn, uống nước làm cho sức khỏe càng kém hơn. Vì vậy, những người trong gia đình cần chú ý quan tâm, chăm sóc người cao tuổi. Để người cao tuổi luôn khỏe mạnh, cần chú ý đến những điều sau.\r\nCần giảm khẩu phần ăn so với thời trẻ, trước hết là ăn giảm chất đường bột\r\n\r\n	Nhu cầu năng lượng ở người 60 tuổi giảm đi 20%, ở người trên 70 tuổi giảm đi 30% so với người 25 tuổi. Theo nhu cầu khuyến nghị của người Việt Nam, với người cao tuổi nhu cầu về năng lượng là từ 1700-1900 kcal/người/ngày. Năng lượng từ ngũ cốc cung cấp 68%, các chất béo cung cấp 18% và các chất đạm cung cấp 14% tổng nhu cầu năng lượng trong khẩu phần ăn hàng ngày. Người cao tuổi cần điều chỉnh chế độ ăn để giữ cân nặng ổn định, để chỉ số BMI từ 18,5-22,9.\r\n\r\n	Về chế độ ăn của người cao tuổi cần lưu ý: Chia bữa ăn thành nhiều bữa nhỏ, tạo không khí vui vẻ thoải mái. Khi ăn, cần nhai chậm nhai kỹ thức ăn. Nên ăn các thức ăn thực vật như: Vừng, lạc, đậu đỗ, rau xanh và hoa quả chín, ăn ít thịt thay vào đó là cá, tôm. Chế biến các món hấp, luộc nhừ thay thế các món rán nướng. Nên ăn thay đổi thực đơn thường xuyên, tránh đơn điệu để bữa ăn sẽ ngon hơn. Các món ăn chế biến mềm, thái nhỏ, hầm kỹ để phù hợp với hàm răng yếu và dễ tiêu hóa. Không ăn quá no, nhất là vào buổi tối, sau khi ăn xong nên ngồi hoặc đi lại nhẹ nhàng trong vòng 30 phút sẽ giúp dạ dày nhào trộn thức ăn và dễ tiêu.\r\n\r\nĂn giảm thịt, giảm chất béo và giảm muối\r\n\r\n	Về chất đạm, nhu cầu Protein từ 60-70 gam/ngày, trong đó đạm động vật chiếm 30% tổng số Protein. Người cao tuổi ăn ít thịt, thay vào đó là các thực phẩm giàu can xi như: Cá, tôm, cua (100 g tép chứa 910 mg can xi, 100 g cua chứa 5040 mg can xi). Các Protein thực vật như đậu đỗ, vừng lạc, đậu phụ vì chúng có nhiều chất xơ giúp thải lượng Cholesterol. Hạn chế ăn các thực phẩm có nhiều Cholesterol như: nội tạng động vật, óc. Nên ăn ít nhất 3 bữa cá/1 tuần, 3 quả trứng/1 tuần và ăn thêm sữa chua (dễ tiêu và có lợi cho tiêu hóa).\r\n\r\n	Về chất béo, nên ăn cả dầu thực vật và mỡ động vật, tỷ lệ chất béo thực vật chiếm 35% tổng lượng chất béo. Dầu thực vật tốt với người có tăng huyết áp, không có Cholesterol và ít Acid béo bão hòa (dưới 40%) hơn mỡ động vật.\r\n\r\n	Ngoài giảm cơm, người cao tuổi cần chú ý giảm thịt, giảm mỡ, giảm đường. Thịt tính bình quân không vượt quá 1,5kg đầu người trong một tháng, mỡ/dầu dưới 600g, đường dưới 500g.\r\n\r\n	Ngoài ra, ăn hạn chế các thực phẩm có hàm lượng muối cao như các loại dưa cà muối. Hạn chế những đồ ăn, thức uống gây mất ngủ như: Cà phê, chè đặc. Hạn chế ăn mặn, lượng muối ăn dưới 150g/người/tháng, vì ăn muối nhiều có liên quan đến bệnh tăng huyết áp.\r\n\r\nĂn thêm đậu, lạc, vừng và cá\r\n\r\n	Ở người có tuổi, tiêu hóa hấp thụ chất đạm đều kém nên dễ xảy ra tình trạng thiếu chất đạm. Ở đậu, lạc, vừng và cá có nhiều chất đạm, ngoài ra chúng lại có nhiều chất dầu giúp đề phòng các bệnh về tim mạch. Cho nên người cao tuổi nên ăn nhiều món ăn từ đậu tương như đậu phụ, sữa đậu nành, tào phớ.\r\n\r\n	Mỗi gia đình nên có một lọ vừng lạc để bổ sung cho bữa ăn hằng ngày. Mỗi tuần ăn 2-3 bữa cá. Nên ăn cá nhỏ, kho nhừ hai lửa để ăn được cả xương có thêm canxi đề phòng bệnh xốp xương ở người cao tuổi. Ðậu, lạc, vừng, cá có tác dụng phòng, chống các bệnh tim mạch; đậu phụ còn có tác dụng phòng chống ung thư.\r\n\r\nĂn nhiều rau tươi, quả chín\r\n\r\n	Người có tuổi cần chú ý ăn nhiều rau để có chất xơ kích thích nhu động ruột, tránh táo bón. Các chất xơ trong rau quả còn có tác dụng như cái chổi quét hết các chất bổ béo thừa đẩy ra theo phân, giúp cơ thể phòng chống bệnh xơ vữa động mạch. Rau tươi, quả chín còn cung cấp các chất dinh dưỡng hết sức quan trọng đối với người cao tuổi là các vitamin và chất khoáng.\r\n\r\n	Nhu cầu chất xơ 25g/ngày, đặc biệt là chất xơ hòa tan có tác dụng làm giảm Cholesterol và đường máu, nó tốt với người tiểu đường, tăng huyết áp. Người cao tuổi thường bị loãng xương và thiếu các vitamin, khoáng chất. Cần ăn các loại rau xanh hoa quả giàu vitamin và khoáng chất, mỗi ngày nên ăn 300g rau xanh và 100g hoa quả.\r\n\r\nUống đủ nước theo nhu cầu\r\n\r\n	Người cao tuổi thường thường uống ít nước do sợ đi tiểu nhiều, mất ngủ. Nước giúp cho tiêu hóa tốt hơn và đào thải các chất cặn bã của cơ thể. Uống từ 1.5-2 lít nước/ một ngày, cần chủ động uống nước không chờ khát mới uống. Nên uống nước trà xanh tốt cho tim mạch và các loại thức uống có tác dụng an thần như hạt sen, chè ngó sen...\r\n\r\n	Người cao tuổi nên sinh hoạt điều độ, ăn, ngủ đúng giờ. Để làm chậm quá trình lão hóa và duy trì cân nặng hợp lý bằng việc tập thể dục nhẹ nhàng hàng ngày. Mỗi buổi sáng dậy vận động 30 phút, trưa nằm nghỉ hoặc ngủ 15 phút, tối nên đi bộ 30 phút giúp ngủ ngon hơn. Ði bộ là cách vận động tốt nhất, phù hợp với người cao tuổi và có thể phòng bệnh xơ cứng động mạch, và cải thiện tình trạng đau mỏi xương.\r\n\r\n	Tóm lại, người cao tuổi nên có một chế độ dinh dưỡng hợp lý, tinh thần thoải mái, thực hiện chế độ vận động vừa sức, phù hợp với sức khỏe và tuổi tác của từng người để có tuổi thọ tốt hơn. Hiện tượng lão hóa không thể dừng lại theo thời gian, nhưng nếu biết vận dụng thời gian để tập thể dục thì chẳng những sức khỏe của người cao tuổi sẽ cải thiện mà còn làm quá trình lão hóa chậm hơn. Bên cạnh đó, mỗi gia đình tiếp tục phát huy truyền thống đạo đức dân tộc trong việc tôn trọng, chăm sóc người cao tuổi.\r\n\r\n	Việt Nam đang nằm trong top 5 quốc gia có tốc độ già hóa dân số nhanh nhất thế giới với những tác động mạnh mẽ đối với sự phát triển kinh tế - xã hội. Theo kết quả điều tra quốc gia về người cao tuổi năm 2011 cho thấy, hơn 60% số người cao tuổi có tình trạng sức khỏe yếu hoặc rất yếu cần người chăm sóc. Các bệnh mãn tính thường gặp ở người cao tuổi là bệnh mạch vành, tăng huyết áp, đột quỵ, đái tháo đường, ung thư, tắc nghẽn mạch phổi, thoái hóa khớp, loãng xương, sa sút trí tuệ... phải điều trị suốt đời. Do đó, việc chăm sóc sức khỏe người cao tuổi trong bối cảnh hiện nay rất quan trọng, đòi hỏi cả xã hội phải chung tay vào cuộc, góp phần phát huy vai trò của người cao tuổi và thích ứng với quá trình già hóa dân số.','Dinh dưỡng','viendinhduong.vnnnnnnnn','Dinh dưỡng cho người cao tuổi','dinhduongchotre3tuoi.jpg',1,'2020-08-11 21:57:05.975'),(38,'Chế độ dinh dưỡng hợp lý cho người cao tuổi','Với người cao tuổi, bảo vệ sức khỏe có vai trò rất quan trọng. Một chế độ dinh dưỡng hợp lý sẽ giúp họ nâng cao sức đề kháng, phòng tránh nhiều bệnh tật. Ngược lại, khi chế độ dinh dưỡng thiếu hay thừa các chất đều có thể gây ra các loại bệnh nghiêm trọng\r\n\r\nNgười lớn tuổi thường có nguy cơ thiếu hụt dinh dưỡng. Tình trạng dinh dưỡng kém thường là do chán ăn, ăn quá ít hoặc chế độ ăn không đủ dinh dưỡng. Trên thực tế còn có thể do nhiều nguyên nhân khác về thể chất, tâm lý và xã hội. Dinh dưỡng kém ở người già có thể dẫn đến những vấn đề sức khỏe nghiêm trọng như: Hệ thống miễn dịch yếu làm tăng nguy cơ nhiễm trùng, dễ mắc bệnh; Vết thương lâu lành; Suy dinh dưỡng cũng làm mất lớp cơ, loãng xương có thể dẫn đến ngã và gãy xương\r\nChế độ ăn các chất dinh dưỡng đầy đủ và cân đối\r\n\r\nNgười cao tuổi ít hoạt động so với thời trẻ. Các chức năng cơ quan suy giảm, nhu cầu năng lượng cũng giảm.  Bữa ăn của người cao tuổi nên cân bằng tinh bột, chất đạm, chất béo, vitamin và khoáng chất; uống đủ nước; không ăn mặn, hạn chế thực phẩm chế biến lại; tránh xa các chất có hại…\r\n\r\n- Chất đạm: Người già dễ bị thiếu hụt đạm do việc tiêu hóa, hấp thụ đạm kém, dẫn đến gầy yếu, trí nhớ kém, suy giảm hệ thống miễn dịch… Nên bổ sung thực phẩm như thịt, cá trứng sữa, lạc, vừng, sản phẩm chế biến từ đậu.\r\n\r\n- Tinh bột và chất béo: Đối với tinh bột, nên ăn ở mức vừa phải. Mỗi bữa chỉ nên ăn 1-2 lưng bát cơm, ăn nhiều rau xanh, khoai củ để cung cấp chất xơ ngăn ngừa táo bón. Ngoài ra, nên ăn các món luộc, hấp, hạn chế các món xào, rán, chất béo từ mỡ động vật. Chất béo ưu tiên sử dụng chất béo chưa no như các loại dầu thực vật, hạn chế ăn mỡ động vật, bổ sung thêm omaga - 3 tốt cho hệ tim mạch.\r\n\r\n- Vitamin và khoáng chất: Nên bổ sung các loại vitamin nhóm B, C, D… và các khoáng chất quan trọng như sắt, canxi, kẽm… để tăng cường sức đề kháng.\r\n\r\nKhẩu phần và tần suất các bữa ăn\r\n\r\nSự bài tiết dịch vị trong dạ dày ở cơ thể người cao tuổi thường giảm đi, vì thế việc hấp thụ các chất như can-xi, sắt cũng trở nên kém hơn và quá trình tiêu hoá thức ăn cũng dài hơn. Khẩu phần bữa ăn nên cân đối 4 nhóm thực phẩm:đạm, đường, béo, vitamin và khoáng chất.\r\n\r\nViệc chia nhỏ các bữa ăn trong ngày giúp làm giảm áp lực cho dạ dày và giúp hệ tiêu hoá hoạt động dễ dàng hơn. Vì thế, người cao tuổi nên chia 3 bữa ăn chính xen kẽ 2-3 bữa phụ, đồng thời khoảng cách giữa các bữa ăn nên đều nhau và đúng giờ. Điều này càng có ý nghĩa hơn đối với những người cao tuổi mắc thêm bệnh tiểu đường.\r\n\r\nThực đơn gợi ý hàng ngày cho người cao tuổi\r\n\r\nNgười lớn tuổi nên nạp vào cơ thể ít calo hơn vì nhịp chuyển hóa của cơ thể đã chậm. So với lúc trẻ, các cụ thường giảm khối cơ bắp (mỗi năm giảm khoảng 200 g) vì ít hoạt động hơn trước. Giảm 200 – 400 calo/ngày sẽ đáp ứng với hiện tượng chuyển hóa chậm vì bớt hoạt động chân, tay. Một khẩu phần cho người \"có tuổi\" thường chỉ 1.600 calo/ngày là đủ.\r\n\r\nNgười cao tuổi nên ăn thành nhiều bữa ăn nhỏ xen kẽ các bữa ăn chính, khoảng ba bữa chính và xen kẽ 2 bữa phụ và nhận đủ dinh dưỡng như sau:\r\n\r\n- Chất bột đường: Người cao tuổi nên sử dụng ít cơm và ăn nhiều khoai củ để tăng chất xơ, chống táo bón, thải cholesterol thừa, đồng thời hạn chế nguy cơ tăng đường huyết và ung thư đại tràng.\r\n\r\n- Chất béo: Người cao tuổi cần hạn chế tối đa dung nạp mỡ lợn, da, óc, nội tạng vào cơ thể. Mỗi tuần chỉ ăn 2-3 quả trứng và dùng dầu thực vật như: dầu nành, dầu mè để không mắc bệnh béo phì, huyết áp. mỡ dưới 600 gam.\r\n\r\n- Chất đạm: Người cao tuổi vẫn cần cung cấp đủ chất đạm như: cá, đậu hũ, các loại đậu và sữa đậu nành. Tuy nhiên, cần hạn chế thịt lợn mỡ, đồng thời ăn xen kẽ các bữa thịt và cá trong tuần trung bình 1kg thịt, 2kg cá và 3kg đậu hũ một tháng là hợp lý.\r\n\r\n- Nên sử dụng sữa: Không chỉ trẻ nhỏ mới cần bổ sung dinh dưỡng mỗi ngày mà người cao tuổi cũng cần có nguồn thực phẩm này để bổ sung đầy đủ dưỡng chất, mỗi ngày, người cao tuổi nên uống ít nhất 1 ly sữa ít béo, ít đường để đảm bảo dinh dưỡng cho cơ thể.\r\n\r\n- Thường xuyên uống nhiều nước trong ngày, đảm bảo đủ lượng nước cơ thể cần.\r\n\r\n- Tránh ăn quá no và nhịn đói lâu, đồng thời giảm lượng đường, muối trong khẩu phần ăn hàng ngày muối dưới 300g/tháng, đường dưới 500g/tháng.\r\n\r\n- Người cao tuổi nên tăng cường ăn rau xanh, và quả chín ít ngọt rất tốt cho sức khỏe, nguồn thực phẩm này giúp người cao tuổi bổ sung thêm lượng vitamin, chất khoáng đồng thời giúp chống bệnh táo bón, hạn chế tăng đường huyết, kiểm soát cân nặng và chống lão hóa hiệu quả.\r\n\r\nChế độ dinh dưỡng của mỗi người cũng khác nhau tùy theo tình trạng sinh lý, mức đáp ứng dinh dưỡng. Khi cần tư vấn cụ thể về chế độ dinh dưỡng hợp lý, người cao tuổi có thể đến Phòng khám, tư vấn dinh dưỡng thuộc khoa Dinh dưỡng - Tiết chế - Bệnh viện đa khoa tỉnh Phú Thọ sẽ có bác sĩ tư vấn, cung cấp thông tin cụ thể về vấn đề dinh dưỡng liên quan tới bệnh lý của mình cũng như được cung cấp thực đơn mẫu miễn phí về thực hiện tại gia đình.\r\n\r\nBên cạnh chế độ dinh dưỡng, Thạc sĩ Lạn Hương cũng đưa ra lời khuyên cho các bác người cao tuổi là luôn giữ tinh thần lạc quan,yêu đời. Hệ thần kinh hoạt động tốt thì cơ thể sẽ tự điều chỉnh để thích nghi với sự thay đổi của tuổi già. Luôn giữ tâm hồn thanh thản không nghĩ ngợi nhiều; Duy trì chế độ ăn uống hợp lý kết hợp lối sống lành mạnh; Tập thể dục vừa sức hàng ngày đều đặn hai buổi sáng, tối; Tối ngủ không ngủ muộn hơn 22h nhưng cũng không nên đi ngủ sớm để đảm bảo duy trì được giấc ngủ sâu từ 11h đêm đến 3 giờ sáng, đây là thời gian cho các cơ quan bộ phận nghỉ ngơi đồng thời cũng là thời gian cơ thể thải đọc, tái tạo tế bào sẽ giúp người cao tuổi duy trì sức khỏe ổn đinh, tăng tuổi thọ.','Dinh dưỡng','benhviendakhoatinhphutho.vn','Dinh dưỡng cho người cao tuổi','12.jpg',1,'2020-08-11 21:57:05.975'),(39,'Dinh dưỡng cho người già: Ăn thế nào mới tốt?','Chế độ dinh dưỡng cho người già là một trong những yếu tố không thể bỏ qua khi bạn chăm sóc sức khỏe người cao tuổi. Nếu biết cách cân bằng chế độ ăn uống, người lớn tuổi không những có sức khỏe thể chất dẻo dai mà còn duy trì được đời sống tinh thần lạc quan.\r\n\r\nQuá trình lão hóa dẫn tới suy giảm chức năng tiêu hóa, tim mạch, hệ niệu… khiến sức khỏe và đời sống của người già có nhiều thay đổi, đặc biệt là về dinh dưỡng. Vì thế, bạn hãy cùng Hello Bacsi tìm hiểu về nhu cầu dinh dưỡng của người già để lựa chọn chế độ dinh dưỡng sao cho hợp lý nhé!\r\n\r\nYếu tố ảnh hưởng đến dinh dưỡng cho người già\r\nDinh dưỡng cho người già có nhiều khác biệt so với trẻ em, trẻ vị thành niên và người trưởng thành. Những thay đổi về dinh dưỡng liên quan đến độ tuổi có thể ảnh hưởng tới cách cơ thể hấp thụ thức ăn, nhu cầu dinh dưỡng cũng như khẩu vị. Sau đây là những yếu tố làm thay đổi chế độ dinh dưỡng cho người già bạn nên biết:\r\n\r\n1. Quá trình trao đổi chất chậm lại\r\nKhi bạn già đi, tốc độ trao đổi chất của cơ thể sẽ chậm hơn. Đặc biệt, nếu người già ít tập luyện thể thao và vận động, sự suy giảm trao đổi chất càng thể hiện rõ. Khi trao đổi chất chậm lại, cơ thể không đốt cháy nhiều calo, điều này cũng đồng nghĩa với việc người già nên giảm khẩu phần ăn để có thể kiểm soát cân nặng ở mức cho phép. Đồng thời, người già cũng nên ăn các thực phẩm giàu dinh dưỡng ít calo hơn.\r\nĐối với những người thường xuyên vận động thể chất, nhu cầu năng lượng trung bình đối với phụ nữ là 1.800 calo mỗi ngày và đối với nam giới là 2.300 calo mỗi ngày. Nếu bạn hơi gầy hơn, nhu cầu năng lượng sẽ thấp hơn một chút, nếu bạn vận động thể lực tích cực hơn sẽ cần nhiều năng lượng hơn một chút.\r\n\r\n2. Hệ tiêu hóa yếu dần đi\r\nNgười già thường suy giảm cơ nhai, răng yếu hoặc bị rụng, khiến việc nhai và nghiền nát thức ăn kém hiệu quả. Dạ dày người già yếu, trương lực và sức co bóp khả năng tiết dịch vị đều giảm cũng như nhu động ruột hoạt động kém, vì vậy người già dễ bị rối loạn tiêu hóa.\r\n\r\nCơ thể người già cũng tạo ra ít chất lỏng hơn. Chất lỏng rất cần thiết cho quá trình hấp thụ thức ăn của hệ tiêu hóa. Do đó, những thay đổi này có thể gây ra nhiều khó khăn cho việc hấp thụ các chất dinh dưỡng quan trọng như axit folic, vitamin B6 và vitamin B12.\r\n\r\n3. Vị giác suy giảm gây chán ăn\r\nVị giác của người ở tuổi già thường suy giảm. Bên cạnh đó, người già thường mắc một số bệnh cần điều trị bằng thuốc như bệnh mạn tính, do đó họ có thể phải uống các loại thuốc để kiểm soát vấn đề sức khỏe. Tuy nhiên, các loại thuốc điều trị thường có tác dụng phụ gây khó chịu ở dạ dày hoặc gây chán ăn dễ dẫn đến tình trạng thiếu chất dinh dưỡng.\r\n\r\n4. Tâm lý thay đổi thất thường\r\nNhiều người ở độ tuổi cao thường cảm thấy chán nản hoặc cô đơn và thường không hứng thú với ăn uống. Ngược lại, đôi lúc một số vấn đề về tâm trạng còn có thể khiến một số người ăn nhiều hơn và tăng cân không mong muốn.\r\n\r\nXây dựng chế độ dinh dưỡng cho người già\r\nMột chế độ dinh dưỡng cân đối, lành mạnh với đầy đủ dưỡng chất thiết yếu có thể giúp người già ngăn ngừa các vấn đề sức khỏe phổ biến như táo bón, vấn đề về tim mạch, tiểu đường, cao huyết áp, thừa cholesterol… Các thực phẩm giàu dinh dưỡng cũng giúp người già duy trì cân nặng khỏe mạnh và luôn tràn đầy năng lượng.\r\n\r\nViện lão khoa quốc gia Hoa Kỳ (The National Institute on Aging) đưa ra hai lựa chọn dinh dưỡng cho người già là chế độ dinh dưỡng USDA và chế độ dinh dưỡng DASH.\r\n\r\n• Chế độ dinh dưỡng USDA: Đây là chế độ ăn cân bằng và lành mạnh mà bạn cần lưu ý những điều sau đây:\r\n\r\n– Ăn ngũ cốc nguyên hạt.\r\n\r\n– Kiểm soát khẩu phần ăn vừa phải.\r\n\r\n– Nửa khẩu phần ăn là trái cây và rau quả.\r\n\r\n– Người già nên ăn nhạt, kiểm soát hàm lượng muối trong các món ăn như súp, bánh mì…\r\n\r\n• Chế độ dinh dưỡng DASH: Đây là chế độ dinh dưỡng tập trung vào tất cả các nhóm thực phẩm chủ chốt được thiết kế để giúp giảm huyết áp và tốt cho tim. Các loại thực phẩm bổ sung mỗi ngày bao gồm: sữa, rau, dầu ăn, ngũ cốc, hoa quả, thịt, đậu…\r\n\r\nBên cạnh việc lựa chọn chế độ dinh dưỡng cho người già, bạn cần biết cách chọn thực phẩm nào cần bổ sung vào để đạt được hiệu quả cao.\r\n\r\nThực phẩm bổ sung cho dinh dưỡng cho người già\r\nThực đơn cho người già cần đủ bốn nhóm thực phẩm chính (carbohydrate, chất đạm, chất béo, hoa quả và rau xanh) và các dưỡng chất cần thiết cho cơ thể. Chế độ dinh dưỡng cho người già nên bổ sung các loại thực phẩm dưới đây:\r\n\r\n• Chất béo lành mạnh: Người già cần bổ sung thực phẩm giàu chất béo tốt cho sức khỏe như các loại hạt, quả bơ, cá béo và dầu thực vật… đặc biệt là dầu oliu vào chế độ dinh dưỡng cho người già. Người già cần hạn chế tối đa chất béo bão hòa và chất béo chuyển hóa.\r\n\r\n• Nước lọc: Người già nên tích cực bổ sung đủ nước cho cơ thể. Ngoài ra, các loại trái cây, thực phẩm tốt cho sức khỏe và chứa nhiều nước như các món súp, dưa chuột, nho và dưa hấu rất tốt cho sức khỏe.\r\n\r\n• Ngũ cốc nguyên hạt: Ngũ cốc nguyên hạt rất giàu chất xơ, chất dinh dưỡng giúp hỗ trợ tiêu hóa và tốt cho tim. Bạn nên lựa chọn gạo lứt, ngũ cốc nguyên hạt, bánh mì nguyên hạt để bổ sung vào chế độ dinh dưỡng cho người già.\r\n\r\n• Thực phẩm giàu chất xơ: Người già cần bổ sung thực phẩm giàu chất xơ như trái cây, rau xanh và ngũ cốc nguyên hạt mỗi ngày. Điều này giúp ngăn ngừa táo bón, cung cấp vitamin, chất khoáng, chất xơ, các chất dinh dưỡng cần thiết cho cơ thể, duy trì cân nặng và giảm nguy cơ mắc bệnh tim.\r\n\r\n• Protein: Người già cần tăng cường bổ sung protein từ các loại đậu, trứng, thịt gà, cá và thịt nạc cũng như các loại hạt để cung cấp năng lượng cho cơ thể.\r\n\r\n• Canxi, vitamin D: Canxi là nguyên tố vi lượng đóng vai trò rất quan trọng, giúp người già có hệ xương chắc, khỏe. Vì vậy, chế độ dinh dưỡng cho người già cần bổ sung các thực phẩm giàu canxi và vitamin D.\r\n\r\n• Vitamin B12: Khả năng hấp thụ vitamin B12 của người già giảm đi, do đó chế độ dinh dưỡng cho người già cần tăng cường các thực phẩm giàu vitamin B12 như các loại ngũ cốc. Ngoài ra có thể bổ sung thêm vitamin B12 theo chỉ định của bác sĩ khi cần.\r\n\r\nNgười già cần có chế độ dinh dưỡng hợp lý, ăn ngủ điều độ và đúng giờ mới đảm bảo sức khỏe tốt. Bạn có thể linh hoạt chế biến món ăn đa dạng và hợp khẩu vị để cả gia đình cảm thấy ngon miệng nhé!','Mẹo hay','hellobacsi.commmmmmmmm','Mẹo hay chế biến nguyên liệu','13.jpg',1,'2020-08-11 21:57:05.975'),(40,'CHẾ ĐỘ DINH DƯỠNG CHO NGƯỜI CAO TUỔI','Chế độ dinh dưỡng cho người cao tuổi cần được quan tâm, chú trọng vì giai đoạn này, hệ tiêu hóa của họ bắt đầu suy giảm hiệu suất hoạt động, tuyến nước bọt ít… khiến người cao tuổi cảm thấy khó tiêu, ăn không ngon.\r\n\r\nLưu ý về chế độ dinh dưỡng cho người cao tuổi\r\nBên cạnh đó, nguy cơ mắc các bệnh tim mạch, đái tháo đường, máu nhiễm mỡ… rất cao, vì thế ngoài việc duy trì cơ thể khỏe mạnh, một chế độ dinh dưỡng hợp lý, khoa học sẽ giúp nâng cao sức khỏe, và có thể giúp kéo dài tuổi thọ một cách đáng kể.\r\n\r\nNếu ở người trẻ tuổi mỗi ngày cần 2.500 Kcal thì khi đã 60 tuổi chỉ cần 2.000 Kcal và 70 tuổi chỉ cần 1.800 Kcal là đủ.\r\n\r\nVới người già, cần chia thức ăn thành nhiều bữa nhỏ, ăn chậm, nhai kỹ thức ăn. Nên tăng các thức ăn có nguồn gốc thực vật (vừng, lạc, đậu đỗ, rau xanh và quả chín), giảm lượng thịt và thay bằng cá. Chế biến các món hấp, luộc thay thế các món rán, nướng.\r\n\r\nThường xuyên thay đổi thực đơn, tránh đơn điệu, chú ý các món ăn mềm, thái nhỏ hầm kỹ, các món canh chất lượng dễ tiêu.\r\n\r\nKhông ăn quá no, nhất là vào buổi tối, vì khi nằm, dạ dày căng to sẽ đẩy cơ hoành lên chèn ép cản trở hoạt động của tim.\r\n\r\nSau khi ăn xong, nên ngồi hoặc đi lại nhẹ nhàng trong vòng 30 phút nhằm giúp dạ dày nhào trộn tiêu hóa thức ăn để chuyển xuống ruột non dễ dàng hơn.\r\n\r\nTrứng là thực phẩm có nhiều cholesterol không tốt cho bệnh tim mạch, tăng huyết áp nhưng trong trứng cũng có lecithin giúp chuyển hóa cholesterol. Vì vậy, để dung hòa, mỗi tuần người cao tuổi nên ăn 3 quả trứng.\r\n\r\nThêm mỡ, giảm đường\r\nNếu không bị béo phì, không mắc bệnh tim mạch, mỡ máu không cao thì NCT cần bổ sung mỡ hằng ngày. Theo các chuyên gia dinh dưõng, với người Việt Nam, năng lượng do chất béo cung cấp nên đạt từ 20% tổng số năng lượng khẩu phần.\r\n\r\nĐiều đó có nghĩa là hiện nay, bữa ăn của chúng ta còn thiếu chất béo, nhất là ở nhiều vùng nông thôn, do đó không nên quá đề cao dầu thực vật mà bỏ quên mỡ động vật.\r\n\r\nĐối với NCT, ăn nhiều đường không tốt cho sức khỏe vì sẽ tăng nguy cơ mắc bệnh béo phì, đái tháo đường, tim mạch…\r\n\r\nViệc ăn bao nhiêu đường bột là vừa thì còn phụ thuộc vào nhu cầu năng lượng của mỗi người (tình trạng gầy béo, hoạt động thể lực nhiều hay ít…) nhưng với NCT nói chung, nên giảm lượng đường, bột trong khẩu phần.\r\n\r\nBớt muối\r\nChế độ ăn hợp lý có tác động làm giảm huyết áp và phòng xơ vữa động mạch. Vì thế, NCT chỉ nên ăn lượng muối dưới 4-5g/ngày.\r\n\r\nTheo một số nghiên cứu dịch tễ học cho thấy ở các quần thể dân cư có tập quán ăn mặn thì tỉ lệ người bị tăng huyết áp cao hơn hẳn so với các quần thể dân cư có tập quán ăn nhạt hơn.\r\n\r\nNgoài ra cần bổ sung chế độ ăn giàu kali. Kali có nhiều trong các loại rau, hoa quả như rau dền, dưa chuột, bắp cải, súp lơ, su hào, xà lách, đậu cô ve, giá đỗ, cải xoong, cà chua, cà rốt, cam, chanh, chuối, mận, mơ, dưa hấu…\r\n\r\nViệc sử dụng thức ăn giàu canxi (sữa và các chế phẩm của sữa) cũng rất quan trọng nhưng nên sử dụng sữa tách bơ.','Dinh dưỡng','hongngochospital.vnnnnn','Dinh dưỡng cho người cao tuổi','14.jpg',1,'2020-08-11 21:57:05.975'),(41,'7 NGUYÊN TẮC ĂN UỐNG CHO NGƯỜI CAO TUỔI','Ở người cao tuổi, hệ tiêu hóa bắt đầu suy giảm hiệu quả hoạt động, thị lực giảm, răng yếu, mũi kém nhạy, tuyến nước bọt tiết ít, khiến người cao tuổi cảm thấy ăn không ngon miệng, làm ảnh hưởng đến nhu cầu dinh dưỡng của tuổi già. \r\n\r\nBên cạnh đó, nguy cơ mắc các bệnh tim mạch, đái tháo đường, máu nhiễm mỡ... rất cao. Vì thế, ngoài việc duy trì cơ thể khỏe mạnh là một chế độ dinh dưỡng hợp lý, khoa học sẽ giúp nâng cao sức khỏe, kéo dài tuổi thọ đáng kể.\r\n\r\nĐảm bảo hàm lượng dinh dưỡng\r\n\r\nHệ tiêu hóa của người cao tuổi đã bắt đầu kém, khả năng tổng hợp của cơ thể cũng giảm do đó dễ xảy ra tình trạng thiếu dinh dưỡng, vì vậy cần chọn những loại thực phẩm dễ tiêu hóa, đảm bảo đủ chất đạm nhưng không quá nặng nề đối với đường tiêu hóa. \r\n\r\nThịt khi tiêu hóa thường tạo ra chất có sunfua ở đại tràng, đây là những chất độc ảnh hưởng không tốt đến sức khỏe. Do đó, người cao tuổi nên hạn chế ăn thịt, tăng cường ăn cá vì cá có nhiều chất đạm quý, dễ tiêu, ít sinh khí sunfua hơn thịt lại có nhiều axit béo không no rất cần đối với người cao tuổi. \r\n\r\nCác món ăn nên chế biến theo cách hấp hoặc luộc để đảm bảo hàm lượng các vitamin và khoáng chất, tránh các đồ xào, rán chứa nhiều mỡ. \r\n\r\nƯu tiên các loại rau và hoa quả    \r\n\r\nRau xanh và hoa quả là những loại thực phẩm không thể thiếu trong chế độ dinh dưỡng của người cao tuổi. Hàm lượng chất xơ cao trong các loại rau xanh giúp hệ tiêu hóa hoạt động dễ dàng hơn. Ngoài ra, các loại vitamin, khoáng chất và các axit hữu cơ có tác dụng kích thích sự thèm ăn và duy trì sự cân bằng các chuyển hóa trong cơ thể.\r\n\r\nGiảm áp lực cho dạ dày về đêm\r\n\r\nQuá trình tiêu hóa của người cao tuổi thường diễn ra dài hơn do sự bài tiết dịch vị trong dạ dày giảm đi, việc hấp thụ các chất như canxi, sắt cũng kém hơn. Để không bị đầy hơi gây ra hiện tượng khó ngủ về đêm, nên ăn tối trước 19h hoặc nên ăn ít đi, có những bữa lót dạ nhẹ nhanh trước khi đi ngủ khoảng 2 tiếng.\r\n\r\nĐồ ăn cần có độ mềm\r\n\r\nDo khả năng tiêu hoá và hấp thụ thức ăn kém nên bữa ăn của người cao tuổi cần có độ mềm thích hợp. Thức ăn nên chế biến theo phương pháp hấp, luộc, hầm hoặc kho. Việc lựa chọn thực phẩm cũng cần chú ý tránh chọn các thực phẩm có kết cấu thô hoặc cứng. Các loại thịt mềm, thịt cá, sữa hoặc trứng là thức ăn tốt nhất cho người cao tuổi.\r\n\r\nNên ăn nhạt\r\n\r\nĂn mặn có thể gây nên nhiều loại bệnh nguy hiểm như tim mạch, huyết áp… vì thế người cao tuổi cần ăn nhạt. Các nhà khoa học khuyên người cao tuổi nên ăn không quá 6g muối/ngày.\r\n\r\nThực đơn lý tưởng cho người cao tuổi hàng ngày là:\r\n\r\n+ 150-250g ngũ cốc và tinh bột\r\n\r\n+ 100g thịt nạc, cá hoặc tôm\r\n\r\n+ 50g đậu và cá chế phẩm từ đậu\r\n\r\n+ 300g rau xanh\r\n\r\n+ 250g hoa quả tươi\r\n\r\n+ 250ml sữa\r\n\r\n+ 30r dầu ăn, dưới 6g muối, 25g đường\r\n\r\nvà 2.000ml nước.\r\n\r\nHạn chế chất đường\r\n\r\nNgười cao tuổi phải hạn chế ăn đường, hạn chế uống nước ngọt và ăn bánh kẹo. Chỉ nên dùng chất đường có nguồn gốc là chất bột như cơm, bánh mì, bún, phở... vì các chất đường này được tiêu hóa, hấp thu từ từ, dự trữ ở gan và cơ, chỉ giải phóng ra từ từ tạo năng lượng theo nhu cầu hoạt động của cơ thể, nên không làm tăng đường huyết đột ngột.\r\n\r\nHạn chế chất béo\r\n\r\nNgười cao tuổi nếu bị thừa chất béo sẽ dễ bị thừa mỡ trong máu, cholesterol trong máu tăng gây nguy cơ xơ vữa động mạch, nhồi máu cơ tim, thiếu máu não, có thể bị xuất huyết não, liệt nửa người… Hạn chế mỡ trong khẩu phần ăn, giảm mỡ động vật, ăn dầu thực vật, bớt ăn chất đường là điều nên làm đối với người cao tuổi.','Dinh dưỡng','vocarimex.com.vnnnnn','Dinh dưỡng cho người cao tuổi','15.jpg',1,'2020-08-11 21:57:05.975'),(42,'MỘT SỐ MÓN ĂN KỴ NHAU','Để bữa ăn gia đình vừa ngon vừa đảm bảo sức khỏe thì việc lựa chọn thực phẩm là điều không thể coi nhẹ. Một số thực phẩm kỵ nhau mà nhiều người trong chúng ta không để ý đến. Trong quá trình hấp thu và chuyển hóa, giữa các thành phần của thức ăn luôn có những tương tác rất phức tạp. Chúng có thể \"hợp đồng tác chiến\" (chẳng hạn vitamin A giúp tăng cường sự tổng hợp các chất đạm, vitamin C xúc tiến quá trình hấp thụ sắt) kiềm chế lẫn nhau (chất này cản trở sự hấp thu và chuyển hóa chất kia). Hậu quả của sự phối hợp không hợp lý các thức ăn sẽ trở thành gánh nặng đối với cơ thể. Khi gánh nặng đó vượt quá khả năng tự điều chỉnh, cơ thể sẽ bị trúng độc.\r\nMột số món ăn kỵ nhau:\r\n1. Không nên xào nấu gan lợn với giá đỗ.\r\nCác nhà khoa học phân tích 100g gan lợn thấy có 2,5mg đồng và trong giá đậu có nhiều vitamin C. Nếu ta xào lẫn hoặc ăn gan lợn với giá đậu cùng một lúc hoặc trong thời gian gần nhau sẽ làm vitamin C bị oxy hoá. Kết quả giá đậu thành chất bã sẽ không còn chất bổ.\r\n\r\n2. Không nấu gan động vật với cà rốt, rau cần\r\nKhông nên dùng các loại rau, củ, quả này sau khi ăn món gan động vật. Lý do là trong gan động vật, hàm lượng đồng, sắt và một số nguyên tố kim loại khác khá cao. Các ion kim loại rất dễ làm cho vitamin C có trong loại rau, củ, quả này bị ôxy hóa và mất hết công hiệu. Ngoài ra loại rau, củ, quả này chứa nhiều chấtcellulose và acid oxalic ảnh hưởng tới sự hấp thụ sắtcủa cơ thể.\r\n\r\n3. Không ăn dưa chuột với cà chua.\r\nVì trong dưa chuột chứa một loại men phân giải Vitamin C, khi ăn dưa chuột với cà chua hay những loại thực phẩm giàu Vitamin C sẽ không tốt vì làm giảm khả năng hấp thụ Vitamin C của cơ thể.\r\n\r\n 4. Sữa đậu nành và trứng gà:\r\nSữa này có men protidaza kiềm chế các protein trong trứng gà, cản trở tiêu hóa, gây khó tiêu, đầy bụng.\r\n\r\n5. Sữa bò và nước hoa quả chua (Cam, quýt):\r\nSữa bò chứa nhiều protein, trong đó chất cazeine chiếm tới 80%. Khi sữa bò pha lẫn hoặc uống cùng với nước trái cây chua sẽ làm cho chất cazeine kết dính, lắng đọng lại làm cho khó tiêu và rối loạn tiêu hóa. Nếu trẻ uống lâu dài sẽ rất mắc bệnh methemoglobin,bệnh này gây khó thở, tím tái và có nguy cơ khiến trẻ tử vong. Nước hoa quả có tính axít, làm biến đổi tính chất của sữa bò gây khó tiêu.\r\n\r\n6. Tỏi + trứng vịt:\r\nNếu tráng trứng vịt với tỏi rất độc.\r\n7. Sữa đậu nành và đường đen\r\nĐường đen có chất axít Osalic và axít malic, nếu dùng đường đen sẽ gây ra tác dụng axít sinh ra chất \"lắng biến tính\", chất bổ sẽ bị giảm đi. Trẻ sơ sinh sẽ bị đầy bụng hoặc mất đi chức năng tiêu hóa. Vậy nên ta phải dùng đường trắng.\r\n\r\n8. Thịt dê, thịt chó và nước chè:\r\nThịt chó và thịt dê rất giàu protein. Nếu vừa ăn thịt chó hoặc thịt dê mà lại uống nước chè ngay thì chất acid tanic có trong nước chè sẽ kết hợp thành protein trong thịt chó hoặc thịt dê tạo thành chất tannalbin làm se niêm mạc ruột, giảm nhu động ruột dễ dẫn đến táo bón, nguy cơ gây ra ung thư.\r\n\r\n9. Các loại động vật có vỏ sống trong nước + chất vitamin C\r\nCác loại động vật có vỏ: như tôm nước ngọt có nhiều hợp chất asen hóa trị 5 sau khi ăn nếu uống vitamin C hay ăn những thức ăn có chứa vitamin C như ớt, cà chua, mướp đắng, cam quýt, chanh... sẽ làm cho a sen hóa trị 5 biến thành a sen hóa trị 3, túc là chất thạch tín có độc bảng A có thể chết. Vì vậy đã uống vitamin C và ăn các thứ có vitamin C thì tuyệt đối không được ăn các loại động vật có vỏ sống trong nước.\r\n\r\n10. Củ cải trắng và các loại lê, táo, nho:\r\nCeton đồng có trong những loại trái cây này phản ứng với axit cianogen lưu huỳnh trong củ cải, khiến người ăn bị suy tuyến giáp trạng và bướu cổ.\r\n\r\n11. Thịt dê kỵ giấm:\r\nGiấm chứa nhiều acid acetic, thịt dê chứa nhiều hoạt chất sinh học và đạm, hai thứ ăn chung acid acetic sẽ phá hủy thành phần dinh dưỡng của thịt dê.\r\n\r\n12. Rau dền và quả lê vốn kỵ nhau.\r\nNếu ăn cùng sẽ dễ bị nôn. Ngoài ra, bạn cũng không nên tráng miệng bằng quả lê sau bữa ăn có thịt ngỗng, vì hai món này khi kết hợp dễ gây sốt.\r\n\r\n13. Hồng với cua.\r\nLoại quả này cũng không nên ăn cùng khoai lang: Tinh bột trong khoai lang kích thích dạ dày tiết ra axít, tác dụng với chất chát tanin trong quả hồng, gây viêm loét và chảy máu dạ dày.\r\n\r\n14. Thịt chó không nên ăn với tỏi\r\nVì sẽ gây khó tiêu.\r\n15.Cá chép kỵ thịt cầy:\r\nCá chép chứa nhiều hoạt chất sinh học, thịt cầy cũng với thành phần dinh dưỡng phong phú, hai thứ ăn chung xảy ra phản ứng hóa học phức tạp, sản sinh ra chất có hại cho cơ thể.\r\n\r\n16. Bí rợ kỵ cải thìa:\r\nBí rợ chứa enzym phân giải vitamin C, khi ăn chung với cải thìa sẽ làm giá trị dinh dưỡng của cải thìa.\r\n\r\n17. Muối tiêu và khoai môn (nếu ăn cùng dễ làm ruột đau thắt)\r\nChuối hột thì kỵ mật mía, đường (ăn cùng lúc bị chướng bụng).\r\n\r\n18. Dưa hấu và thịt dê (ăn cùng dễ trúng độc).\r\n19. Hoa quả nhiều axit tanic với hải sản kỵ nhau:\r\n\r\n\r\nCác loại quả có tính axit tanic như ổi, hồng, nho nếu ăn cùng hải sản sẽ khó tiêu hóa, gây đau bụng, buồn nôn.\r\n\r\n20. Cà chua kỵ khoai lang, khoai tây:\r\nCà chua chứa nhiều chất toan, cùng với khoai lang trong dạ dày sẽ hình thành chất khó tiêu, rất dễ dẫn đến đau bụng; tiêu chảy và rối loạn tiêu hóa.\r\n\r\n21. Cà chua kỵ rượu:\r\nCà chua chứa acid tannic, có thể hình thành chất khó tiêu trong dạ dày, gây tắc nghẽn đường ruột.\r\n\r\n23. Đậu hũ (tào phớ) kỵ hành:\r\nĐậu hủ chứa nhiều calci, hành chứa acid oxalic, hai thứ ăn chung sẽ tạo kết tủa oxalac calci, không dễ tiêu hóa hấp thu, có hại cho cơ thể.\r\n\r\n24. Đào lông kỵ thịt ba ba:\r\nThịt ba ba chứa nhiều đạm, đào lông chứa nhiều acid malic, acid này sẽ làm cho đạm bị biến chất, làm giảm giá trị dinh dưỡng, cho nên, thịt ba ba không thích hợp ăn chung với đào.\r\n\r\n25. Tiêu muối kỵ chè - cháo:\r\nLương thực ngũ cốc đều chứa nhiều vitamin nhóm B; chất khoáng và xơ, các chất dinh dưỡng này rất dễ phân giải trong môi trường kiềm, tạo ra lãng phí dinh dưỡng, khi dùng ngũ cốc nấu cháo thì không nên bỏ tiêu muối (người ta nấu chè, cháo hay bỏ vào tiêu muối cho mau nhừ). ( Lời bình : Cái này chắc là muối diêm chứ không phải muối và tiêu.)\r\n\r\n26. Thịt ba ba kỵ trứng gà:\r\nThịt ba ba chứa nhiều hoạt chất sinh học, trứng gà là đạm chất lượng cao, hai thứ ăn chung sẽ dẫn đến chất đạm biến chất; làm giảm giá trị dinh dưỡng, thai phụ và sản phụ không nên ăn.\r\n\r\n27. Thịt bò kỵ hạt dẻ:\r\nThịt bò chứa nhiều đạm, hạt dẻ chứa nhiều vitamin C làm cho đạm bị biến chất, dẫn đến làm giảm giá trị dinh dưỡng.\r\n\r\n28. Cà rốt kỵ củ cải:\r\nCà rốt chứa nhiều enzym phân giải vitamin C, củ cải giàu vitamin C, hai thứ này ăn chung sẽ phá hủy các thành phần dinh dưỡng.\r\n\r\n29. Củ cải kỵ nấm mèo đen:\r\nCủ cải chứa nhiều engym, nấm mèo đen chứa nhiều hoạt chất sinh học, hai thứ ăn chung có thể phát sinh phản ứng hóa học phức tạp, dẫn đến phát sinh viêm da.\r\n\r\n30. Rượu kỵ thịt bò:\r\nThịt bò có tác dụng bồi bổ, rượu cũng là chất cay nóng, hai thứ ăn chung dễ dẫn đến các chứng như táo bón; viêm khóe miệng; mắt đỏ; ù tai\r\n\r\n31. Nhân sâm và hải sản kỵ nhau:\r\nKhi uống nhân sâm, nên kiêng ăn tất cả các loại củ cải (đỏ, trắng, xanh...) và hải sản đều là cấm kỵ sau khi bạn uống nhân sâm. Theo y học cổ truyền, củ cải và đồ biển đại hạ khí, còn nhân sâm đại bổ khí, hai thứ triệt tiêu lẫn nhau, gây hại cho người sử dụng. Dù là sắc hay hấp cách thủy, bạn cũng không được dùng đồ kim loại để nấu nhân sâm. Sau khi dùng loại dược liệu này, bạn không được uống trà, vì trà sẽ làm giảm tác dụng của nhân sâm.','Mẹo hay','amp.thaythuoccuaban.com','Các thực phẩm kị nhau','16.jpeg',1,'2020-08-11 21:57:05.975'),(43,'Tránh ăn những loại thực phẩm này cùng nhau - Dễ dẫn tới ngộ độc','Suckhoedoisong.vn - Đôi khi chúng ta vô tình chế biến hoặc ăn một số loại thực phẩm cùng nhau có thể gây những phản ứng không tốt cho sức khoẻ như ngộ độc, đau bụng, buồn nôn…. thậm chí còn có thể gây nguy hại đến tính mạng.\r\nCó một số sản phẩm khi ăn cùng với nhau sẽ có các phản ứng hóa học khiến từ lành thành độc hoặc bị khử những thành phần vi lượng có trong các thực phẩm thành loại thực phẩm không còn giá trị dinh dưỡng để bổ sung cho cơ thể nữa như: Vitamin A giúp tăng cường sự tổng hợp các chất đạm, vitamin C xúc tiến quá trình hấp thụ sắt…\r\n\r\nMặt khác, trong quá trình chế biến thực phẩm, sự kết hợp giữa một số thực phẩm có thành phần chất kỵ nhau sẽ tạo nên những chất không có lợi cho sức khỏe, gây ngộ độc…Chẳng hạn như thói quen xào nấu gan động vật với giá đỗ nhưng điều này là không nên bởi trong gan động vật có chứa nhiều hàm lượng như đồng, sắt và một số nguyên tố kim loại khác, trong khi đó ở giá đỗ có rất nhiều vitamin C, nên khi xào 2 thứ này chung với nhau lượng vitamin C sẽ bị ôxy hóa, làm mất hết công hiệu. Còn khi gánh nặng đó vượt quá khả năng tự điều chỉnh, cơ thể sẽ bị trúng độc.\r\n\r\nDưới đây là một số loại thực phẩm không nên kết hợp cùng nhau:\r\n\r\n1. Rau dền và quả lê vốn kỵ nhau: Nếu ăn cùng sẽ dễ bị nôn. Ngoài ra, bạn cũng không nên tráng miệng bằng quả lê sau bữa ăn có thịt ngỗng, vì hai món này khi kết hợp dễ gây sốt.\r\n\r\n2. Sữa đậu nành với trứng gà: Trong sữa này có men protidaza kiềm chế các protein trong trứng gà, cản trở tiêu hóa, gây khó tiêu, đầy bụng.\r\n\r\n3. Sữa đậu nành và đường đen: Bởi trong đường đen có chất axít Osalic và axít malic, nếu dùng đường đen sẽ gây ra tác dụng axít sinh ra chất \"lắng biến tính\", chất bổ sẽ bị giảm đi. Trẻ sơ sinh sẽ bị đầy bụng hoặc mất đi chức năng tiêu hóa. Vậy nên ta phải dùng đường trắng.\r\n\r\n4. Củ cải trắng với các loại lê, táo, nho: Bởi Ceton đồng có trong những loại trái cây này phản ứng cùng axit cianogen lưu huỳnh trong củ cải, khiến người ăn bị suy tuyến giáp trạng và bướu cổ.\r\n\r\n5. Sữa bò và nước hoa quả chua (cam, quýt): Bởi sữa bò chứa nhiều protein, trong đó chất cazeine chiếm tới 80%. Khi sữa bò pha lẫn hoặc uống cùng với nước trái cây chua sẽ làm cho chất cazeine kết dính, lắng đọng lại làm cho khó tiêu và rối loạn tiêu hóa. Nếu trẻ uống lâu dài sẽ rất mắc bệnh methemoglobin gây khó thở, tím tái và có nguy cơ khiến trẻ tử vong. Nước hoa quả có tính axít, làm biến đổi tính chất của sữa bò gây khó tiêu.\r\n\r\n6. Không nấu gan động vật với cà rốt, rau cần: Bởi là trong gan động vật, hàm lượng đồng, sắt và một số nguyên tố kim loại khác khá cao. Các ion kim loại rất dễ làm cho vitamin C có trong loại rau, củ, quả này bị ôxy hóa và mất hết công hiệu. Ngoài ra loại rau, củ, quả này chứa nhiều chất cellulose và acid oxalic ảnh hưởng tới sự hấp thụ sắt của cơ thể.\r\n\r\n7. Xào nấu gan lợn với giá đỗ: Các nhà khoa học phân tích 100g gan lợn thấy có 2,5 mg đồng và trong giá đậu có nhiều vitamin C. Nếu ta xào lẫn hoặc ăn gan lợn với giá đậu cùng một lúc hoặc trong thời gian gần nhau sẽ làm vitamin C bị oxy hoá. Kết quả giá đậu thành chất bã sẽ không còn chất bổ.\r\n\r\n8. Ăn dưa chuột với cà chua: Vì trong dưa chuột chứa một loại men phân giải vitamin C, khi ăn dưa chuột với cà chua hay những loại thực phẩm giàu vitaminC sẽ không tốt vì làm giảm khả năng hấp thụ vitamin C của cơ thể.\r\n\r\n9. Các loại động vật có vỏ sống trong nước chứa chất vitamin C: Các loại động vật có vỏ như tôm nước ngọt có nhiều hợp chất asen hóa trị 5 sau khi ăn nếu uống vitamin C hay ăn những thức ăn có chứa vitamin C như ớt, cà chua, mướp đắng, cam quýt, chanh... sẽ làm cho asen hóa trị 5 biến thành asen hóa trị 3, tức là chất thạch tín có độc bảng A có thể gây chết người. Vì vậy đã uống vitamin C và ăn các thứ có vitamin C thì tuyệt đối không được ăn các loại động vật có vỏ sống trong nước.\r\n\r\n10. Bí đỏ kỵ cải thìa: Bí đỏ chứa enzym phân giải vitamin C, khi ăn chung với cải thìa sẽ làm giá trị dinh dưỡng của cải thìa.\r\n\r\n11. Cà rốt kỵ củ cải: Cà rốt chứa nhiều enzym phân giải vitamin C, củ cải giàu vitamin C, hai thứ này ăn chung sẽ phá hủy các thành phần dinh dưỡng.\r\n\r\n12. Củ cải kỵ nấm mèo đen: Củ cải chứa nhiều engym, nấm mèo đen chứa nhiều hoạt chất sinh học, hai thứ ăn chung có thể phát sinh phản ứng hóa học phức tạp, dẫn đến phát sinh viêm da.\r\n\r\n13. Nấu phô mai với cua, lươn, rau mồng tơi, rau dền: Không cho phô mai nấu chung với thực phẩm như cua, lươn, rau mồng tơi, rau dền, là do bản thân phô mai đã giàu đạm và năng lượng, nên nếu nấu chung với cua, lươn sẽ dư thừa đạm, năng lượng, trẻ em và người già sẽ khó tiêu hóa hơn.\r\n\r\n14. Nấu thịt bò với tôm hoặc ăn chung: Do trong thịt bò chứa sắt, tôm chứa canxi, sắt và canxi không thể chuyển hóa cùng một lúc nên ăn chung sẽ không có tác dụng.\r\n\r\n15. Đậu, khoai lang và cải bó xôi: Bởi chúng là những thực phẩm có chứa rất nhiều axit phytic. Axit này sẽ liên kết với canxi trong cơ thể tạo thành muối. Kết quả là canxi không được hấp thụ vào cơ thể bé mà thậm chí vì lý do sức khỏe, cơ thể trẻ sẽ còn “trục xuất” các hợp chất muối mới này dưới hình thức chất thải. Do vậy, nếu đã có tôm, cua hay các loại hải sản thì nên tránh có đậu, đậu phụ, khoai lang và rau cải bó xôi, bí đỏ.\r\n\r\n16. Canh, súp cà rốt và củ cải: Cà rốt nấu chung với củ cải thường rất thơm ngon, ngọt nước nên bé thích mê. Vậy nhưng sự kết hợp này không có lợi cho bé về mặt dinh dưỡng. Trong cà rốt có chứa enzyme có thể phá hủy vitamin C. Do đó, lượng vitamin C trong củ cải cũng sẽ bị phá hủy hoàn toàn khi ăn kèm với cà rốt.\r\n\r\n17. Cá chép kỵ thịt cầy: Cá chép chứa nhiều hoạt chất sinh học, thịt cầy cũng với thành phần dinh dưỡng phong phú, hai thứ ăn chung xảy ra phản ứng hóa học phức tạp, sản sinh ra chất có hại cho cơ thể.\r\n\r\n18. Thịt chó, thịt dê với nước chè: Nguyên nhân bởi thịt chó tính cam ôn nhiệt, có nhiều protein trong khi nước chè có tính chát. Hai thứ này kết hợp với nhau sẽ sinh táo bón, phân khô, khiến cho ruột chậm nhu động nên rất có hại, thậm chí còn gây ung thư. Để đảm bảo sức khỏe, khi ăn thịt chó, cũng không nên ăn thêm thịt dê. Bởi thịt chó có tính cam ôn, thịt dê đại nhiệt, hai loại thịt gặp nhau sẽ thành tích nhiệt, sinh tả lỵ.\r\n\r\n19. Thịt dê kỵ giấm: Giấm chứa nhiều acidacetic, thịt dê chứa nhiều hoạt chất sinh học và đạm, hai thứ ăn chung acid acetic sẽ phá hủy thành phần dinh dưỡng của thịt dê.\r\n\r\n20.  Quả hồng, cà chua ăn cùng khoai lang: Tinh bột trong khoai lang kích thích dạ dày tiết ra axít, tác dụng với chất chát tanin trong quả hồng, gây viêm loét và chảy máu dạ dày.\r\n\r\n21. Bí rợ kỵ cải thìa: Bí rợ chứa enzym phân giải vitamin C, khi ăn chung với cải thìa sẽ làm giá trị dinh dưỡng của cải thìa.\r\n\r\n22. Óc lợn và trứng gà: Tuy món óc lợn tráng trứng rất hấp dẫn nhưng dùng trứng chung với óc lợn sẽ làm tăng cholesterol trong máu. Vì vậy, không nên ăn nhiều món này nhất là những người huyết áp cao.\r\n\r\n23. Hoa quả nhiều axit tanic với hải sản kỵ nhau: Các loại quả có tính axit tanic như ổi, hồng, nho nếu ăn cùng hải sản sẽ khó tiêu hóa, gây đau bụng, buồn nôn.\r\n\r\n24. Đào lông kỵ thịt ba ba: Thịt ba ba chứa nhiều đạm, đào lông chứa nhiều acid malic, acid này sẽ làm cho đạm bị biến chất, làm giảm giá trị dinh dưỡng, cho nên, thịt ba ba không thích hợp ăn chung với đào.\r\n\r\n25. Thịt ba ba kỵ trứng gà: Thịt ba ba chứa nhiều hoạt chất sinh học, trứng gà là đạm chất lượng cao, hai thứ ăn chung sẽ dẫn đến chất đạm biến chất; làm giảm giá trị dinh dưỡng, thai phụ và sản phụ không nên ăn.\r\n\r\n26. Thịt bò kỵ hạt dẻ: Thịt bò chứa nhiều đạm, hạt dẻ chứa nhiều vitamin C làm cho đạm bị biến chất, dẫn đến làm giảm giá trị dinh dưỡng.\r\n\r\n27. Nhân sâm và hải sản kỵ nhau: Khi uống nhân sâm, nên kiêng ăn tất cả các loại củ cải (đỏ, trắng, xanh...) và hải sản đều là cấm kỵ sau khi uống nhân sâm. Theo y học cổ truyền, củ cải và đồ biển đại hạ khí, còn nhân sâm đại bổ khí, hai thứ triệt tiêu lẫn nhau, gây hại cho người sử dụng. Dù là sắc hay hấp cách thủy, cũng không được dùng đồ kim loại để nấu nhân sâm. Sau khi dùng loại dược liệu này,  không được uống trà, vì trà sẽ làm giảm tác dụng của nhân sâm.\r\n\r\n28. Muối tiêu và khoai môn, ăn cùng dễ làm ruột đau thắt. Chuối hột thì kỵ mật mía, đường, ăn cùng lúc bị  bụng.\r\n\r\n29. Dưa hấu và thịt dê ăn cùng dễ trúng độc.\r\n\r\n30. Thịt chó không nên ăn với tỏi sẽ gây khó tiêu.','Mẹo hay','suckhoedoisong.vnnnnnnn','Các thực phẩm kị nhau','17.jpg',1,'2020-08-11 21:57:05.975'),(44,'Những loại thực phẩm kỵ nhau tuyệt đối không dùng chung trong ngày Tết','Trong quá trình hấp thu và chuyển hóa, giữa các thành phần của thức ăn luôn có những tương tác rất phức tạp. Hậu quả của sự phối hợp không hợp lý các thức ăn sẽ trở thành gánh nặng đối với cơ thể. Khi gánh nặng đó vượt quá khả năng tự điều chỉnh, cơ thể sẽ bị trúng độc.\r\n\r\n1. Thực phẩm kỵ thịt bò\r\n- Thủy sản: Trong thịt bò chứa nhiều phốt pho rất cần cho việc hình thành xương. Trong thủy sản thì giàu canxi và magiê. Vì vậy khi dùng chung 2 loại thực phẩm này với nhau sẽ tạo ra sự kết tủa muối. Dạng muối này sẽ làm cản trở hấp thu phốt pho và giảm tốc độ hấp thu canxi.\r\n\r\n- Đậu nành: đậu nành thuộc nhóm có nhiều purin. Chất này là nguyên nhân tạo ra acid uric gây ra bệnh gout. Thịt bò cũng vậy chứa nhiều purin. Chính vì vậy khi ăn chung 2 loại thực phẩm này với nhau sẽ tạo nên sự cộng hưởng làm tăng cường acid uric gây cơn đau khớp.\r\n\r\n- Hạt dẻ: Dịp Tết, rất nhiều người mua hạt dẻ để ăn, tuy nhiên thịt bò chứa nhiều đạm, hạt dẻ chứa nhiều vitamin C, nếu ăn chung chất đạm trong thịt bò dễ bị biến chất, mất giá trị dinh dưỡng.\r\n\r\n2. Thực phẩm kỵ thịt gà\r\n- Cá chép: Thịt gà tính ôn, cá chép tính hàn, nếu ăn 2 loại này cùng nhau, dễ sinh mụn nhọt, phát chứng trường ung.\r\n\r\n- Tôm: Tôm và gà ăn cùng dễ sinh ra chứng ngứa ngáy khắp người.\r\n\r\n- Cơm nếp: Cơm nếp có tính ngọt ấm nên khi ăn kèm với thịt gà dễ sinh ra chứng bạch thốn trùng, dân gian thường gọi là sán dây, sán sơ mít. Nên chúng ta không nên ăn nhiều 2 thứ này liền với nhau.\r\n\r\n- Tỏi, rau cải và hành sống: Thịt gà vốn cam ôn, tỏi thì đại nhiệt, rau cải và hành sống lại cam hàn nên khi kết hợp với nhau dễ gây sinh nhiệt hay hàn nhiệt giao tranh mà sinh ra bệnh lỵ, gây tổn thương khí huyết.\r\n\r\n3. Thực phẩm kị với thịt lợn\r\n- Thịt bò: Thịt lợn có tính hàn, thịt bò có tính ôn, ích khí, nên khi kết hợp hai thứ này lại với nhau sẽ làm hạn chế điểm mạnh của nhau, làm giảm giá trị dinh dưỡng của từng loại thực phẩm.\r\n\r\n- Gan: Các loại gan, đặc biệt là gan dê có mùi gây, hôi nên khi xào cùng thịt lợn sẽ kiến cho món ăn càng có mùi khó chịu, gây phản cảm với những người thưởng thức món ăn.\r\n\r\n- Đậu tương: Trong đậu tương có từ 60-80 phốt phi nên khi kết hợp với thịt lợn, hàm lượng phốt pho trong đậu tương sẽ làm giảm giá trị dinh dưỡng của thịt lợn đi, đặc biệt là thịt nạc.\r\n\r\n- Ngoài ra, thịt lợn còn không nên kết hợp với ốc bươu, cam thảo, hay rau thơm. Thịt bò, thịt trâu không nên ăn cùng với lươn, hệ.\r\n\r\n4. Thực phẩm kỵ với hải sản\r\n- Trái cây: Không ăn trái cây ngay sau khi ăn hải sản, bởi vì các chất axit có trong trái cây có thể kết hợp với canxi trong hải sản tạo thành một chất khó tiêu hóa. Chất này sẽ kích thích ruột gây đau bụng, buồn nôn hoặc nôn mửa…\r\n\r\n- Bia: Nghe thì lạ vì bạn vẫn thường uống bia với hải sản. Nhưng thực tế 2 loại thực phẩm này kị nhau, vì trong hải sản có hàm lượng đạm khá cao, nếu uống thêm bia vào thì sẽ cản trở quá trình bài tiết đạm thừa ra khỏi cơ thể. Và nếu đạm thừa không được bài tiết thì nguy cơ cao có thể mắc bệnh gout.\r\n\r\n- Nước trà: Trong trà chứa nhiều axit taninic, dễ kết hợp với canxi trong hải sản hình thành muối canxi kết tủa không có lợi cho cơ thể.\r\n\r\n5. Gan xào giá đỗ\r\nCác loại giá đỗ, rau cần chứa nhiều vitamin C còn gan động vật có hàm lượng các chất đồng, sắt và một số nguyên tố kim loại khác khá cao dễ làm cho vitamin C bị oxy hóa trong quá trình chế biến. Không những thế, trong giá đỗ, rau cần, cà rốt có chứa nhiều chất cellulose và acid oxalic ảnh hưởng tới sự hấp thụ sắt có trong gan lợn, làm mất giá trị dinh dưỡng của cả 2 loại thực phẩm này.\r\n\r\n6. Các loại rau củ kỵ nhau\r\nCủ cải trắng kỵ cà rốt, mộc nhĩ đen: Củ cải giàu vitamin C, cà rốt chứa nhiều enzym phân giải vitamin C, hai thứ này ăn chung sẽ bị phá hủy các thành phần dinh dưỡng. Củ cải chứa nhiều enzym, mộc nhĩ đen chứa nhiều hoạt chất sinh học, hai thứ ăn chung có thể phát sinh phản ứng hóa học phức tạp, gây viêm da.\r\n\r\nDưa chuột kỵ cà chua: Các nhà dinh dưỡng cho biết, trong dưa chuột chứa một loại men phân giải vitamin C. Bởi vậy khi ăn dưa chuột với cà chua hay những loại thực phẩm giàu vitaminC sẽ không tốt vì làm giảm khả năng hấp thụ vitamin C của cơ thể.\r\n\r\nCà chua kỵ khoai lang, khoai tây: do cà chua chứa nhiều chất toan, nếu ăn cùng với khoai lang, vào dạ dày sẽ hình thành chất khó tiêu, rất dễ dẫn đến đau bụng, tiêu chảy, rối loạn tiêu hóa.','Mẹo hay','24h.com.vnnnnnnnnnnn','Các thực phẩm kị nhau','18.jpg',1,'2020-08-11 21:57:05.975'),(45,'Những thực phẩm kỵ nhau chan chát, chớ dại ăn cùng kẻo mang họa vào thân','Ăn dưa chuột với cà chua\r\nVì trong dưa chuột chứa một loại men phân giải vitamin C, khi ăn dưa chuột với cà chua hay những loại thực phẩm giàu vitaminC sẽ không tốt vì làm giảm khả năng hấp thụ vitamin C của cơ thể.\r\nCác loại động vật có vỏ sống trong nước chứa chất vitamin C\r\nCác loại động vật có vỏ như tôm nước ngọt có nhiều hợp chất asen hóa trị 5 sau khi ăn nếu uống vitamin C hay ăn những thức ăn có chứa vitamin C như ớt, cà chua, mướp đắng, cam quýt, chanh... sẽ làm cho asen hóa trị 5 biến thành asen hóa trị 3, tức là chất thạch tín có độc bảng A có thể gây chết người. Vì vậy đã uống vitamin C và ăn các thứ có vitamin C thì tuyệt đối không được ăn các loại động vật có vỏ sống trong nước.\r\nBí đỏ kỵ cải thìa\r\nBí đỏ chứa enzym phân giải vitamin C, khi ăn chung với cải thìa sẽ làm giá trị dinh dưỡng của cải thìa.\r\n\r\nCà rốt kỵ củ cải\r\nCà rốt chứa nhiều enzym phân giải vitamin C, củ cải giàu vitamin C, hai thứ này ăn chung sẽ phá hủy các thành phần dinh dưỡng.\r\n\r\nSữa chua và thịt giăm bông\r\n\r\nSữa chua là một loại thực phẩm giàu dinh dưỡng tốt cho sức khỏe. Tuy nhiên, sữa chua có thể làm bạn mắc bệnh ung thư nếu dùng chung với thịt giăm bông. Những người bán hàng thường có xu hướng thêm nitrat vào trong thịt để tránh nhiễm botulinum, một dạng protein và độc tố thần kinh có trong thịt nhằm hạn chế việc nhiễm khuẩn thực phẩm. Tuy nhiên, nitrat còn giúp làm chậm quá trình thịt bị thiu và bắt đầu phân hủy nên giúp bảo quản thịt lâu hơn. Khi sữa chua và thịt kết hợp với nhau sẽ tạo ra những hợp chất nitrosamines và carcinogen, chính là những chất gây ung thư.\r\n\r\nDưa hấu và thịt\r\n\r\nThịt thường được xếp vào danh sách những thực phẩm “nóng” đối với cơ thể bạn và ngược lại, dưa hấu thuộc nhóm thực phẩm “mát”. Chính vì sự trái ngược này mà khi được kết hợp với nhau, mức độ hiệu quả về mặt dinh dưỡng của thịt sẽ bị giảm xuống trầm trọng. Không chỉ dừng lại ở đó, điều này thậm chí còn có thể gây nguy hiểm cho những người mắc chứng bệnh suy nhược lá lách và gây ảnh hưởng không nhỏ đến dạ dày của bạn.\r\n\r\nThịt và giấm\r\n\r\nThịt được xếp loại “nóng” và giấm cũng được vào nhóm “ấm”. Khi kết hợp hai loại thực phẩm này sẽ khiến cơ thể bạn giải phóng năng lượng vượt quá mức bình thường. Bên cạnh đó, quá trình tuần hoàn cũng bị thúc đẩy hơn mức cho phép. Việc kết hợp hai loại thực phẩm này có thể gây tổn hại cho tim của bạn.\r\n\r\nThịt bò và hạt dẻ\r\n\r\nBên trong hạt dẻ có chứa một lượng vitamin C dồi dào. Tuy nhiên, lượng vitamin C này lại có thể phản ứng với các vi sinh vật có thể tìm thấy ở thịt bò, từ đó giảm đi giá trị dinh dưỡng của hạt dẻ. Hơn nữa, việc kết hợp hai loại món ăn này đôi khi còn gây tác hại đến cho hệ tiêu hóa của bạn.\r\n\r\nThịt cua và trà\r\n\r\nMột trong các nguyên nhân dẫn đến các vấn đề về tiêu hóa đó chính là uống trà khi bạn đang ăn cua. Nước trà có thể làm loãng dịch vị trong dạ dày của bạn. Trong trà có chứa một lượng axit tannic tương tự như trong quả hồng. Việc dịch vị bị pha loãng không chỉ ảnh hưởng đến quá trình đồng hóa của cơ thể bạn mà còn gây cản trở việc khử trùng thức ăn của dạ dày.\r\n\r\nTôm và vitamin C\r\n\r\nTrong tôm thông thường có nhiều arsenic trioxide (As205). Chính vì thế, nếu bạn kết hợp tôm với các thực phẩm chức năng bổ sung vitamin C thì sẽ dẫn đến việc tạo ra những phản ứng hóa học trong dạ dày của bạn hình thành nên arsenic trioxide. Sự kết hợp này có thể dẫn đến những vấn đề sức khỏe nghiêm trọng, thậm chí là tử vong.\r\n\r\nHồng và khoai\r\n\r\nTrong khoai tây có rất nhiều axit vô cơ. Việc kết hợp khoai và hồng có thể khiến cho cơ thể, cụ thể là dạ dày bạn chứa đầy cặn và xác của trái hồng. Những loại cặn này hầu như không thể hòa tan hoặc pha loãng, chính vì vậy chúng sẽ có ảnh hưởng không nhỏ đối với quá trình tiêu hóa của cơ thể bạn.\r\n\r\nCủ cải kỵ nấm mèo đen\r\n\r\nCủ cải chứa nhiều engym, nấm mèo đen chứa nhiều hoạt chất sinh học, hai thứ ăn chung có thể phát sinh phản ứng hóa học phức tạp, dẫn đến phát sinh viêm da.\r\n\r\nNấu phô mai với cua, lươn, rau mồng tơi, rau dền\r\n\r\nKhông cho phô mai nấu chung với thực phẩm như cua, lươn, rau mồng tơi, rau dền, là do bản thân phô mai đã giàu đạm và năng lượng, nên nếu nấu chung với cua, lươn sẽ dư thừa đạm, năng lượng, trẻ em và người già sẽ khó tiêu hóa hơn.\r\n\r\nNấu thịt bò với tôm hoặc ăn chung\r\n\r\nDo trong thịt bò chứa sắt, tôm chứa canxi, sắt và canxi không thể chuyển hóa cùng một lúc nên ăn chung sẽ không có tác dụng.','Mẹo hay','tienphong.vnnnnnnnnnn','Mẹo hay chế biến nguyên liệu','19.jpg',1,'2020-08-11 21:57:05.975'),(46,'Những loại thực phẩm kỵ nhau tuyệt đối không được dùng trong bữa ăn','Để bữa ăn gia đình vừa ngon vừa đảm bảo sức khỏe thì việc lựa chọn thực phẩm là quan trọng và cần phải để ý một số thực phẩm kỵ nhau. \r\n\r\nThực phẩm kỵ thịt bò\r\n\r\nThủy sản: Trong thịt bò chứa nhiều phốt pho rất cần cho việc hình thành xương. Trong thủy sản thì giàu canxi và magie. Vì vậy khi dùng chung 2 loại thực phẩm này với nhau sẽ tạo ra sự kết tủa muối. Dạng muối này sẽ làm cản trở hấp thu phốt pho và giảm tốc độ hấp thu canxi.\r\n\r\nĐậu nành: đậu nành thuộc nhóm có nhiều purin. Chất này là nguyên nhân tạo ra acid uric gây ra bệnh gout. Thịt bò cũng vậy chứa nhiều purin. Chính vì vậy khi ăn chung 2 loại thực phẩm này với nhau sẽ tạo nên sự cộng hưởng làm tăng cường acid uric gây cơn đau khớp.\r\n\r\nThịt cua và trà\r\n\r\nMột trong các nguyên nhân dẫn đến các vấn đề về tiêu hóa đó chính là uống trà khi bạn đang ăn cua.\r\n\r\nNước trà có thể làm loãng dịch vị trong dạ dày của bạn. Trong trà có chứa một lượng axit tannic tương tự như trong quả hồng. Việc dịch vị bị pha loãng không chỉ ảnh hưởng đến quá trình đồng hóa của cơ thể bạn mà còn gây cản trở việc khử trùng thức ăn của dạ dày.\r\n\r\nTôm và vitamin C\r\n\r\nTrong tôm thông thường có nhiều arsenic trioxide (As205). Chính vì thế, nếu bạn kết hợp tôm với các thực phẩm chức năng bổ sung vitamin C thì sẽ dẫn đến việc tạo ra những phản ứng hóa học trong dạ dày của bạn hình thành nên arsenic trioxide.\r\n\r\nSự kết hợp này có thể dẫn đến những vấn đề sức khỏe nghiêm trọng, thậm chí là tử vong.\r\n\r\nGan và giá đỗ\r\n\r\nCác loại giá đỗ, rau cần chứa nhiều vitamin C còn gan động vật có hàm lượng các chất đồng, sắt và một số nguyên tố kim loại khác khá cao dễ làm cho vitamin C bị oxy hóa trong quá trình chế biến.\r\n\r\nKhông những thế, trong giá đỗ, rau cần, cà rốt có chứa nhiều chất cellulose và acid oxalic ảnh hưởng tới sự hấp thụ sắt có trong gan lợn, làm mất giá trị dinh dưỡng của cả 2 loại thực phẩm này.\r\n\r\nCủ cải kỵ nấm mèo đen\r\n\r\nCủ cải chứa nhiều engym, nấm mèo đen chứa nhiều hoạt chất sinh học, hai thứ ăn chung có thể phát sinh phản ứng hóa học phức tạp, dẫn đến phát sinh viêm da.\r\n\r\nSữa bò và nước hoa quả chua (Cam, quýt)\r\n\r\nSữa bò chứa nhiều protein, trong đó chất cazeine chiếm tới 80%. Khi sữa bò pha lẫn hoặc uống cùng với nước trái cây chua sẽ làm cho chất cazeine kết dính, lắng đọng lại làm cho khó tiêu và rối loạn tiêu hóa.\r\n\r\nNếu trẻ em uống lâu dài sẽ rất mắc bệnh methemoglobin,bệnh này gây khó thở, tím tái và có nguy cơ khiến trẻ tử vong. Nước hoa quả có tính axít, làm biến đổi tính chất của sữa bò gây khó tiêu.','Mẹo hay','laodong.vnnnnnnnnnnn','Các thực phẩm kị nhau','20.jpg',1,'2020-08-11 21:57:05.975');
